public class Solution {
    /***
     * Time Complexity: O(n)
     * Space Complexity: O(n)
     *
     * Build a map between num and it's target
     * for each num in nums
     *  if num's target is in map
     *      return the indexes of this num and the index of the target in the map
     *  put index and target value in map
     *
     * return [-1, -1]
     */
    public int[] twoSum(int[] nums, int target) {
        Map<Integer, Integer> targetToIndex = new HashMap<>();
        for(int i = 0; i < nums.length; i++){
            // If the diff from target is in the map
            if(targetToIndex.containsKey(nums[i]))
                return new int[]{targetToIndex.get(nums[i]), i};
            // Otherwise put diff and index to the map
            targetToIndex.put(target - nums[i], i);
        }
        return new int[]{-1, -1};
    }
}

public class Solution {
    public int reverse(int x) {
        if(x < 10 && x > -10) return x;

        int absX = x > 0 ? x : -x;
        long longRes = 0;
        while(absX > 0){
            longRes = longRes*10 + absX % 10;
            if(longRes > Integer.MAX_VALUE || -longRes < Integer.MIN_VALUE)
                return 0;
            absX /= 10;
        }

        return x > 0 ? (int)longRes : -1*(int)longRes;
    }
}

public class Solution {
    public boolean isPalindrome(int x) {
        //negative numbers are not palindrome
        if (x < 0) return false;

        // find the most siginificant digit
        int div = 1;
        while (x / div >= 10) {
            div *= 10;
        }

        // stop till x is 0
        while (x != 0) {
            int left = x / div; // left digit
            int right = x % 10; // right digit

            if (left != right)	return false;
            // remove first and last digit
            x = (x % div) / 10;
            // reduce dividor
            div /= 100;
        }
        return true;
    }
}

public class Solution {
    public int romanToInt(String s) {
        if (s.length() == 0)
            return 0;

        List<Character> romans = Arrays.asList('I', 'V', 'X', 'L', 'C', 'D', 'M');
        int[] numbers = {1, 5, 10, 50, 100, 500, 1000};

        if (s.length() == 1)
            return numbers[romans.indexOf(s.charAt(0))];

        int index = 0, result = 0;
        while (index < s.length() - 1) {
            // If cur char smaller than next
            if (romans.indexOf(s.charAt(index)) >= romans.indexOf(s.charAt(index + 1))) {
                result += numbers[romans.indexOf(s.charAt(index))];
                index++;
            } else {
                result += numbers[romans.indexOf(s.charAt(index + 1))]
                        - numbers[romans.indexOf(s.charAt(index))];
                index += 2;
            }
        }
        // Deal with the last char. Need to check since we have index += 2
        if (index == s.length() - 1)
            result += numbers[romans.indexOf(s.charAt(index))];

        return result;
    }
}

public class Solution {
    public String longestCommonPrefix(String[] strs) {
        if (strs.length == 0) return "";
        StringBuilder res = new StringBuilder();
        for (int i = 0; i < strs[0].length(); i++) {
            // Check each char of first string
            int strsInd = 0;
            while (strsInd < strs.length && i < strs[strsInd].length() &&
                    strs[strsInd].charAt(i) == strs[0].charAt(i))
                strsInd++;
            // If all strings have the char
            if (strsInd == strs.length) {
                res.append(strs[0].charAt(i));
            } else
                return res.toString();
        }
        return strs[0];
    }
}

public class Solution {
    public boolean isValid(String s) {
        Deque<Character> stack = new ArrayDeque<>();
        int i = 0;
        while (i < s.length()) {
            if (s.charAt(i) == '(' || s.charAt(i) == '{' || s.charAt(i) == '[')
                stack.push(s.charAt(i));
            else if (s.charAt(i) == ')' && (stack.isEmpty() || stack.pop() != '(') ||
                    s.charAt(i) == '}' &&(stack.isEmpty() || stack.pop() != '{') ||
                    s.charAt(i) == ']' && (stack.isEmpty() || stack.pop() != '[')){
                return false;
            }
            i++;
        }
        return stack.isEmpty();
    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode(int x) { val = x; }
 * }
 */
public class Solution {
    public ListNode mergeTwoLists(ListNode l1, ListNode l2) {
        ListNode fakeHead = new ListNode(0);
        ListNode current = fakeHead;

        while (l1 != null || l2 != null) {
            if (l1 == null || (l2 != null && l1.val >= l2.val)) {
                current.next = l2;
                current = l2;
                l2 = l2.next;
            } else {
                current.next = l1;
                current = l1;
                l1 = l1.next;
            }
        }
        return fakeHead.next;
    }
}

public class Solution {
    public int removeDuplicates(int[] nums) {
        if (nums.length <= 1)
            return nums.length;

        int endIndex = 0;
        for (int i = 1; i < nums.length; i++) {
            if (nums[i] != nums[endIndex]){
                endIndex++;
                nums[endIndex] = nums[i];
            }
        }
        return endIndex + 1;
    }
}

public class Solution {
    public int removeElement(int[] nums, int val) {
        if (nums.length == 0){
            return 0;
        }

        if (nums.length == 1) {
            return nums[0] == val ? 0 : 1;
        }

        int firstIndex = 0;
        int lastIndex = nums.length - 1;
        while (firstIndex < lastIndex) {
            if (nums[firstIndex] != val) {
                firstIndex++;
                continue;
            }
            nums[firstIndex] += nums[lastIndex];
            nums[lastIndex] = nums[firstIndex] - nums[lastIndex];
            nums[firstIndex] = nums[firstIndex] - nums[lastIndex];
            lastIndex--;
        }

        return nums[firstIndex] == val ? firstIndex : firstIndex + 1;
    }
}

public class Solution {
    public int strStr(String haystack, String needle) {
        if (needle == null || haystack == null || needle.length() > haystack.length())
            return -1;
        if (needle.length() == 0){
            return 0;
        }

        int[] overlay = getOverlay(needle);
        int i = 0;
        while (i <= haystack.length() - needle.length()){
            int j = 0;
            while(j < needle.length()){
                if (haystack.charAt(i+j) != needle.charAt(j)){
                    i = j == 0 ? i+1 : i+j-overlay[j-1];
                    break;
                }
                j++;
            }
            if(j == needle.length()){
                return i;
            }
        }
        return -1;
    }

    // Generate the prefix function for pattern itself
    private int[] getOverlay(String pattern){
        int[] res = new int[pattern.length()];
        res[0] = 0;
        for(int i = 1; i < pattern.length(); i++){
            int index = res[i-1];
            while (index > 0 && pattern.charAt(index) != pattern.charAt(i))
                index = res[index-1];
            res[i] = (pattern.charAt(index) == pattern.charAt(i)) ? index + 1 : 0;
        }
        return res;
    }
}

public class Solution {
    public int searchInsert(int[] nums, int target) {
        int low = 0;
        int high = nums.length - 1;
        while (low <= high) {
            int mid = low + (high - low) / 2;
            if (nums[mid] == target) {
                return mid;
            } else if (nums[mid] > target) {
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }
        return low;
    }
}

public class Solution {
    public String countAndSay(int n) {
        StringBuilder curr = new StringBuilder("1");
        for (int i = 1; i < n; i++) {
            StringBuilder prev = curr;
            curr = new StringBuilder();
            int count = 1;
            char say = prev.charAt(0);

            for (int j = 1, len = prev.length(); j < len; j++) {
                if (prev.charAt(j) != say) {
                    curr.append(count).append(say);
                    count = 1;
                    say = prev.charAt(j);
                } else count++;
            }
            curr.append(count).append(say);
        }
        return curr.toString();
    }
}

public class Solution {
    // DP solution
    public int maxSubArray(int[] nums) {
        int max = Integer.MIN_VALUE, sum = 0;
        for (int i = 0; i < nums.length; i++) {
            sum = (sum < 0) ? nums[i] : (sum + nums[i]);
            if (sum > max)
                max = sum;
        }
        return max;
    }

    // Divide and Conquer
    public int maxSubArray(int[] nums) {
        return divide(nums, 0, nums.length - 1);
    }

    int divide(int nums[], int low, int high) {
        if (low == high){
            return nums[low];
        }
        if (low == high - 1)
            return Math.max(nums[low] + nums[high], Math.max(nums[low], nums[high]));
        int mid = low + (high - low) / 2;
        int lmax = divide(nums, low, mid - 1);
        int rmax = divide(nums, mid + 1, high);
        int mmax = nums[mid];
        int tmp = mmax;
        for (int i = mid - 1; i >= low; i--) {
            tmp += nums[i];
            if (tmp > mmax){
                mmax = tmp;
            }
        }
        tmp = mmax;
        for (int i = mid + 1; i <= high; i++) {
            tmp += nums[i];
            if (tmp > mmax){
                mmax = tmp;
            }
        }
        return Math.max(mmax, Math.max(lmax, rmax));
    }
}

public class Solution {
    public int lengthOfLastWord(String s) {
        int count = 0;
        if(s.length()==0) return 0;
        int len = s.length()-1;

        while(len>=0 && s.charAt(len) == ' ')
            len--;

        if(len<0) return 0;


        for(int i=len; i>=0; i--){
            if(s.charAt(i) != ' ')
                count++;
            else
                break;
        }
        return count;
    }
}

public class Solution {
    public int[] plusOne(int[] digits) {
        int carry = 1;
        for(int i=digits.length-1; i >= 0; i--){
            if((digits[i] + carry) < 10){
                digits[i] = digits[i] + carry;
                return digits;
            }else{
                digits[i] = digits[i] + carry - 10;
                carry = 1;
            }
        }

        int[] res = new int[digits.length+1];
        res[0] = 1;
        return res;
    }
}

public class Solution {
    public String addBinary(String a, String b) {
        int aInd = a.length() - 1;
        int bInd = b.length() - 1;
        int carry = 0;
        StringBuilder res = new StringBuilder();
        while(aInd >= 0 || bInd >= 0){
            int sum = carry;
            if(aInd >= 0 && a.charAt(aInd) == '1')
                sum += 1;
            if(bInd >= 0 && b.charAt(bInd) == '1')
                sum += 1;
            res.insert(0, sum % 2);
            carry = sum / 2;
            aInd--;
            bInd--;
        }
        if(carry > 0)
            res.insert(0, '1');
        return res.toString();
    }
}

public class Solution {
    public int mySqrt(int x) {
        if(x <= 1) return x;
        int start = 0, end = x/2 + 1;
        while(start <= end) {
            int mid = start + (end-start)/2;
            if(x/mid == mid)
                return mid;
            else if(x/mid < mid)
                end = mid-1;
            else
                start = mid+1;
        }
        return end;
    }
}

public class Solution {
    public int climbStairs(int n) {
        int[] steps = new int[n+1];
        if(n==1) return 1;
        if(n==2) return 2;

        steps[1] = 1;
        steps[2] = 2;
        int i = 3;

        while(i <= n){
            steps[i] = steps[i-1] + steps[i-2];
            i++;
        }

        return steps[n];
    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode(int x) {
 *         val = x;
 *         next = null;
 *     }
 * }
 */
public class Solution {
    public ListNode deleteDuplicates(ListNode head) {
        if(head == null || head.next == null) return head;

        ListNode cur = head;
        while(cur != null && cur.next != null){
            while(cur.val == cur.next.val){
                cur.next = cur.next.next;
                if(cur.next == null)
                    break;
            }
            cur = cur.next;
        }
        return head;
    }
}

public class Solution {
    public void merge(int[] nums1, int m, int[] nums2, int n) {
        int i = m - 1;
        int j = n - 1;
        int k = m + n - 1;
        while (i > -1 && j > -1){
            nums1[k--] = (nums1[i] > nums2[j]) ? nums1[i--] : nums2[j--];
        }
        while (j > -1){
            nums1[k--] = nums2[j--];
        }
    }
}

/**
 * Definition for binary tree
 * public class TreeNode {
 * int val;
 * TreeNode left;
 * TreeNode right;
 * TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public boolean isSameTree(TreeNode p, TreeNode q) {
        if (p == null && q == null) return true;
        if (p == null || q == null) return false;

        return (p.val == q.val) && isSameTree(p.left, q.left)
                && isSameTree(p.right, q.right);
    }
}

/**
 * Definition for binary tree
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public boolean isSymmetric(TreeNode root) {
        if(root == null) return true;
        return isSymmetric(root.left, root.right);
    }

    private boolean isSymmetric(TreeNode leftSub, TreeNode rightSub){
        if(leftSub == null && rightSub == null) return true;
        if(leftSub == null || rightSub == null) return false;

        if(leftSub.val == rightSub.val){
            return isSymmetric(leftSub.left, rightSub.right) && isSymmetric(leftSub.right, rightSub.left);
        }

        return false;
    }
}

/**
 * Definition for binary tree
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public int maxDepth(TreeNode root) {
        if(root == null) return 0;

        if(root.left == null && root.right == null) return 1;

        if(root.left == null)
            return 1+maxDepth(root.right);
        else if(root.right == null)
            return 1+maxDepth(root.left);
        else
            return 1+Math.max(maxDepth(root.left), maxDepth(root.right));
    }
}

/**
 * Definition for binary tree
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public List<List<Integer>> levelOrderBottom(TreeNode root) {
        List<List<Integer>> res = new ArrayList<List<Integer>>();
        if(root == null) return res;

        Deque<TreeNode> curLevel = new ArrayDeque<TreeNode>();
        Deque<TreeNode> nextLevel = new ArrayDeque<TreeNode>();
        curLevel.addLast(root);
        Deque<List<Integer>> listStack = new ArrayDeque<List<Integer>>();

        while(!curLevel.isEmpty()){
            List<Integer> curList = new ArrayList<Integer>();
            for(TreeNode n : curLevel){
                curList.add(n.val);
                if(n.left != null) nextLevel.addLast(n.left);
                if(n.right != null) nextLevel.addLast(n.right);
            }
            listStack.addFirst(curList);
            curLevel = new ArrayDeque<TreeNode>(nextLevel);
            nextLevel.clear();
        }

        for(List<Integer> l : listStack){
            res.add(l);
        }
        return res;
    }
}

/**
 * Definition for binary tree
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public TreeNode sortedArrayToBST(int[] num) {
        if(num.length == 0) return null;

        int startIndex = 0, endIndex = num.length - 1;
        int midIndex = (startIndex + endIndex)/2;

        TreeNode root = new TreeNode(num[midIndex]);
        if(num.length == 1) return root;

        root.left = arrayToBST(num, startIndex, midIndex-1);
        root.right = arrayToBST(num, midIndex+1, endIndex);

        return root;
    }

    public TreeNode arrayToBST(int[] num, int start, int end){
        TreeNode root = null;
        if(start <= end){
            int mid = (start + end)/2;
            root = new TreeNode(num[mid]);
            if(start == end)
                return root;
            root.left = arrayToBST(num, start, mid-1);
            root.right = arrayToBST(num, mid+1, end);
        }

        return root;
    }
}

/**
 * Definition for binary tree
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public boolean isBalanced(TreeNode root) {
        if(root == null) return true;

        int leftHeight = treeHeight(root.left);
        int rightHeight = treeHeight(root.right);

        if(Math.abs(leftHeight-rightHeight) <=1)
            return isBalanced(root.left) && isBalanced(root.right);
        else
            return false;
    }

    private int treeHeight(TreeNode root){
        if(root == null) return 0;
        int left = treeHeight(root.left);
        int right = treeHeight(root.right);

        return 1+Math.max(left, right);
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public int minDepth(TreeNode root) {
        if (root == null)
            return 0;
        if (root.left == null && root.right == null)
            return 1;
        if (root.left == null)
            return 1 + minDepth(root.right);
        if (root.right == null)
            return 1 + minDepth(root.left);
        return 1 + Math.min(minDepth(root.left), minDepth(root.right));
    }
}

/**
 * Definition for binary tree
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public boolean hasPathSum(TreeNode root, int sum) {
        if(root == null) return false;

        if(root.left == null && root.right == null)
            return (sum == root.val);

        return hasPathSum(root.left, sum-root.val) ||
                hasPathSum(root.right, sum-root.val);
    }
}

public class Solution {
    public List<List<Integer>> generate(int numRows) {
        List<List<Integer>> res = new ArrayList<List<Integer>>();
        if (numRows == 0) return res;

        List<Integer> curRow = new ArrayList<Integer>();
        curRow.add(1);
        res.add(curRow);
        if (numRows == 1)
            return res;

        List<Integer> preRow = curRow;
        for (int i = 2; i < numRows + 1; i++) {
            curRow = new ArrayList<Integer>();
            for (int j = 0; j < i; j++) {
                if (j == 0) {
                    curRow.add(preRow.get(0));
                } else if (j == i - 1) {
                    curRow.add(preRow.get(j - 1));
                } else {
                    curRow.add(preRow.get(j) + preRow.get(j - 1));
                }
            }
            res.add(curRow);
            preRow = curRow;
        }

        return res;
    }
}

public class Solution {
    public List<Integer> getRow(int rowIndex) {
        List<Integer> row = new ArrayList<Integer>();
        row.add(1);
        for (int i = 1; i <= rowIndex; i++) {
            List<Integer> preRow = row;
            row = new ArrayList<Integer>();
            for (int j = 0; j <= i; j++) {
                int val = ((j > 0) ? preRow.get(j - 1) : 0)
                        + ((j < preRow.size()) ? preRow.get(j) : 0);
                row.add(val);
            }
        }
        return row;
    }
}

public class Solution {
    public int maxProfit(int[] prices) {
        if(prices == null || prices.length <= 1) return 0;
        // Track min value so far
        int minSofar = prices[0];
        int maxProfit = 0;
        for(int i = 1; i < prices.length; i++){
            if(prices[i] < minSofar){
                minSofar = prices[i]; // update min so far
            } else {
                // update max profit
                maxProfit = Math.max(prices[i] - minSofar, maxProfit);
            }
        }
        return maxProfit;
    }
}

public class Solution {
    public int maxProfit(int[] prices) {
        if(prices == null || prices.length <= 1) return 0;
        int maxProfit = 0;
        for(int i = 1 ; i < prices.length; i++){
            if(prices[i] > prices[i-1]){
                maxProfit += prices[i] - prices[i-1];
            }
        }
        return maxProfit;
    }
}

public class Solution {
    public boolean isPalindrome(String s) {
        if(s == null) return false;
        // Replace char that is not in the list
        s = s.replaceAll("[^a-zA-Z0-9]", "").toLowerCase();
        if(s.length() == 0) return true;
        // Compare char from both ends
        for(int i = 0; i < s.length() ; i++){
            if(s.charAt(i) != s.charAt(s.length() - 1 - i)){
                return false;
            }
        }
        return true;
    }
}

public class Solution {
    public int singleNumber(int[] A) {
        if (A.length == 0) return -1;
        int result = A[0];

        for (int i = 1; i < A.length; i++) {
            result ^= A[i];
        }

        return result;
    }
}

/**
 * Definition for singly-linked list.
 * class ListNode {
 * int val;
 * ListNode next;
 * ListNode(int x) {
 * val = x;
 * next = null;
 * }
 * }
 */
public class Solution {
    public boolean hasCycle(ListNode head) {
        if (head == null || head.next == null) return false;

        ListNode slow = head.next, fast = head.next.next;
        while (fast != null && fast.next != null) {
            slow = slow.next;
            fast = fast.next.next;
            if (slow == fast) return true;
        }
        return false;
    }
}

class MinStack {
    Deque<Integer> stackContent = new ArrayDeque<Integer>();
    Deque<Integer> stackMinVal = new ArrayDeque<Integer>();

    public void push(int x) {
        this.stackContent.push(x);
        if (this.stackMinVal.isEmpty() || x <= this.stackMinVal.peek()) {
            this.stackMinVal.push(x);
        }
    }

    public void pop() {
        if (this.stackContent.isEmpty()) return;
        if (this.stackContent.peek().equals(this.stackMinVal.peek())) {
            this.stackMinVal.pop();
        }
        this.stackContent.pop();
    }

    public int top() throws IndexOutOfBoundsException {
        if (this.stackContent.isEmpty())
            throw new IndexOutOfBoundsException();
        return this.stackContent.peek();
    }

    public int getMin() {
        if (this.stackMinVal.isEmpty())
            return -1;
        return this.stackMinVal.peek();
    }
}

public class Solution extends Reader4 {
    //  O(N)  O(1)
    public int read(char[] buf, int n) {
        for(int i = 0; i < n; i += 4){
            char[] tmp = new char[4];
            // 
            int len = read4(tmp);
            // buf
            System.arraycopy(tmp, 0, buf, i, Math.min(len, n - i));
            // 4
            if(len < 4) return Math.min(i + len, n);
        }
        // 4
        return n;
    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 * int val;
 * ListNode next;
 * ListNode(int x) {
 * val = x;
 * next = null;
 * }
 * }
 */
public class Solution {
    public ListNode getIntersectionNode(ListNode headA, ListNode headB) {
        if (headA == null || headB == null) return null;
        int lenA = getLength(headA);
        int lenB = getLength(headB);

        int cut = lenA - lenB;
        while (cut > 0) {
            headA = headA.next;
            cut--;
        }
        while (cut < 0) {
            headB = headB.next;
            cut++;
        }

        while (headA != null && headB != null) {
            if (headA == headB) return headA;
            headA = headA.next;
            headB = headB.next;
        }
        return null;
    }

    static int getLength(ListNode head) {
        int len = 0;
        while (head != null) {
            head = head.next;
            len++;
        }
        return len;
    }
}

public class Solution {
    public int[] twoSum(int[] numbers, int target) {
        int[] indice = new int[2];
        if (numbers == null || numbers.length < 2)
            return indice;
        int left = 0, right = numbers.length - 1;
        while (left < right) {
            int v = numbers[left] + numbers[right];
            if (v == target) {
                indice[0] = left + 1;
                indice[1] = right + 1;
                break;
            } else if (v > target) {
                right--;
            } else {
                left++;
            }
        }
        return indice;
    }
}

