public class Solution {
    public void rotate(int[] nums, int k) {
        if (nums.length <= 1) return;
        k = k % nums.length;

        reverseArray(nums, 0, nums.length - 1 - k);
        reverseArray(nums, nums.length - k, nums.length - 1);
        reverseArray(nums, 0, nums.length - 1);
    }

    static void reverseArray(int[] nums, int start, int end) {
        while (start < end) {
            nums[start] += nums[end];
            nums[end] = nums[start] - nums[end];
            nums[start] = nums[start] - nums[end];
            start++;
            end--;
        }
    }
}

public class Solution {
    // you need treat n as an unsigned value
    public int reverseBits(int n) {
        int firstBitInd = 0, lastBitInd = 31;
        while(firstBitInd < lastBitInd){
            int firstBit = (n >> firstBitInd) & 1;
            int lastBit = (n >> lastBitInd) & 1;
            // Set last bit
            if(firstBit > 0)
                n |= (1 << lastBitInd);
            else
                n &= ~(1 << lastBitInd);
            // Set first bit
            if(lastBit > 0)
                n |= (1 << firstBitInd);
            else
                n &= ~(1 << firstBitInd);
            firstBitInd++;
            lastBitInd--;
        }
        return n;
    }
}

public class Solution {
    // you need to treat n as an unsigned value
    public int hammingWeight(int n) {
        int ans = 0;
        while (n != 0) {
            ans += n & 1;
            n >>>= 1;
        }
        return ans;
    }

    // n & (n - 1) drops the lowest set bit. It's a neat little bit trick.
}

public class Solution {
    public int rob(int[] num) {
        // Algorithm
        // case 1: current house is not robbed:
        //      Max. amount is Max(Max Amount When last house is robbed,
        //             Max Amount when last house is not robbed)
        // case 2: current house is robbed:
        //   Max. amount is Max Amount when last house is not robbed + current house's value
        // repeat this from first two until the last
        // Compare case 1 and case 2 to return the Max
        if(num.length == 0) return 0;
        if(num.length == 1) return num[0];
        if(num.length == 2) return Math.max(num[0], num[1]);

        // start from index = 2
        int lastNotRobbed = Math.max(num[0], num[1]);
        int lastRobbed = num[0] + num[2];
        int index = 3;

        while(index <= num.length-1){
            int tempLastNotRobbed = Math.max(lastNotRobbed, lastRobbed);
            lastRobbed = lastNotRobbed + num[index++];
            lastNotRobbed = tempLastNotRobbed;
        }

        return Math.max(lastRobbed, lastNotRobbed);
    }
}

public class Solution {
    public boolean isHappy(int n) {
        Set<Integer> wordMap = new HashSet<Integer>();
        while(n != 1 && !wordMap.contains(n)){
            wordMap.add(n);
            int temp = n;
            n = 0;
            while(temp >= 1){
                n += (temp%10)*(temp%10);
                temp /= 10;
            }
        }
        return n == 1;
    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode(int x) { val = x; }
 * }
 */
public class Solution {
    public ListNode removeElements(ListNode head, int val) {
        if(head == null) return null;

        ListNode fakeHead = new ListNode(-1);
        ListNode pre = fakeHead;
        fakeHead.next = head;
        while(head != null && head.val == val){
            head = head.next;
            fakeHead.next = head;
        }

        while(head != null){
            if(head.val != val){
                pre = head;
                head = head.next;
            }
            else {
                head = head.next;
                pre.next = head;
            }
        }
        return fakeHead.next;
    }
}

public class Solution {
    public int countPrimes(int n) {
        int res = 0;
        if (n <= 2)  return res; // A prime number must be >= 2.
        boolean[] flags = new boolean[n+1];

        int rootN = (int)Math.sqrt(n);
        for (int i=2; i<=rootN; ++i) {  // [1]
            // flags[i]==true means it has been marked before and
            // thus all its multiples can be skipped
            if (!flags[i]) {
                // Note: if we get here, i itself is a prime number

                // mark its multiples
                for (int j=i*i; j<=n; j+=i) {  // [2]
                    flags[j] = true;
                }
            }
        }

        for (int i=2; i<n; ++i) {
            if (!flags[i]) res++;
        }
        return res;
    }
}

