/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode(int x) { val = x; }
 * }
 */
public class Solution {
    // Iterative Solution
    public ListNode reverseList(ListNode head) {
        if(head == null || head.next == null) return head;

        ListNode fakeHead = new ListNode(-1);
        fakeHead.next = head;
        ListNode curNode = head.next;

        while(curNode != null){
            head.next = curNode.next;
            curNode.next = fakeHead.next;
            fakeHead.next = curNode;
            curNode = head.next;
        }
        return fakeHead.next;
    }
    // Recursive Solution
    public ListNode reverseList(ListNode head) {
        if (head == null || head.next == null)
            return head;
        ListNode p = reverseList(head.next);
        head.next.next = head;
        head.next = null;
        return p;
    }
}

public class Solution {
    public boolean containsNearbyDuplicate(int[] nums, int k) {
        Map<Integer, List<Integer>> map = new HashMap<>();
        for(int i=0; i<nums.length; i++){
            List<Integer> indexes = map.containsKey(nums[i]) ?
                map.get(nums[i]) : new ArrayList<Integer>();
            for(int ind=0; ind < indexes.size(); ind++){
                if(Math.abs(i - indexes.get(ind)) <= k) return true;
            }
            indexes.add(i);
            map.put(nums[i], indexes);
        }
        return false;
    }
}

class MyStack {

	Deque<Integer> queue1 = null, queue2 = null;

	public MyStack(){
		this.queue1 = new ArrayDeque<Integer>();
		this.queue2 = new ArrayDeque<Integer>();
	}

	// Push element x onto stack.
    public void push(int x) {
    	this.queue1.offer(x);
        this.queue2.offer(x);
    }

    // Removes the element on top of the stack.
    public int pop() {
        int topElem = -1;
    	this.queue2.clear();
    	while(!this.queue1.isEmpty()){
    		topElem = this.queue1.poll();
    		if(!this.queue1.isEmpty())
    			this.queue2.offer(topElem);
        }
    	this.queue1 = new ArrayDeque<Integer>(this.queue2);
    	return topElem;
    }

    // Get the top element.
    public int top() {
    	while(!this.queue1.isEmpty()){
        	int res = this.queue1.poll();
        	if(this.queue1.isEmpty()){
            	this.queue1 = new ArrayDeque<Integer>(this.queue2);
            	return res;
        	}
        }
    	return -1;
    }

    // Return whether the stack is empty.
    public boolean empty() {
        return this.queue2.isEmpty();
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public TreeNode invertTree(TreeNode root) {
		if (root == null) return root;
		swapNodes(root, null);
		return root;
	}

	private void swapNodes(TreeNode firstNode, TreeNode secondNode) {
		if (firstNode == null && secondNode == null) return;

		if (firstNode != null){
			TreeNode temp = firstNode.left;
			firstNode.left = firstNode.right;
			firstNode.right = temp;
			swapNodes(firstNode.left, firstNode.right);
		}

		if (secondNode != null){
			TreeNode temp = secondNode.left;
			secondNode.left = secondNode.right;
			secondNode.right = temp;
			swapNodes(secondNode.left, secondNode.right);
		}
		return;
	}
}

