class MyQueue {
    Stack<Integer> mainStack = new Stack<Integer>();
    Stack<Integer> assistStack = new Stack<Integer>();

    // Push element x to the back of queue.
    public void push(int x) {
        assistStack.push(x);
    }

    // Removes the element from in front of queue.
    public int pop() {
        if(mainStack.isEmpty()){
            while(!assistStack.isEmpty()){
                mainStack.push(assistStack.pop());
            }
        }
        return mainStack.pop();
    }

    // Get the front element.
    public int peek() {
        if(mainStack.isEmpty()){
            while(!assistStack.isEmpty()){
                mainStack.push(assistStack.pop());
            }
        }
        return mainStack.peek();
    }

    // Return whether the queue is empty.
    public boolean empty() {
        return mainStack.isEmpty() && assistStack.isEmpty();
    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode(int x) { val = x; }
 * }
 */
public class Solution {
    public boolean isPalindrome(ListNode head) {
        if(head == null || head.next==null)
            return true;

        //find list center
        ListNode fast = head;
        ListNode slow = head;
        while(fast.next!=null && fast.next.next!=null){
            fast = fast.next.next;
            slow = slow.next;
        }

        ListNode secondHead = slow.next;
        slow.next = null;
        //reverse second part of the list
        ListNode p1 = secondHead;
        ListNode p2 = p1.next;
        while(p1!=null && p2!=null){
            ListNode temp = p2.next;
            p2.next = p1;
            p1 = p2;
            p2 = temp;
        }

        secondHead.next = null;
        //compare two sublists now
        ListNode p = (p2==null?p1:p2);
        ListNode q = head;
        while(p!=null){
            if(p.val != q.val)
                return false;
            p = p.next;
            q = q.next;
        }
        return true;
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public TreeNode lowestCommonAncestor(TreeNode root, TreeNode p, TreeNode q) {
        int rootVal = root.val;
        if(p.val == rootVal || q.val == rootVal || p.val < rootVal && q.val > rootVal
                || p.val > rootVal && q.val < rootVal){
            return root;
        }
        if(p.val < root.val && q.val < root.val){
            return lowestCommonAncestor(root.left, p, q);
        }
        return lowestCommonAncestor(root.right, p, q);
    }
}

