    }
}

public class Solution {
    // Observe the pattern of num % 9
    public int addDigits(int num) {
        if(num == 0) return num;
        return num % 9 == 0 ? 9 : num % 9;
    }

    /***
     * Traditional solution
     *
     * res = 0
     * digitCount = digit count of num
     * mostSig = 10^(digitCount - 1)
     *
     * while(num > 9){
     *  digitCount = digit count of num
     *  mostSig = 10^(digitCount - 1)
     *  int temp = 0
     *  while(mostSig > 0){
     *      temp += num / mostSig
     *      num %= mostSig
     *      mostSig /= 10
     *  }
     *  num = temp
     * }
     *
     */
    public int addDigits(int num) {
        if(num == 0) return num;
        while(num > 9){
            int digitCount = String.valueOf(num).length();
            int mostSig = (int)Math.pow(10, digitCount-1);
            int temp = 0;
            while(mostSig > 0){
                temp += num / mostSig;
                num %= mostSig;
                mostSig /= 10;
            }
            num = temp;
        }
        return num;
    }
}

public class Solution {
    public boolean isUgly(int num) {
        if(num <= 0) 
            return false;
        if(num < 7)
            return true;
        while(num % 5 == 0) num /= 5;
        while(num % 3 == 0) num /= 3;
        while(num % 2 == 0) num /= 2;
        return num == 1;
    }
}

public class Solution {
    public int missingNumber(int[] nums) {
        if (nums.length == 0) return 0;
        int min = nums[0], max = nums[0];
        for(int num : nums){
            min = min < num ? min : num;
            max = max > num ? max : num;
        }
        if (min > 0) return 0;
        if (max == (nums.length - 1)) return nums.length;
        int total = max*(max+1)/2;
        for(int num : nums){
            total -= num;
        }
        return total;
    }
}

/* The isBadVersion API is defined in the parent class VersionControl.
      boolean isBadVersion(int version); */

public class Solution extends VersionControl {
    public int firstBadVersion(int n) {
        if(n == 1) return 1;
        int startIndex = 1, endIndex = n;
        while(startIndex < endIndex){
            int midIndex = startIndex + (endIndex - startIndex)/2;
            if(isBadVersion(midIndex)){
                endIndex = midIndex;
            }
            else{
                startIndex = midIndex+1;
            }
        }
        return endIndex;
    }
}

public class Solution {
    /**
     * Put nonzero numbers ahead
     * Set rest to be zeros
     */
    public void moveZeroes(int[] nums) {
        int curInd = 0;
        int scanInd = 0;
        while(scanInd < nums.length){
            if(nums[scanInd] == 0){
                scanInd++;
            } else {
                nums[curInd++] = nums[scanInd++];
            }
        }

        while(curInd < nums.length){
            nums[curInd++] = 0;
        }
    }
}

