public class Solution {
    public int largestPalindrome(int n) {
        if (n == 1) return 9;
        int max = (int) Math.pow(10, n) - 1;
        for (int v = max - 1; v > max / 10; v--) {
            StringBuilder vStr = new StringBuilder().append(v);
            long u = Long.valueOf(v + vStr.reverse().toString());
            for (long x = max; x * x >= u; x--)
                if (u % x == 0)
                    return (int) (u % 1337);
        }
        return 0;
    }
}

