public class Solution {
    public int findUnsortedSubarray(int[] nums) {
        int min = Integer.MAX_VALUE, max = Integer.MIN_VALUE;
        // Find where the unsorted array sure start
        // and get min after the turning point
        boolean flag = false;
        for (int i = 1; i < nums.length; i++) {
            if (nums[i] < nums[i - 1])
                flag = true;
            if (flag)
                min = Math.min(min, nums[i]);
        }
        // Find where the unsorted array sure end
        // and get the max after the turning point
        flag = false;
        for (int i = nums.length - 2; i >= 0; i--) {
            if (nums[i] > nums[i + 1])
                flag = true;
            if (flag)
                max = Math.max(max, nums[i]);
        }
        // Find the actual start point where
        // it is less than min
        int l, r;
        for (l = 0; l < nums.length; l++) {
            if (min < nums[l])
                break;
        }
        // Find the actual start point where
        // it is greater than max
        for (r = nums.length - 1; r >= 0; r--) {
            if (max > nums[r])
                break;
        }
        return r - l < 0 ? 0 : r - l + 1;
    }
}

public class Solution {
    public int findLHS(int[] nums) {
        if(nums.length <= 1) return 0;
        Map<Integer, Integer> numsMap = new HashMap<>();
        int res = 0;
        int cur = 0;

        for(int num : nums){
            numsMap.putIfAbsent(num, 0);
            numsMap.put(num, numsMap.get(num) + 1);
        }

        for(int num : nums){
            if(numsMap.containsKey(num + 1)){
                cur = numsMap.get(num) + numsMap.get(num + 1);
            }
            res = cur > res ? cur : res;
        }
        return res;
    }
}

public class Solution {
    // Just find the size of the left upper corner
    // Time complexity : O(x)
    // Space complexity : O(1)
    public int maxCount(int m, int n, int[][] ops) {
        for (int[] op: ops) {
            m = Math.min(m, op[0]);
            n = Math.min(n, op[1]);
        }
        return m * n;
    }
}

public class Solution {
    public String[] findRestaurant(String[] list1, String[] list2) {
        Map<String, Integer> string1ToIndex = new HashMap<>();
        Map<String, Integer> commonToSum = new HashMap<>();
        int minSum = Integer.MAX_VALUE;
        // Put list 1 into map
        for(int i = 0; i <list1.length; i++){
            string1ToIndex.put(list1[i], i);
        }
        // Get common interests and track min sum of indexes
        for(int i = 0; i <list2.length; i++){
            if(string1ToIndex.containsKey(list2[i])){
                int indexSum = string1ToIndex.get(list2[i]) + i;
                minSum = Math.min(minSum, indexSum);
                commonToSum.put(list2[i], indexSum);
            }
        }
        List<String> resList = new ArrayList<>();
        for(Map.Entry<String, Integer> entry : commonToSum.entrySet()){
            if(entry.getValue() == minSum)
                resList.add(entry.getKey());
        }
        String[] res = new String[resList.size()];
        for(int i = 0; i < resList.size(); i++){
            res[i] = resList.get(i);
        }
        return res;
    }
}

public class Solution {
    public boolean canPlaceFlowers(int[] flowerbed, int n) {
        int i = 0, count = 0;
        while (i < flowerbed.length) {
            if (flowerbed[i] == 0 && (i == 0 || flowerbed[i - 1] == 0) &&
                  (i == flowerbed.length - 1 || flowerbed[i + 1] == 0)) {
                flowerbed[i] = 1;
                count++;
            }
            i++;
        }
        return count >= n;
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    // Recursive Solution
    public String tree2str(TreeNode t) {
        if(t == null)
            return "";
        // Case 2: None of the left or the right child exist for the current node.
        if(t.left == null && t.right == null)
            return t.val + "";
        // Case 3: Only the left child exists for the current node.
        if(t.right == null)
            return t.val + "(" + tree2str(t.left) + ")";
        // Case 1: Both the left child and the right child exist for the current node.
        // Case 4: Only the right child exists for the current node.
        return t.val + "(" + tree2str(t.left) + ")(" + tree2str(t.right) + ")";
    }

    // Iterative Solution
    public String tree2str(TreeNode t) {
        if (t == null)
            return "";
        Stack < TreeNode > stack = new Stack < > ();
        stack.push(t);
        Set < TreeNode > visited = new HashSet < > ();
        String s = "";
        while (!stack.isEmpty()) {
            t = stack.peek();
            if (visited.contains(t)) {
                stack.pop();
                s += ")";
            } else {
                visited.add(t);
                s += "(" + t.val;
                if (t.left == null && t.right != null)
                    s += "()";
                if (t.right != null)
                    stack.push(t.right);
                if (t.left != null)
                    stack.push(t.left);
            }
        }
        return s.substring(1, s.length() - 1);
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public TreeNode mergeTrees(TreeNode t1, TreeNode t2) {
        if(t1 == null) return t2;
        if(t2 == null) return t1;
        TreeNode newRoot = new TreeNode(t1.val + t2.val);
        newRoot.left = mergeTrees(t1.left, t2.left);
        newRoot.right = mergeTrees(t1.right, t2.right);
        return newRoot;
    }
}

public class Solution {
    public int maxDistance(List<List<Integer>> arrays) {
        int min = arrays.get(0).get(0);
        int max = arrays.get(0).get(arrays.get(0).size() - 1);
        int res = 0;
        for(int i = 1; i < arrays.size(); i++){
            int len = arrays.get(i).size();
            int curMin = arrays.get(i).get(0);
            int curMax = arrays.get(i).get(len - 1);
            res = Math.max(res, curMax - min);
            res = Math.max(res, max - curMin);
            min = Math.min(min, curMin);
            max = Math.max(max, curMax);
        }
        return res;
    }
}

public class Solution {
    public int maximumProduct(int[] nums) {
        int min1 = Integer.MAX_VALUE, min2 = Integer.MAX_VALUE;
        int max1 = Integer.MIN_VALUE, max2 = Integer.MIN_VALUE, max3 = Integer.MIN_VALUE;
        for (int n: nums) {
            if (n <= min1) {
                min2 = min1;
                min1 = n;
            } else if (n <= min2) {     // n lies between min1 and min2
                min2 = n;
            }
            if (n >= max1) {            // n is greater than max1, max2 and max3
                max3 = max2;
                max2 = max1;
                max1 = n;
            } else if (n >= max2) {     // n lies betweeen max1 and max2
                max3 = max2;
                max2 = n;
            } else if (n >= max3) {     // n lies betwen max2 and max3
                max3 = n;
            }
        }
        return Math.max(min1 * min2 * max1, max1 * max2 * max3);
    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode(int x) { val = x; }
 * }
 */
public class Solution {
    /**
     * Time Complexity: O(n)
     * Space Complexity: O(1)
     *
     * Create carry to track the carry over
     * Create a pointer to track the node position
     * build the list while l1 not null or l2 not null or carry > 0
     *      reset next node and carry
     *
     */
    public ListNode addTwoNumbers(ListNode l1, ListNode l2) {
        // Since both lists are non-empty
        ListNode head = new ListNode((l1.val + l2.val) % 10);
        int carry = (l1.val + l2.val) / 10;
        ListNode cur = head;
        // Go to next nodes
        l1 = l1.next;
        l2 = l2.next;
        while(l1 != null || l2 != null || carry > 0){
            int totalVal = carry;
            totalVal += l1 == null ? 0 : l1.val;
            totalVal += l2 == null ? 0 : l2.val;
            carry = totalVal / 10;
            cur.next = new ListNode(totalVal % 10);
            cur = cur.next;
            l1 = l1 == null ? null : l1.next;
            l2 = l2 == null ? null : l2.next;
        }
        return head;
    }
}

public class Solution {
    /**
     * Time Complexity: O(n)
     * Space Complexity: O(n)
     *
     * Create a pointer to track the end of the substring
     * Create a pointer to track the start of the substring
     * Create a map for char to its latest index
     * Set both start and end pointer to the first char
     * while end is not the end of the s
     *  if(map have end char)
     *      update start pointer
     *  update max length
     *  put the index into the map for the char     *
     */
    public int lengthOfLongestSubstring(String s) {
        if(s.length() <= 1)
            return s.length();
        int startInd = 0;
        int endInd = 0;
        int maxLen = 0;
        Map<Character, Integer> charToIndMap = new HashMap<>();

        while(endInd < s.length()){
            char c = s.charAt(endInd);
            if(charToIndMap.containsKey(c)){
                startInd = (charToIndMap.get(c) + 1) > startInd ?
                        (charToIndMap.get(c) + 1) : startInd;
            }
            maxLen = Math.max(maxLen, endInd - startInd + 1);
            charToIndMap.put(c, endInd++);
        }
        return maxLen;
    }
}

public class Solution {
    /**
     * Time Complexity: O(n^2)
     * Space Complexity: O(1)
     *
     *
     * 1. Palindrome with center in the middle
     *  for each element scan both side until they are different
     * 2. Palindrome with center of two elements in the middle
     *  for every two elements scan both side until they are different
     *
     */
    public String longestPalindrome(String s) {
        if (s == null || s.length() <= 1) { return s;}

        String longest = s.substring(0, 1);
        for (int i = 0; i < s.length(); i++) {
            // get longest palindrome with center of i
            String tmp = helper(s, i, i);
            if (tmp.length() > longest.length()) {
                longest = tmp;
            }

            // get longest palindrome with center of i, i+1
            tmp = helper(s, i, i + 1);
            if (tmp.length() > longest.length()) {
                longest = tmp;
            }
        }
        return longest;
    }

    // Given a center, either one letter or two letter,
    // Find longest palindrome
    public String helper(String s, int begin, int end) {
        while (begin >= 0 && end <= s.length() - 1
                && s.charAt(begin) == s.charAt(end)) {
            begin--;
            end++;
        }
        return s.substring(begin + 1, end);
    }
}

public class Solution {
    public String convert(String s, int numRows) {
        if(s == null || s.length() == 0 || numRows == 1)
            return s;
        // string builders for each row
        StringBuilder[] sbs = new StringBuilder[numRows];
        // Have to initialize each element this way
        for(int i = 0; i < sbs.length; i++){
            sbs[i] = new StringBuilder();
        }
        int rowFlag = 1; // flag to incr or decr
        int rowInd = 0; // index for rows
        for(int i = 0; i < s.length(); i++){
            sbs[rowInd].append(s.charAt(i));
            if(rowInd == numRows - 1)
                rowFlag = -1;
            if(rowInd == 0)
                rowFlag = 1;
            rowInd += rowFlag;
        }
        StringBuilder res = new StringBuilder();
        for(StringBuilder sb : sbs){
            res.append(sb);
        }
        return res.toString();
    }
}

public class Solution {
    public int myAtoi(String str) {
        String curStr = str.trim();

        int ind = 0;
        boolean isPositive = true;
        long longRes = 0;

        if(curStr.length() > 0 && (curStr.charAt(0) == '+' || curStr.charAt(0) == '-')) {
            isPositive = curStr.charAt(0) == '+';
            ind++;
        }

        while(ind < curStr.length() && Character.isDigit(curStr.charAt(ind))){
            longRes = longRes*10 + curStr.charAt(ind) - '0';
            if(isPositive && longRes >= Integer.MAX_VALUE){
                return Integer.MAX_VALUE;
            }
            if(!isPositive && -1*longRes <= Integer.MIN_VALUE){
                return Integer.MIN_VALUE;
            }
            ind++;
        }

        return isPositive ? (int)longRes : -1*(int)longRes;
    }
}

public class Solution {
    public int maxArea(int[] height) {
        int front = 0;
        int tail = height.length - 1;
        int result = 0;

        while (front < tail) {
            result = Math.max(result, Math.min(height[front], height[tail]) * (tail - front));
            if (height[front] < height[tail]) {
                front++;
            } else {
                tail--;
            }
        }
        return result;
    }
}

public class Solution {
    public String intToRoman(int num) {
        String[] romans = {"I", "IV", "V", "IX", "X", "XL", "L", "XC","C", "CD", "D", "CM","M"};
        int[] numbers = {1, 4, 5, 9, 10, 40, 50, 90, 100, 400, 500, 900, 1000};

        StringBuilder res = new StringBuilder();

        int index = numbers.length - 1;
        while(num != 0 && index >= 0){
            if((num - numbers[index]) >= 0){
                num -= numbers[index];
                res.append(romans[index]);
            }else{
                index--;
            }
        }

        return res.toString();
    }
}

public class Solution {
    public List<List<Integer>> threeSum(int[] nums) {
        List<List<Integer>> res = new ArrayList<>();
        if(nums == null || nums.length < 3)
            return res;
        Arrays.sort(nums);
        // i for first element
        for(int i = 0; i < nums.length - 2; i++){
            // Stop if first elem is positive
            if(nums[i] > 0) break;
            // Skip duplicate for first elem
            if(i > 0 && nums[i] == nums[i - 1]) continue;
            int secondElemInd = i + 1; // track second elem
            int thirdElemInd = nums.length - 1; // track third elem
            int sum = 0;
            while(secondElemInd < thirdElemInd){
                // secondElemInd > i + 1 to ensure not first element
                if(secondElemInd > i + 1 && nums[secondElemInd] == nums[secondElemInd - 1]){
                    secondElemInd++; // skip duplicate for second element
                    continue;
                }
                // Stop if sum of first two element greater than 0
                if(nums[secondElemInd] + nums[i] > 0) break;
                sum = nums[i] + nums[secondElemInd] + nums[thirdElemInd];
                if(sum == 0){
                    List<Integer> li = new ArrayList<>();
                    li.add(nums[i]);
                    li.add(nums[secondElemInd]);
                    li.add(nums[thirdElemInd]);
                    res.add(li);
                    thirdElemInd--;
                    secondElemInd++;
                } else if(sum > 0){
                    thirdElemInd--;
                } else {
                    secondElemInd++;
                }
            }
        }
        return res;
    }
}

public class Solution {
    public int threeSumClosest(int[] nums, int target) {
        int n = nums.length;
        int ans = 0;
        int sum = 0;

        Arrays.sort(nums);
        if(nums.length <= 3){
            for(int i = 0; i < n; i++){
                sum += nums[i];
            }
            return sum;
        }

        ans = nums[0] + nums[1] + nums[2];
        for (int i = 0; i < n - 2; i++){
            int j = i + 1;
            int k = n - 1;
            while(j < k){
                sum = nums[i] + nums[j] + nums[k];
                if(sum == target){
                    return sum;
                }
                if(Math.abs(target - ans) > Math.abs(target - sum)){
                    ans = sum;
                }
                if(sum > target){
                    k--;
                } else{
                    j++;
                }
            }
        }
        return ans;
    }
}

public class Solution {
    /**
     * Time Complexity ~ O(3^n), Space Compelxity - O(n);
     *
     * backtrackingsame as permutation
     * backtracking for the letters for each number
     *
     */
    public List<String> letterCombinations(String digits) {
        List<String> re = new ArrayList<>();
        if(digits.length() == 0){
            return re;
        }
        char[][] pad = {
                {'0'},
                {'1'},
                {'a','b','c'},
                {'d','e','f'},
                {'g','h','i'},
                {'j','k','l'},
                {'m','n','o'},
                {'p','q','r','s'},
                {'t','u','v'},
                {'w','x','y','z'},
        };

        getletters(digits, pad, re, "", 0);
        return re;
    }

    private void getletters(String digits, char[][] pad,
                            List<String> re, String str, int start){
        if(str.length() == digits.length()){
            re.add(str);
            return;
        }

        int temp = digits.charAt(start)-'0';
        for(int j = 0; j < pad[temp].length; j++){
            getletters(digits, pad, re, str + pad[temp][j], start+1);
        }
    }
}

public class Solution {
    public List<List<Integer>> fourSum(int[] nums, int target) {
        List<List<Integer>> result = new LinkedList<List<Integer>>();
        if (nums.length < 4) {
            return result;
        }

        Arrays.sort(nums);
        Map<Integer, List<Integer>> toMatch = new HashMap<Integer, List<Integer>>();
        for (int i = 0; i < nums.length - 1; i++) {
            for (int j = i + 1; j < nums.length; j++) {
                int toMatchInt = target - nums[i] - nums[j];
                List<Integer> tmpList = new LinkedList<Integer>();
                if (toMatch.containsKey(toMatchInt)) {
                    tmpList = toMatch.get(toMatchInt);
                }

                tmpList.add(i);
                tmpList.add(j);
                toMatch.put(toMatchInt, tmpList);
            }
        }

        int firstValue = Integer.MAX_VALUE;
        for (int firstIndex = 0; firstIndex < nums.length - 3; firstIndex++) {
            if (nums[firstIndex] == firstValue) {
                continue; // Skip first duplicate
            }
            firstValue = nums[firstIndex];
            int secondIndex = firstIndex + 1;
            while (secondIndex < nums.length - 2) {
                int secondValue = nums[secondIndex];
                int tempSum = nums[firstIndex] + nums[secondIndex];
                if (toMatch.containsKey(tempSum)) {
                    List<Integer> matchList = toMatch.get(tempSum);
                    int count = Integer.MAX_VALUE;
                    for (int i = 0; i < matchList.size() - 1; i += 2) {
                        // If 3rd element Ind not greater then 2nd ind
                        // or duplicate pair found
                        if (matchList.get(i) <= secondIndex ||
                                (i > count && nums[matchList.get(i - 2)] == nums[matchList.get(i)]
                                        && nums[matchList.get(i - 1)] == nums[matchList.get(i + 1)])) {
                            continue;
                        }
                        List<Integer> tmpList = new LinkedList<Integer>();
                        tmpList.add(nums[firstIndex]);
                        tmpList.add(nums[secondIndex]);
                        tmpList.add(nums[matchList.get(i)]);
                        tmpList.add(nums[matchList.get(i + 1)]);
                        result.add(tmpList);
                        count = i;
                    }
                }
                while (++secondIndex < nums.length - 2 && nums[secondIndex] == secondValue) ;
            }
        }
        return result;
    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 * int val;
 * ListNode next;
 * ListNode(int x) { val = x; }
 * }
 */
public class Solution {
    public ListNode removeNthFromEnd(ListNode head, int n) {
        ListNode p = new ListNode(-1);
        p.next = head;

        ListNode q = head;
        head = p;

        for (int i = 0; i < n; i++)
            q = q.next;

        while (q != null) {
            q = q.next;
            p = p.next;
        }
        p.next = p.next.next;
        return head.next;
    }
}

public class Solution {
    public List<String> generateParenthesis(int n) {
        List<String> re = new ArrayList<String>();
        if(n == 0) return re;

        String str = "";
        getParenthesis(n, n, str, re);
        return re;
    }

    private void getParenthesis(int left, int right, String str, List<String>re){
        if(left == 0 && right == 0){
            re.add(str);
            return;
        }

        if(left>0){
            getParenthesis(left-1,right,str+"(",re);
        }

        if(right>left){
            getParenthesis(left,right-1,str+")",re);
        }
    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode(int x) { val = x; }
 * }
 */
public class Solution {
    public ListNode swapPairs(ListNode head) {
        ListNode dummy = new ListNode(0);
        dummy.next = head;
        ListNode current = dummy;
        while (current.next != null && current.next.next != null) {
            ListNode first = current.next;
            ListNode second = current.next.next;
            first.next = second.next;
            second.next = first;
            current.next = second;
            current = current.next.next;
        }
        return dummy.next;
    }
}

public class Solution {
    // The time complexity is O(logn).
    // This problem can be solved based on the fact that any number
    // can be converted to the format of the following:
    // num=a_0*2^0+a_1*2^1+a_2*2^2+...+a_n*2^n
    public int divide(int dividend, int divisor) {
        if(divisor==-1 && dividend == Integer.MIN_VALUE)
            return Integer.MAX_VALUE;
        int sign = 1;
        if (dividend < 0) {
            sign *= -1;
        }
        if (divisor < 0) {
            sign *= -1;
        }
        long a = dividend;
        long b = divisor;
        // must cast to long here for dealing with the Integer.MIN_VALUE
        // because Math.abs(-2147483648) > Integer.MAX_VALUE
        a = Math.abs(a);
        b = Math.abs(b);
        int count = 0;
        while (a >= b) {
            long temp = b;
            int multi = 1;
            while (a >= temp) {
                count += multi;
                a -= temp;
                temp += temp;
                multi += multi;
            }
        }
        return count * sign;
    }
}

public class Solution {
    public void nextPermutation(int[] nums) {
        if (nums.length <= 1) {
            return;
        }

        int indexdisorder = nums.length - 1;
        while (indexdisorder > 0 && nums[indexdisorder] <= nums[indexdisorder - 1]) {
            indexdisorder--;
        }
        if (indexdisorder == 0) {
            reverse(nums, 0, nums.length - 1);
            return;
        }
        indexdisorder--;

        int firstlarger = nums.length - 1;
        while (firstlarger > 0 && nums[firstlarger] <= nums[indexdisorder]) {
            firstlarger--;
        }

        swap(nums, indexdisorder, firstlarger);
        reverse(nums, indexdisorder + 1, nums.length - 1);
    }

    private void reverse(int[] nums, int first, int last) {
        while (first < last) {
            swap(nums, first, last);
            first++;
            last--;
        }
    }

    private void swap(int[] nums, int first, int last) {
        nums[first] = nums[first] + nums[last];
        nums[last] = nums[first] - nums[last];
        nums[first] = nums[first] - nums[last];
    }
}

public class Solution {
    public int search(int[] A, int target) {
        int l = 0, r = A.length - 1;
        while (l <= r) {
            int m = l + (r - l) / 2;
            if (A[m] == target)
                return m;
            if (A[l] <= A[m]) {
                if (target >= A[l] && target <= A[m])
                    r = m;
                else
                    l = m + 1;
            } else {
                if (target <= A[r] && target >= A[m])
                    l = m;
                else
                    r = m - 1;
            }
        }
        return -1;
    }
}

public class Solution {
    public int[] searchRange(int[] nums, int target) {
        int[] res = new int[]{-1, -1};
        if(nums == null || nums.length == 0)
            return res;
        res[0] = searchLeft(nums, target);
        if(res[0] != -1)
            res[1] = searchRight(nums, target);
        return res;
    }

    private int searchLeft(int[] nums, int target){
        int left = 0;
        int right = nums.length - 1;
        int mid = left + (right - left)/2;
        while(left < right - 1){
            if(nums[mid] < target)
                left = mid + 1;
            else {
                right = nums[mid] > target ? mid - 1 : mid;
            }
            mid = left + (right - left)/2;
        }
        // Check left first
        return nums[left] == target ? left :
                nums[right] == target ? right : -1;
    }

    private int searchRight(int[] nums, int target){
        int left = 0;
        int right = nums.length - 1;
        int mid = left + (right - left)/2;
        while(left < right - 1){
            if(nums[mid] > target)
                right = mid - 1;
            else {
                left = nums[mid] < target ? mid + 1 : mid;
            }
            mid = left + (right - left)/2;
        }
        // Check right first
        return nums[right] == target ? right :
                nums[left] == target ? left : -1;
    }
}

public class Solution {
    public boolean isValidSudoku(char[][] board) {

        HashMap<Character, ArrayList<Integer>> numHashset = new HashMap<Character, ArrayList<Integer>>();
        ArrayList<Integer> coords = new ArrayList<Integer>();

        for (int row = 0; row < board.length; row++) {
            for (int col = 0; col < board[0].length; col++) {
                if (board[row][col] == '.')
                    continue;
                coords = new ArrayList<Integer>();
                if (numHashset.containsKey(board[row][col])) {
                    coords = numHashset.get(board[row][col]);
                    if (!isValid(coords, row, col)) {
                        return false;
                    }
                }
                coords.add(row);
                coords.add(col);
                numHashset.put(board[row][col], coords);
            }
        }
        return true;
    }

    private boolean isValid(ArrayList<Integer> coords, int row, int col) {
        for (int i = 0; i < coords.size(); i += 2) {
            if (coords.get(i) == row || coords.get(i + 1) == col)
                return false;

            int dx = coords.get(i) / 3;
            int dy = coords.get(i + 1) / 3;
            for (int j = dx * 3; j < dx * 3 + 3; j++) {
                for (int k = dy * 3; k < dy * 3 + 3; k++)
                    if (j == row && k == col)
                        return false;
            }
        }

        return true;
    }
}

public class Solution {
    /**
     * Time Complexity: C(n,1) + C(n,2) + ..C(n, n) = O(2^n)
     * where n is the size of candidates,
     * and k is the max repeated times for each candidates.
     * Space complexity: O(m) where m is the size of array for the solution
     *
     * @param candidates
     * @param target
     * @return
     */

    public List<List<Integer>> combinationSum(int[] candidates, int target) {
        Arrays.sort(candidates);
        List<List<Integer>> re = new ArrayList<>();
        List<Integer> li = new ArrayList<>();
        getList(candidates, target, li, re, 0);
        return re;
    }

    private void getList(int[] candidates, int target, List<Integer> li,
                         List<List<Integer>> re, int start) {
        if(target==0){
            List<Integer> templi = new ArrayList<>(li);
            if(!re.contains(templi)){
                re.add(templi);
            }
            return;
        }

        for(int i=start; i<candidates.length; i++){
            if(target-candidates[i]>=0){
                li.add(candidates[i]);
                getList(candidates, target-candidates[i], li, re, i);
                li.remove(li.size()-1);
            }
        }
    }
}

public class Solution {
    /**
     * Time Complexity: C(n,1) + C(n,2) + ..C(n, n) = O(2^n)
     * where n is the size of candidates,
     * and k is the max repeated times for each candidates.
     * Space complexity: O(m) where m is the size of array for the solution
     *
     * @param candidates
     * @param target
     * @return
     */

    public List<List<Integer>> combinationSum(int[] candidates, int target) {
        Arrays.sort(candidates);
        List<List<Integer>> re = new ArrayList<>();
        List<Integer> li = new ArrayList<>();
        getList(candidates, target, li, re, 0);
        return re;
    }

    private void getList(int[] candidates, int target, List<Integer> li,
                         List<List<Integer>> re, int start) {
        if(target==0){
            List<Integer> templi = new ArrayList<>(li);
            if(!re.contains(templi)){
                re.add(templi);
            }
            return;
        }

        for(int i=start; i<candidates.length; i++){
            if(target-candidates[i]>=0){
                li.add(candidates[i]);
