                getList(candidates, target-candidates[i], li, re, i+1);
                li.remove(li.size()-1);
            }
        }
    }
}

public class Solution {
    public String multiply(String num1, String num2) {
        int[] num = new int[num1.length() + num2.length()];
        for (int i = 0; i < num1.length(); i++) {
            int carry = 0;
            int a = num1.charAt(num1.length() - 1 - i) - '0';
            for (int j = 0; j < num2.length(); j++) {
                int b = num2.charAt(num2.length() - 1 - j) - '0';
                num[i + j] += carry + a * b;
                carry = num[i + j] / 10;
                num[i + j] %= 10;
            }
            num[i + num2.length()] += carry;
        }
        int i = num.length - 1;
        while (i > 0 && num[i] == 0){
            i--;
        }

        StringBuilder temp = new StringBuilder("");
        while (i >= 0)
            temp.append((char) ('0' + num[i--]));

        return temp.toString();
    }
}

public class Solution {
    public List<List<Integer>> permute(int[] num) {

        List<List<Integer>> res = new ArrayList<List<Integer>>();
        List<Integer> curPer = new ArrayList<Integer>();

        int len = num.length;
        int[] used = new int[len];

        getPermutations(num, used, len, curPer, res);

        return res;

    }

    private void getPermutations(int[] num, int[] used, int len,
                                 List<Integer> curPer, List<List<Integer>> res) {

        if(curPer.size() == len){
            ArrayList<Integer> temp = new ArrayList<Integer>(curPer);
            res.add(temp);
            return;
        }

        for(int i=0; i<len; i++){
            if(used[i] != 1){
                used[i] = 1;
                curPer.add(num[i]);
                getPermutations(num, used, len, curPer, res);
                used[i] = 0;
                curPer.remove(curPer.size()-1);
            }
        }
    }
}

public class Solution {
    public List<List<Integer>> permuteUnique(int[] nums) {
        List<Integer> cal = new ArrayList<Integer>();
        List<List<Integer>> re = new ArrayList<List<Integer>>();

        Arrays.sort(nums);
        int[] used = new int[nums.length];
        getpermute(nums, used, cal, re);
        return re;
    }

    void getpermute(int[] nums, int[] used, List<Integer> cal, List<List<Integer>> re){
        if(cal.size() == nums.length){
            List<Integer> temp = new ArrayList<Integer>(cal);
            re.add(temp);
            return;
        }

        for(int i = 0; i < nums.length; i++){
            if(i > 0 && nums[i-1] == nums[i]) continue;
            if(used[i] == 1){
                continue;
            }
            cal.add(nums[i]);
            used[i] = 1;
            getpermute(nums, used, cal, re);
            used[i] = 0;
            cal.remove(cal.size()-1);
            while(i < nums.length-1 && nums[i] == nums[i+1]){
                i++;
            }
        }
    }
}

public class Solution {
    public void rotate(int[][] matrix) {
        int msize = matrix.length;
        if(msize==1) return;
        int i=0;
        int j=msize-1;

        while(i<j){
            rotateOutside(matrix, i, j);
            i++;
            j--;
        }
    }

    public void rotateOutside(int[][] matrix, int i, int j){
        for(int k=0; k<j-i; k++){
            swap(matrix, i, i+k, j-k, i);
            swap(matrix, j-k, i, j, j-k);
            swap(matrix, j, j-k, i+k, j);
        }
    }

    public void swap(int[][] matrix, int i, int j, int k, int l){
        int temp = matrix[i][j];
        matrix[i][j] = matrix[k][l];
        matrix[k][l] = temp;
    }
}

public class Solution {
    public List<List<String>> groupAnagrams(String[] strs) {
        List<List<String>> rst = new ArrayList<List<String>>();
        HashMap<String, List<String>> map = new HashMap<String, List<String>>();
        Arrays.sort(strs);

        for(int i = 0; i < strs.length; i++) {
            char[] strChar = strs[i].toCharArray();
            Arrays.sort(strChar);
            String str = new String(strChar);
            if(map.containsKey(str)) {
                map.get(str).add(strs[i]);
            }
            else {
                List<String> list = new ArrayList<String>();
                list.add(strs[i]);
                map.put(str, list);
            }
        }

        for(List<String> val : map.values()) {
            rst.add(val);
        }

        return rst;
    }
}

public class Solution {
    public double myPow(double x, int n) {
        if(n==0) return 1;
        if(n==1) return x;
        if(n==-1) return 1/x;
        if(n==2) return x*x;

        double temp = myPow(x, n / 2);
        if (n % 2 == 0)
            return temp*temp;
        else {
            if (n > 0)
                return x * temp*temp;
            else
                return 1 / x * temp*temp;
        }
    }
}

public class Solution {
    public List<Integer> spiralOrder(int[][] matrix) {
        List<Integer> res = new ArrayList<Integer>();
        if (matrix.length == 0) {
            return res;
        }

        int rowBegin = 0;
        int rowEnd = matrix.length - 1;
        int colBegin = 0;
        int colEnd = matrix[0].length - 1;
        while (rowBegin <= rowEnd && colBegin <= colEnd) {
            // Traverse Right
            for (int j = colBegin; j <= colEnd; j++) {
                res.add(matrix[rowBegin][j]);
            }
            rowBegin++;
            // Traverse Down
            for (int j = rowBegin; j <= rowEnd; j++) {
                res.add(matrix[j][colEnd]);
            }
            colEnd--;
            // Traverse Left
            if (rowBegin <= rowEnd) {
                for (int j = colEnd; j >= colBegin; j--) {
                    res.add(matrix[rowEnd][j]);
                }
            }
            rowEnd--;
            // Traver Up
            if (colBegin <= colEnd) {
                for (int j = rowEnd; j >= rowBegin; j--) {
                    res.add(matrix[j][colBegin]);
                }
            }
            colBegin++;
        }
        return res;
    }
}

public class Solution {
    public boolean canJump(int[] nums) {
        int reach = 1;
        for (int i = 0; i < reach && reach < nums.length; ++i)
            reach = Math.max(reach, nums[i] + i + 1);
        return reach >= nums.length;
    }
}

/**
 * Definition for an interval.
 * public class Interval {
 *     int start;
 *     int end;
 *     Interval() { start = 0; end = 0; }
 *     Interval(int s, int e) { start = s; end = e; }
 * }
 */
public class Solution {
    public List<Interval> merge(List<Interval> intervals) {
        List<Interval> result = new ArrayList<Interval>();
        if (intervals.size() == 0)
            return result;
        Collections.sort(intervals, new Comparator<Interval>() {
            public int compare(Interval o1, Interval o2) {
                return o1.start - o2.start;
            }
        });
        Interval travel = intervals.get(0);
        if (intervals.size() == 1) {
            result.add(travel);
            return result;
        }

        for (int i = 1; i < intervals.size(); i++) {
            Interval temp = intervals.get(i);
            if (temp.end < travel.start || temp.start > travel.end) {
                result.add(travel);
                travel = temp;
            } else {
                travel.start = Math.min(travel.start, temp.start);
                travel.end = Math.max(travel.end, temp.end);
            }
        }
        result.add(travel);
        return result;
    }
}

public class Solution {
    public int[][] generateMatrix(int n) {
        int[][] matrix = new int[n][n];

        if (n == 1) {
            matrix[0][0] = 1;
            return matrix;
        }

        int i = 0;
        int j = n - 1;
        int m = 1;

        while (j >= i) {
            m = spiral(m, i, j, matrix);
            i++;
            j--;
        }
        return matrix;
    }

    public int spiral(int m, int i, int j, int[][] matrix) {
        if (i == j) {
            matrix[i][i] = m;
            m++;
            return m;
        }

        for (int p = i; p < j; p++) {
            matrix[i][p] = m;
            m++;
        }

        for (int p = i; p < j; p++) {
            matrix[p][j] = m;
            m++;
        }

        for (int p = j; p > i; p--) {
            matrix[j][p] = m;
            m++;
        }

        for (int p = j; p > i; p--) {
            matrix[p][i] = m;
            m++;
        }

        return m;
    }
}

public class Solution {
    public String getPermutation(int n, int k) {
        int t = 1; // n! to each i
        List<Integer> numbers = new ArrayList<>(n);
        for (int i = 1; i <= n; i++) {
            t = t * i;
            numbers.add(i);
        }
        t /= n; // (n - 1)!
        k--;
        StringBuilder sb = new StringBuilder();
        for (int i = n - 1; i >= 1; i--) {
            int p = k / t; // find which number to use
            int np = numbers.get(p); // get the number to use
            sb.append(String.valueOf(np));
            numbers.remove(p); // remove used number
            k %= t; // what left for k
            t /= i; //(n - i)!
        }
        // append the last number
        sb.append(String.valueOf(numbers.get(0)));
        return sb.toString();
    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 * int val;
 * ListNode next;
 * ListNode(int x) {
 * val = x;
 * next = null;
 * }
 * }
 */
public class Solution {
    public ListNode rotateRight(ListNode head, int n) {
        if (head == null || head.next == null)
            return head;
        ListNode dummy = new ListNode(0);
        dummy.next = head;
        ListNode fast = dummy, slow = dummy;

        int len = 0;
        //Get the total length
        while (fast.next != null){
            len++;
            fast = fast.next;
        }
        //Get the i-n%i th node
        for (int j = len - n % len; j > 0; j--)
            slow = slow.next;
        //Do the rotation
        fast.next = dummy.next;
        dummy.next = slow.next;
        slow.next = null;

        return dummy.next;
    }
}

public class Solution {
    public int uniquePaths(int m, int n) {
        if(m <= 1 || n <= 1) return 1;
        int[][] dpTable = new int[m][n];

        // Only one way for first column or row
        for(int i = 0; i < n; i++){
            dpTable[0][i] = 1;
        }
        for(int i = 0; i < m; i++){
            dpTable[i][0] = 1;
        }
        for(int row = 1; row < m; row++){
            for(int col = 1; col < n; col++){
                // Ways to get current grid =
                // ways to get top of left grid
                dpTable[row][col] = dpTable[row-1][col] + dpTable[row][col-1];
            }
        }
        return dpTable[m-1][n-1];
    }
}

public class Solution {
    public int uniquePathsWithObstacles(int[][] obstacleGrid) {
        if(obstacleGrid == null || obstacleGrid.length == 0 ||
                obstacleGrid[0].length == 0) return 0;
        int m = obstacleGrid.length;
        int n = obstacleGrid[0].length;
        // If top left or bottom right element is 1
        if(obstacleGrid[0][0] == 1 || obstacleGrid[m - 1][n - 1] == 1) return 0;

        int[][] dpTable = new int[m][n];
        dpTable[0][0] = 1;
        // First row
        for(int i = 1; i < n; i++){
            dpTable[0][i] = (obstacleGrid[0][i] == 1 ||
                    dpTable[0][i - 1] == 0) ? 0 : 1;
        }
        // First col
        for(int i = 1; i < m; i++){
            dpTable[i][0] = (obstacleGrid[i][0] == 1 ||
                    dpTable[i - 1][0] == 0) ? 0 : 1;
        }
        for(int row = 1; row < m; row++){
            for(int col = 1; col < n; col++){
                // If has obstacle
                if(obstacleGrid[row][col] == 1){
                    dpTable[row][col] = 0;
                } else {
                    // update ways to current grid to be sum of
                    // ways to get top or left grid
                    dpTable[row][col] = dpTable[row-1][col] + dpTable[row][col-1];
                }
            }
        }
        return dpTable[m - 1][n - 1];
    }
}

public class Solution {
    public int minPathSum(int[][] grid) {
        int rows = grid.length;
        if(rows == 0)
            return 0;
        int cols = grid[0].length;

        int sum = 0;
        int[][] path = new int[rows][cols];
        path[0][0] = grid[0][0];

        for(int i = 0; i < rows; i++){
            for(int j = 0; j < cols; j++){
                if(i == 0 && j == 0) continue;
                if(i == 0 && j > 0){
                    path[i][j] = path[i][j - 1] + grid[i][j];
                } else if (j == 0 && i > 0){
                    path[i][j] = path[i - 1][j] + grid[i][j];
                } else {
                    path[i][j] = Math.min(path[i][j - 1], path[i - 1][j]) + grid[i][j];
                }
            }
        }
        return path[rows-1][cols-1];
    }
}

public class Solution {
    public String simplifyPath(String path) {
        String[] sts = path.split("/");
        Deque<String> pathDeque = new ArrayDeque<String>();
        for (String st : sts) {
            st = st.trim();
            if (st.length() == 0 || st.equals(".")){
                continue;
            } else if (st.equals("..")){
                if (pathDeque.isEmpty())
                    continue;
                else
                    pathDeque.removeLast();
            } else {
                pathDeque.addLast(st);
            }
        }
        StringBuilder result = new StringBuilder("/");
        if (pathDeque.isEmpty()) return result.toString();
        while (!pathDeque.isEmpty()){
            result.append(pathDeque.removeFirst() + "/");
        }
        return result.substring(0, result.length()-1);
    }
}

public class Solution {
    public void setZeroes(int[][] matrix) {
        int zero_frow = 0;
        int zero_fcol = 0;

        int rows = matrix.length;
        int cols = matrix[0].length;

        for (int i = 0; i < cols; i++) {
            if (matrix[0][i] == 0) {
                zero_frow = 1;
                break;
            }
        }

        for (int i = 0; i < rows; i++) {
            if (matrix[i][0] == 0) {
                zero_fcol = 1;
                break;
            }
        }

        for (int i = 1; i < rows; i++) {
            for (int j = 1; j < cols; j++) {
                if (matrix[i][j] == 0) {
                    matrix[i][0] = 0;
                    matrix[0][j] = 0;
                }
            }
        }
        for (int j = 1; j < cols; j++) {
            if (matrix[0][j] == 0) {
                for (int i = 1; i < rows; i++)
                    matrix[i][j] = 0;
            }
        }

        for (int i = 1; i < rows; i++) {
            if (matrix[i][0] == 0) {
                for (int j = 1; j < cols; j++)
                    matrix[i][j] = 0;
            }
        }

        if (zero_frow == 1) {
            for (int i = 0; i < cols; i++)
                matrix[0][i] = 0;
        }

        if (zero_fcol == 1) {
            for (int i = 0; i < rows; i++)
                matrix[i][0] = 0;
        }
    }
}

public class Solution {
    // rowMid = (rowLeft + rowRight)/2, columnMid = (columnUp + columnDown)/2
    // 1, First use columnMid index to search line number.
    // 2, Second use rowMid to search for the element.
    public boolean searchMatrix(int[][] matrix, int target) {
        // Special cases
        if (matrix == null || matrix.length == 0  || matrix[0].length == 0) {
            return false;
        }
        int rowNum = matrix.length - 1;
        int colNum = matrix[0].length - 1;
        if (target < matrix[0][0] || target > matrix[rowNum][colNum]) {
            return false;
        }

        //1. Binary search for column first
        int start = 0;
        int end = rowNum;
        int mid = (end - start) / 2 + start;
        while (start < end){
            if (target == matrix[mid][0]) { return true; }
            if (target < matrix[mid][0]){
                end = mid - 1;
            } else {
                start = mid + 1;
            }
            mid = (end - start) / 2 + start;
        }
        int rowToSearch = target < matrix[start][0] ? start-1: start;
        //2. Binary search in the row
        start = 0;
        end = colNum;
        mid = (end - start) / 2 + start;
        while (start < end) {
            if (target == matrix[rowToSearch][mid]) { return true; }
            if (target < matrix[rowToSearch][mid]){
                end = mid - 1;
            } else {
                start = mid + 1;
            }
            mid = (end - start) / 2 + start;
        }
        return target == matrix[rowToSearch][start];
    }
}

public class Solution {
    public void sortColors(int[] A) {
        int oneIndex = 0, twoIndex = A.length - 1, cur = 0;

        while (cur <= twoIndex) {
            switch (A[cur]) {
                case 0:
                    A[cur] = A[oneIndex];
                    A[oneIndex] = 0;
                    cur++;
                    oneIndex++;
                    break;
                case 1:
                    cur++;
                    break;
                case 2:
                    A[cur] = A[twoIndex];
                    A[twoIndex] = 2;
                    twoIndex--;
                    break;
            }
        }
    }
}

public class Solution {
    public List<List<Integer>> combine(int n, int k) {
        List<List<Integer>> res = new ArrayList<List<Integer>>();
        List<Integer> combination = new ArrayList<Integer>();

        getCombinations(n, k, 1, combination, res);
        return res;
    }

    private void getCombinations(int n, int k, int start,
                                 List<Integer> combination, List<List<Integer>> res) {
        if (combination.size() == k) {
            List<Integer> temp = new ArrayList<Integer>(combination);
            res.add(temp);
            return;
        }

        for (int i = start; i < n + 1; i++) {
            combination.add(i);
            getCombinations(n, k, i + 1, combination, res);
            combination.remove(combination.size() - 1);
        }
    }
}

public class Solution {
    public List<List<Integer>> subsets(int[] S) {
        List<List<Integer>> res = new ArrayList<List<Integer>>();
        List<Integer> subset = new ArrayList<Integer>();

        for (int level = 0; level <= S.length; level++) {
            getSubsets(S, level, 0, subset, res);
            subset = new ArrayList<Integer>();
        }
        return res;
    }

    private void getSubsets(int[] S, int level, int start, List<Integer> subset, List<List<Integer>> res) {
        if (start == level) {
            ArrayList<Integer> temp = new ArrayList<Integer>(subset);
            res.add(temp);
            return;
        }

        for (int s : S) {
            if (subset.size() == 0 || subset.get(subset.size() - 1) < s) {
                subset.add(s);
                getSubsets(S, level, start + 1, subset, res);
                subset.remove(subset.size() - 1);
            }
        }
    }
}

public class Solution {
    public boolean exist(char[][] board, String word) {
        for (int i = 0; i < board.length; i++) {
            for (int j = 0; j < board[i].length; j++) {
                if(exist(board, i, j, word, 0))
                    return true;
            }
        }
        return false;
    }

    private boolean exist(char[][] board, int x, int y, String word, int start) {
        if(start >= word.length()) return true;
        if(x < 0 || x >= board.length || y < 0 || y >= board[0].length)
            return false;
        if (board[x][y] == word.charAt(start++)) {
            char c = board[x][y];
            board[x][y] = '#';
            boolean res = exist(board, x + 1, y, word, start) ||
                    exist(board, x - 1, y, word, start) ||
                    exist(board, x, y + 1, word, start) ||
                    exist(board, x, y - 1, word, start);
            board[x][y] = c;
            return res;
        }
        return false;
    }
}

public class Solution {
    public int removeDuplicates(int[] A) {
        if (A.length == 0)
            return 0;
        if (A.length == 1)
            return 1;

        int p = 0;
        int q = 1;
        int count = 0;

        while (q < A.length) {
            if (A[p] == A[q]) {
                count++;
                if (count < 2)
                    A[++p] = A[q];
            } else {
                A[++p] = A[q];
                count = 0;
            }
            q++;
        }
        return p + 1;
    }
}

public class Solution {
    public boolean search(int[] A, int target) {
        int l = 0, r = A.length - 1;

        while (l <= r) {
            int m = l + (r - l) / 2;
            if (A[m] == target)
                return true;
            if (A[l] <= A[m]) {
                while (A[l] == A[m] && l < m)
                    m--;
                if (target >= A[l] && target <= A[m])
                    r = m;
                else
                    l = m + 1;
            } else if (A[l] >= A[m]) {
                while (A[l] == A[m] && m < r)
                    m++;
                if (target <= A[r] && target >= A[m])
                    l = m;
                else
                    r = m - 1;
            }
        }
        return false;
    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 * int val;
 * ListNode next;
 * ListNode(int x) {
 * val = x;
 * next = null;
 * }
 * }
 */
public class Solution {
    public ListNode deleteDuplicates(ListNode head) {

        if (head == null || head.next == null) return head;

        ListNode pre = new ListNode(Integer.MAX_VALUE);
        pre.next = head;
        head = pre;
        ListNode cur = head.next, post = cur.next;

        if (cur.val != post.val) pre = pre.next;

        while (post.next != null) {
            if (cur.val == post.val || post.val == post.next.val) {
                cur = cur.next;
                post = post.next;
            } else {
                cur = cur.next;
                post = post.next;
                pre.next = cur;
                pre = pre.next;
            }
        }

        if (cur.val != post.val) {
            pre.next = post;
        } else {
            pre.next = null;
        }
        return head.next;

    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 * int val;
 * ListNode next;
 * ListNode(int x) {
 * val = x;
 * next = null;
 * }
 * }
 */
public class Solution {
    public ListNode partition(ListNode head, int x) {
        if (head == null) return head;
        ListNode larger, smaller, pre, cur, fakehead = new ListNode(-1), temp;
        fakehead.next = head;
        smaller = fakehead;
        larger = head;
        while (larger != null && larger.val < x) {
            smaller = smaller.next;
            larger = larger.next;
        }

        if (larger == null) return head;

        pre = larger;
        cur = pre.next;
        while (cur != null) {
            if (cur.val >= x) {
                cur = cur.next;
                pre = pre.next;
            } else {
                temp = cur.next;
                pre.next = cur.next;
                cur.next = larger;
                smaller.next = cur;
                smaller = smaller.next;
                cur = temp;
            }
        }
        return fakehead.next;
    }
}

public class Solution {
    public List<Integer> grayCode(int n) {
        List<Integer> re = new ArrayList<>();
        re.add(0);
        if (n == 0) return re;
        re.add(1);
        if (n == 1) return re;
        int i = 2;
        int times = 2;
        while (i++ <= n) {
            int s = re.size();
            for (int j = s - 1; j >= 0; j--) {
                re.add(re.get(j) + times);
            }
            times *= 2;
        }
        return re;
    }
}

public class Solution {
    public List<List<Integer>> subsetsWithDup(int[] num) {
        List<List<Integer>> res = new ArrayList<List<Integer>>();

        Arrays.sort(num);
        for (int level = 0; level <= num.length; level++) {
            List<Integer> subset = new ArrayList<Integer>();
            getSubsets(num, level, 0, subset, res);
        }
        return res;
    }

    private void getSubsets(int[] num, int level, int start,
                            List<Integer> subset, List<List<Integer>> res) {
        if (subset.size() == level) {
            List<Integer> temp = new ArrayList<Integer>(subset);
            res.add(temp);
            return;
        }

        for (int i = start; i < num.length; i++) {
            subset.add(num[i]);
            getSubsets(num, level, i + 1, subset, res);
            subset.remove(subset.size() - 1);
            while (i < num.length - 1 && num[i + 1] == num[i])
                i++;
        }
    }
}

public class Solution {
    public int numDecodings(String s) {
        if(s == null || s.length() == 0 || s.charAt(0) == '0')
            return 0;
        if(s.length() == 1)
            return 1;

        int[] dp = new int[s.length()];
        dp[0] = 1;
        if(Integer.parseInt(s.substring(0,2)) > 26){
            dp[1] = s.charAt(1) != '0' ? 1 : 0;
        } else {
            dp[1] = s.charAt(1) != '0' ? 2 : 1;
        }

        for(int i = 2; i < s.length(); i++){
            if(s.charAt(i) != '0'){
                dp[i] += dp[i-1]; // way to decode without last digit
            }

            int val = Integer.parseInt(s.substring(i-1, i+1)); // the val form by last two digits
            if(val <= 26 && val >= 10){
                dp[i] += dp[i-2]; // way to decode without last two digits
            }
        }
        return dp[s.length()-1];
    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode(int x) {
 *         val = x;
 *         next = null;
 *     }
 * }
 */
public class Solution {
    public ListNode reverseBetween(ListNode head, int m, int n) {
        if(m==n) return head;

        ListNode first = new ListNode(-1);
        first.next = head;
        ListNode p = head;
        ListNode q, r;
        head = first;

        for(int i=1; i<m; i++){
            first = first.next;
            p = p.next;
        }

        if(p.next == null)
            return head.next;

        q = p.next;
        r = q.next;
        int count = 1;
        while(count<n-m){
            q.next = p;
            p = q;
            q = r;
            r = q.next;
            count++;
        }

        q.next = p;
        first.next.next = r;
        first.next = q;

        return head.next;
    }
}

public class Solution {
    public List<String> restoreIpAddresses(String s) {
        List<String> re = new ArrayList<String>();
        String[] sb = new String[4];
        getIpAddresses(re, sb, s, 0);
        return re;
    }

    public void getIpAddresses(List<String> re, String[] sb, String s, int level) {
        int len = s.length();
        // Too few or too many chars left for rest levels
        // Last level but still has chars left
        if (level < 4 && (len < 4 - level || len > 3 * (4 - level)) || level == 4 && len > 0)
            return;
        // If reach level 4
        if (level == 4) {
            StringBuilder temp = new StringBuilder();
            for (int i = 0; i < 4; i++) {
                temp.append(sb[i]);
                temp.append('.');
            }
            temp.deleteCharAt(temp.length() - 1);
            re.add(temp.toString());
            return;
        }
        // If first char is 0
        if (s.charAt(0) == '0') {
            sb[level] = "0";
            getIpAddresses(re, sb, s.substring(1), level + 1);
            return;
        }
        // Get each possible ip address of 1 to 3 chars
        for (int j = 1; j < 4; j++) {
            if (j > len) break;
            String ipStr = s.substring(0, j);
            if (Integer.parseInt(ipStr) <= 255) {
                sb[level] = ipStr;
                getIpAddresses(re, sb, s.substring(j), level + 1);
            }
        }
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    // Recursive Solution
    public List<Integer> inorderTraversal(TreeNode root) {
        List<Integer> re = new ArrayList<>();
        if(root == null) return re;
        re.addAll(inorderTraversal(root.left));
        re.add(root.val);
        re.addAll(inorderTraversal(root.right));
        return re;
    }

    // Iterative Solution
    public List<Integer> inorderTraversal(TreeNode root) {
        List<Integer> re = new ArrayList<>();
        Stack<TreeNode> stack = new Stack<>();
        if (root == null) return re;
        while (!stack.empty() || root != null) {
            if (root != null) {
                stack.push(root);
                root = root.left;
            } else {
                root = stack.pop();
                re.add(root.val);
                root = root.right;
            }
        }
        return re;
    }

    // Morris Traversal method
    public List<Integer> inorderTraversal(TreeNode root) {
        List<Integer> res = new ArrayList<Integer>();
        TreeNode temp = null;
        while(root!=null){
            if(root.left!=null){
                // connect threading for root
                temp = root.left;
                while(temp.right!=null && temp.right != root)
                    temp = temp.right;
                // the threading already exists
                if(temp.right!=null){
                    temp.right = null;
                    res.add(root.val);
                    root = root.right;
                }else{
                    // construct the threading
                    temp.right = root;
                    root = root.left;
                }
            }else{
                res.add(root.val);
                root = root.right;
            }
        }
        return res;
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 * int val;
 * TreeNode left;
 * TreeNode right;
 * TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public List<TreeNode> generateTrees(int n) {
        if (n == 0) {
            return new ArrayList<>();
        }
        return createTree(1, n);
    }

    public List<TreeNode> createTree(int start, int end) {
        List<TreeNode> trees = new ArrayList<>();
        if (end <= start) {
            TreeNode head = end < start ? null : new TreeNode(start);
            trees.add(head);
            return trees;
        }

        for (int i = start; i <= end; i++) {
            List<TreeNode> lefts = createTree(start, i - 1);
            List<TreeNode> rights = createTree(i + 1, end);
            for (TreeNode l : lefts) {
                for (TreeNode r : rights) {
                    TreeNode head = new TreeNode(i);
                    head.left = l;
                    head.right = r;
                    trees.add(head);
                }
            }
        }
        return trees;
    }
}

public class Solution {
    public int numTrees(int n) {
        int[] count = new int[n+1];
        count[0] =1;
        count[1] =1;
        for(int i =2; i<=n; i++){
            for(int j =0; j<i; j++){
                count[i] += count[j]*count[i-j-1];
            }
        }
        return count[n];
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    // Recursive Solution
    public boolean isValidBST(TreeNode root) {
        // Make sure use Long
        return validBST(root, Long.MIN_VALUE, Long.MAX_VALUE);
    }

    private boolean validBST(TreeNode root, long min, long max){
        if(root == null) return true;
        if(root.val >= max || root.val <= min )
            return false;
        return validBST(root.left, min, root.val) &&
                validBST(root.right, root.val, max);
    }

    // Iterative solution
    public boolean isValidBST(TreeNode root) {
        if (root == null) return true;
        Stack<TreeNode> stack = new Stack<>();
        TreeNode pre = null;
        while (root != null || !stack.isEmpty()) {
            while (root != null) {
                stack.push(root);
                root = root.left;
            }
            root = stack.pop();
            if(pre != null && root.val <= pre.val)
                return false;
            pre = root;
            root = root.right;
        }
        return true;
    }
}

/**
 * Definition for binary tree
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public List<List<Integer>> levelOrder(TreeNode root) {
        List<List<Integer>> res = new ArrayList<List<Integer>>();
        if(root == null) return res;

        Deque<TreeNode> curLevel = new ArrayDeque<TreeNode>();
        Deque<TreeNode> nextLevel = new ArrayDeque<TreeNode>();
        curLevel.addLast(root);

        while(!curLevel.isEmpty()){
            List<Integer> curList = new ArrayList<Integer>();
            for(TreeNode n : curLevel){
                curList.add(n.val);
                if(n.left != null) nextLevel.addLast(n.left);
                if(n.right != null) nextLevel.addLast(n.right);
            }
            res.add(curList);
            curLevel = new ArrayDeque<TreeNode>(nextLevel);
            nextLevel.clear();
        }
        return res;
    }
}

/**
 * Definition for binary tree
 * public class TreeNode {
 * int val;
 * TreeNode left;
 * TreeNode right;
 * TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public List<List<Integer>> zigzagLevelOrder(TreeNode root) {
        List<List<Integer>> res = new ArrayList<>();
        if (root == null) return res;
        boolean order = true;
        List<TreeNode> toVisit = new ArrayList<>();
        toVisit.add(root);
        while (!toVisit.isEmpty()) {
            List<TreeNode> next = new ArrayList<>();
            List<Integer> temp = new ArrayList<>();
            for (TreeNode node : toVisit) {
                temp.add(node.val);
            }
            res.add(temp);
            for (int i = toVisit.size() - 1; i >= 0; i--) {
                TreeNode node = toVisit.get(i);
                if (order) {
                    if (node.right != null) next.add(node.right);
                    if (node.left != null) next.add(node.left);
                } else {
                    if (node.left != null) next.add(node.left);
                    if (node.right != null) next.add(node.right);
                }
            }
            order = order ? false : true;
            toVisit = new ArrayList<TreeNode>(next);
        }
        return res;
    }
}

/**
 * Definition for binary tree
 * public class TreeNode {
 * int val;
 * TreeNode left;
 * TreeNode right;
 * TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public TreeNode buildTree(int[] preorder, int[] inorder) {
        if (preorder.length == 0 || preorder.length != inorder.length) return null;
        return build(preorder, 0, preorder.length - 1, inorder, 0, inorder.length - 1);
    }

    public TreeNode build(int[] pre, int start1, int end1, int[] in, int start2, int end2) {
        if (start1 > end1 || start2 > end2) return null;
        int val = pre[start1];
        TreeNode cur = new TreeNode(val);
        int k = start2;
        for (; k <= end2; k++)
            if (in[k] == val) break;
        cur.left = build(pre, start1 + 1, start1 + k - start2, in, start2, k - 1);
        cur.right = build(pre, start1 + k - start2 + 1, end1, in, k + 1, end2);
        return cur;
    }
}

/**
 * Definition for binary tree
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {

    public TreeNode buildTree(int[] inorder, int[] postorder) {
        int inStart = 0;
        int inEnd = inorder.length-1;
        int postStart =0;
        int postEnd = postorder.length-1;

        return buildTree(inorder, inStart, inEnd, postorder, postStart, postEnd);
    }

    public TreeNode buildTree(int[] inorder, int inStart, int inEnd,
                              int[] postorder, int postStart, int postEnd){
        if(inStart > inEnd || postStart > postEnd)
            return null;

        int rootValue = postorder[postEnd];
        TreeNode root = new TreeNode(rootValue);

        int k=0;
        for(int i=0; i< inorder.length; i++){
            if(inorder[i]==rootValue){
                k = i;
                break;
            }
        }

        root.left = buildTree(inorder, inStart, k-1, postorder, postStart, postStart+k-(inStart+1));
        // Becuase k is not the length, it it need to -(inStart+1) to get the length
        root.right = buildTree(inorder, k+1, inEnd, postorder, postStart+k-inStart, postEnd-1);
        // postStart+k-inStart = postStart+k-(inStart+1) +1

        return root;
    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode(int x) { val = x; next = null; }
 * }
 */
/**
 * Definition for binary tree
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public TreeNode sortedListToBST(ListNode head) {
        // calculate list length
        int len = 0;
        ListNode cur = head;
        while (cur != null) {
            cur = cur.next;
            len++;
        }
        // build the BST
        return listToBST(head, 0, len - 1);
    }

    private TreeNode listToBST(ListNode head, int low, int high) {
        if (low > high)
            return null;
        int mid = low + (high - low) / 2;
        // build up tree recursively
        TreeNode left = listToBST(head, low, mid - 1);
        TreeNode root = new TreeNode(head.val);
        root.left = left;
        // Java pass in Object by reference, so we can't change head but we can
        // change its content :)
        if (head.next != null) { // "move to next"
            head.val = head.next.val;
            head.next = head.next.next;
            root.right = listToBST(head, mid + 1, high);
        }
        return root;
    }
}

/**
 * Definition for binary tree
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public List<List<Integer>> pathSum(TreeNode root, int sum) {
        List<List<Integer>> resSet = new ArrayList<>();
        findPathSum(root, sum, new ArrayList<Integer>(), resSet);
        return resSet;
    }

    private void findPathSum(TreeNode root, int sum,
                             List<Integer> path, List<List<Integer>> resSet) {
        if (root == null) return;
        path.add(root.val);
        // get to a leaf and found a path
        if (root.left == null && root.right == null && sum == root.val) {
            // has to make a copy, otherwise the content may be changed
            List<Integer> curPath = new ArrayList<>(path);
            resSet.add(curPath);
        }
        findPathSum(root.left, sum - root.val, path, resSet);
        findPathSum(root.right, sum - root.val, path, resSet);
        path.remove(path.size()-1);
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {

    // Recursive Solution
    public void flatten(TreeNode root) {
        flattenTree(root);
    }

    private TreeNode flattenTree(TreeNode root){
        if (root == null ||
                root.left == null && root.right == null) return root;
        // Convert right subtree before assign root.righ
        TreeNode rightSubtree = flattenTree(root.right);
        // Set root.right to flattened left subtree
        root.right = flattenTree(root.left);
        root.left = null; // set left to null
        // Find tail of the linked list
        TreeNode cur = root;
        while(cur.right != null)
            cur = cur.right;
        // Set right pointer to right sub tree
        cur.right = rightSubtree;
        return root;
    }

    // Iterative Solution
    public void flatten(TreeNode root) {
        while (root != null) {
            if (root.left != null) {
                TreeNode p = root.left;
                while (p.right != null) {
                    p = p.right;
                }
                p.right = root.right;
                root.right = root.left;
                root.left = null;
            }
            root = root.right;
        }
    }
}

/**
 * Definition for binary tree with next pointer.
 * public class TreeLinkNode {
 *     int val;
 *     TreeLinkNode left, right, next;
 *     TreeLinkNode(int x) { val = x; }
 * }
 */
public class Solution {
    public void connect(TreeLinkNode root) {
        if(root == null || root.left == null) return;

        TreeLinkNode cur, level = root, head = root;
        while(root.left != null){
            root.left.next = root.right;
            cur = root.right;
            while(level.next != null){
                level = level.next;
                cur.next = level.left;
                level.left.next = level.right;
                cur = level.right;
            }
            level = root.left;
            root = root.left;
        }
    }
}

/**
 * Definition for binary tree with next pointer.
 * public class TreeLinkNode {
 *     int val;
 *     TreeLinkNode left, right, next;
 *     TreeLinkNode(int x) { val = x; }
 * }
 */
public class Solution {
    public void connect(TreeLinkNode root) {
        TreeLinkNode first = root;
        while (first != null) {
            fillRow(first);
            // move to next level
            while (first != null && first.left == null
                    && first.right == null) first = first.next;
            if (first != null)
                first = (first.left != null) ? first.left : first.right;
        }
    }

    private void fillRow(TreeLinkNode cur) {
        while (cur != null) {
            if (cur.left != null && cur.right != null) {
                cur.left.next = cur.right;
            }

            // find next has-child node
            TreeLinkNode next = cur.next;
            while (next != null && next.left == null && next.right == null) {
                next = next.next;
            }
            if (next == null) break;
            // find next child and link it
            TreeLinkNode nextChild = (next.left != null) ? next.left : next.right;
            if (cur.right != null) {
                cur.right.next = nextChild;
            } else if (cur.left != null) {
                cur.left.next = nextChild;
            }

            // move to next has-child node directly
            cur = next;
        }
    }
}

public class Solution {
    public int minimumTotal(List<List<Integer>> triangle) {
        int[] total = new int[triangle.size()];
        int l = triangle.size() - 1;
        for (int i = 0; i < triangle.get(l).size(); i++) {
            total[i] = triangle.get(l).get(i);
        }

        // iterate from last second row
        for (int i = triangle.size() - 2; i >= 0; i--) {
            for (int j = 0; j < triangle.get(i + 1).size() - 1; j++) {
                total[j] = triangle.get(i).get(j) + Math.min(total[j], total[j + 1]);
            }
        }
        return total[0];
    }
}

public class Solution {
    public int ladderLength(String beginWord, String endWord, List<String> wordList) {
        Set<String> dict = new HashSet<>();
        dict.addAll(wordList);

        if (dict.size() == 0) return 0;

        LinkedList<String> wordQueue = new LinkedList<String>();
        LinkedList<Integer> distanceQueue = new LinkedList<Integer>();

        wordQueue.add(beginWord);
        distanceQueue.add(1);

        while (!wordQueue.isEmpty()) {
            String currWord = wordQueue.pop();
            Integer currDistance = distanceQueue.pop();

            if (currWord.equals(endWord)) {
                return currDistance;
            }

            for (int i = 0; i < currWord.length(); i++) {
                char[] currCharArr = currWord.toCharArray();
                for (char c = 'a'; c <= 'z'; c++) {
                    currCharArr[i] = c;

                    String newWord = new String(currCharArr);
                    if (dict.contains(newWord)) {
                        wordQueue.add(newWord);
                        distanceQueue.add(currDistance + 1);
                        dict.remove(newWord);
                    }
                }
            }
        }
        return 0;
    }
}

/**
 * Definition for binary tree
 * public class TreeNode {
 * int val;
 * TreeNode left;
 * TreeNode right;
 * TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public int sumNumbers(TreeNode root) {
        if (root == null) return 0;
        return sumNumbersHelper(root, root.val, 0);
    }

    // path is the sum from root to the current node
    // sum is the sum of all root-to-leaf numbers up to the current node
    private int sumNumbersHelper(TreeNode root, int path, int sum) {
        if (root.left == null && root.right == null) { // reach a leaf
            return sum + path;
        }

        if (root.left != null) { // go to left subtree
            sum = sumNumbersHelper(root.left, path * 10 + root.left.val, sum);
        }
        if (root.right != null) { // go to right subtree
            sum = sumNumbersHelper(root.right, path * 10 + root.right.val, sum);
        }

        return sum;
    }
}

public class Solution {
    public void solve(char[][] board) {
        if (board.length <= 2 || board[0].length <= 2) return;
        int rows = board.length, columns = board[0].length;

        // Start from 'O's on the edge and mark connected ones as non-flipable.
        // first and last columns
        for (int i = 1; i < rows - 1; ++i) {
            if (board[i][0] == 'O')
                markBFS(board, i, 1);
            if (board[i][columns - 1] == 'O')
                markBFS(board, i, columns - 2);
        }
        // first and last rows
        for (int j = 1; j < columns - 1; ++j) {
            if (board[0][j] == 'O')
                markBFS(board, 1, j);
            if (board[rows - 1][j] == 'O')
                markBFS(board, rows - 2, j);
        }

        // flip
        for (int i = 1; i < rows - 1; ++i) {
            for (int j = 1; j < columns - 1; ++j) {
                if (board[i][j] == 'O') {
                    board[i][j] = 'X';
                } else if (board[i][j] == 'N') {
                    board[i][j] = 'O';
                }
            }
        }
    }

    private void markBFS(char[][] board, int row, int col) {
        Queue<Integer> que = new LinkedList<Integer>();
        mark(board, row, col, que);
        int rows = board.length, columns = board[0].length;
        while (!que.isEmpty()) {
            int cell = que.poll();
            int x = cell / columns, y = cell % columns;
            // push its neighbors to stack if needed
            if (x + 1 < rows - 1)
                mark(board, x + 1, y, que);
            if (x - 1 > 0)
                mark(board, x - 1, y, que);
            if (y + 1 < columns - 1)
                mark(board, x, y + 1, que);
            if (y - 1 > 0)
                mark(board, x, y - 1, que);
        }
    }

    private void mark(char[][] board, int row, int col, Queue<Integer> que) {
        if (board[row][col] != 'O') return;
        board[row][col] = 'N';
        int columns = board[0].length;
        que.offer(row * columns + col);
    }
}

public class Solution {
    public List<List<String>> partition(String s) {
        List<List<String>> res = new ArrayList<List<String>>();
        int len = s.length();
        boolean[][] palindromeTable = new boolean[len][len];

        // build palindrome dp table
        for (int i = 0; i < len; i++) {
            palindromeTable[i][i] = true;

            int l = i, r = i + 1;
            while (l >= 0 && r <= len - 1 && s.charAt(l) == s.charAt(r)) {
                palindromeTable[l--][r++] = true;
            }

            l = i - 1;
            r = i + 1;
            while (l >= 0 && r <= len - 1 && s.charAt(l) == s.charAt(r)) {
                palindromeTable[l--][r++] = true;
            }
        }
        List<String> partition = new ArrayList<String>();
        addPalindrome(s, 0, partition, res, palindromeTable);

        return res;
    }

    private void addPalindrome(String s, int start, List<String> partition,
                               List<List<String>> result, boolean[][] palindromeTable) {
        // stop condition
        if (start == s.length()) {
            List<String> temp = new ArrayList<String>(partition);
            result.add(temp);
            return;
        }

        for (int i = start; i < s.length(); i++) {
            if (palindromeTable[start][i]) {
                String str = s.substring(start, i + 1);
                partition.add(str);
                addPalindrome(s, i + 1, partition, result, palindromeTable);
                partition.remove(partition.size() - 1);
            }
        }
    }
}

/**
 * Definition for undirected graph.
 * class UndirectedGraphNode {
 * int label;
 * ArrayList<UndirectedGraphNode> neighbors;
 * UndirectedGraphNode(int x) { label = x; neighbors = new ArrayList<UndirectedGraphNode>(); }
 * };
 */
public class Solution {

    private UndirectedGraphNode cloneBFS(UndirectedGraphNode root) {
        if (root == null)
            return root;

        ArrayDeque<UndirectedGraphNode> que = new ArrayDeque<UndirectedGraphNode>();
        que.addLast(root);

        HashMap<UndirectedGraphNode, UndirectedGraphNode> visited = new HashMap<UndirectedGraphNode, UndirectedGraphNode>();
        UndirectedGraphNode rootCopy = new UndirectedGraphNode(root.label);
        visited.put(root, rootCopy);

        // BFS
        while (!que.isEmpty()) {
            root = que.removeFirst();
            UndirectedGraphNode node = visited.get(root);
            for (UndirectedGraphNode nb : root.neighbors) {
                if (visited.containsKey(nb)) {
                    node.neighbors.add(visited.get(nb));
                } else {
                    UndirectedGraphNode n = new UndirectedGraphNode(nb.label);
                    node.neighbors.add(n);
                    visited.put(nb, n);
                    que.addLast(nb);
                }
            }
        }

        return rootCopy;
    }

    public UndirectedGraphNode cloneGraph(UndirectedGraphNode node) {
        return cloneBFS(node);
    }
}

public class Solution {
    public int canCompleteCircuit(int[] gas, int[] cost) {
        int[] diff = new int[gas.length];
        for (int i = 0; i < gas.length; ++i) {
            diff[i] = gas[i] - cost[i];
        }

        int leftGas = 0, sum = 0, startnode = 0;
        for (int i = 0; i < gas.length; ++i) {
            leftGas += diff[i];
            sum += diff[i];
            if (sum < 0) {
                startnode = i + 1;
                sum = 0;
            }
        }
        if (leftGas < 0)
            return -1;
        else
            return startnode;
    }
}

public class Solution {
    public int singleNumber(int[] A) {
        if (A.length <= 3) return A[0];

        int bitSum = 0, res = 0;
        for (int i = 0; i < 32; i++) {
            bitSum = 0;
            for (int a : A) {
                if ((a & (1 << i)) != 0) bitSum++;
            }
            if (bitSum % 3 != 0) res |= (1 << i);
        }
        return res;
    }
}

/**
 * Definition for singly-linked list with a random pointer.
 * class RandomListNode {
 * int label;
 * RandomListNode next, random;
 * RandomListNode(int x) { this.label = x; }
 * };
 */
public class Solution {
    // Solution without map, 3 rounds of scan
    public RandomListNode copyRandomList(RandomListNode head) {
        RandomListNode iter = head, next;

        // First round: make copy of each node,
        // and link them together side-by-side in a single list.
        while (iter != null) {
            next = iter.next;
            RandomListNode copy = new RandomListNode(iter.label);
            iter.next = copy;
            copy.next = next;
            iter = next;
        }

        // Second round: assign random pointers for the copy nodes.
        iter = head;
        while (iter != null) {
            if (iter.random != null) {
                iter.next.random = iter.random.next;
            }
            iter = iter.next.next;
        }

        // Third round: restore the original list, and extract the copy list.
        iter = head;
        RandomListNode pseudoHead = new RandomListNode(0);
        RandomListNode copy, copyIter = pseudoHead;

        while (iter != null) {
            next = iter.next.next;
            // extract the copy
            copy = iter.next;
            copyIter.next = copy;
            copyIter = copy;
            // restore the original list
            iter.next = next;
            iter = next;
        }
        return pseudoHead.next;
    }

    // Solution with Map
    public RandomListNode copyRandomList(RandomListNode head) {
        // No node
        if (head == null) return null;

        HashMap<RandomListNode, RandomListNode> map = new HashMap<RandomListNode, RandomListNode>();
        RandomListNode resHead = new RandomListNode(head.label);

        // Copy list with next pointers
        RandomListNode cur = head;
        RandomListNode resCur = resHead;
        map.put(cur, resCur);

        cur = cur.next;
        while (cur != null) {
            resCur.next = new RandomListNode(cur.label);
            map.put(cur, resCur.next);
            cur = cur.next;
            resCur = resCur.next;
        }

        cur = head;
        resCur = resHead;
        while (cur != null) {
            if (cur.random != null) {
                resCur.random = map.get(cur.random);
            } else {
                resCur.random = null;
            }

            cur = cur.next;
            resCur = resCur.next;
        }

        return resHead;
    }
}

public class Solution {
    public boolean wordBreak(String s, Set<String> dict) {
        boolean[] t = new boolean[s.length() + 1];
        t[0] = true; //set first to be true, why?
        //Because we need initial state

        for (int i = 0; i < s.length(); i++) {
            //should continue from match position
            if (!t[i])
                continue;

            for (String a : dict) {
                int len = a.length();
                int end = i + len;
                if (end > s.length())
                    continue;
                if (s.substring(i, end).equals(a)) {
                    t[end] = true;
                }
            }
        }

        return t[s.length()];
    }
}

/**
 * Definition for singly-linked list.
 * class ListNode {
 * int val;
 * ListNode next;
 * ListNode(int x) {
 * val = x;
 * next = null;
 * }
 * }
 */
public class Solution {
    public ListNode detectCycle(ListNode head) {
        ListNode first = head;
        ListNode second = head;

        while (first != null && second != null) {
            first = first.next;
            second = second.next;
            if (second != null)
                second = second = second.next;
            if (first == second)
                break;
        }

        if (second == null)
            return null;

        first = head;
        while (first != second) {
            first = first.next;
            second = second.next;
        }

        return second;
    }
}

/**
 * Definition for singly-linked list.
 * class ListNode {
 * int val;
 * ListNode next;
 * ListNode(int x) {
 * val = x;
 * next = null;
 * }
 * }
 */
public class Solution {
    public void reorderList(ListNode head) {
        if (head == null || head.next == null) return;

        // step 1. cut the list to two halves
        // prev will be the tail of 1st half
        // slow will be the head of 2nd half
        ListNode prev = null, slow = head, fast = head, l1 = head;
        while (fast != null && fast.next != null) {
            prev = slow;
            slow = slow.next;
            fast = fast.next.next;
        }
        prev.next = null;

        // step 2. reverse the 2nd half
        ListNode l2 = reverse(slow);

        // step 3. merge the two halves
        merge(l1, l2);
    }

    private ListNode reverse(ListNode head) {
        ListNode prev = null, curr = head, next = null;
        while (curr != null) {
            next = curr.next;
            curr.next = prev;
            prev = curr;
            curr = next;
        }
        return prev;
    }

    private void merge(ListNode l1, ListNode l2) {
        while (l1 != null) {
            ListNode n1 = l1.next, n2 = l2.next;
            l1.next = l2;
            if (n1 == null) break;

            l2.next = n1;
            l1 = n1;
            l2 = n2;
        }
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 * int val;
 * TreeNode left;
 * TreeNode right;
 * TreeNode(int x) { val = x; }
 * }
 */
public class Solution

        // Recursive Solution
        public List<Integer> preorderTraversal(TreeNode root) {
            List<Integer> re = new ArrayList<Integer>();
            if (root == null) return re;
            re.add(root.val);
            re.addAll(preorderTraversal(root.left));
            re.addAll(preorderTraversal(root.right));
            return re;
        }

        // Iterative Solution
        public List<Integer> preorderTraversal(TreeNode root) {
            List<Integer> res = new ArrayList<>();
            if (root == null) return res;
            Stack<TreeNode> stack = new Stack<>();
            stack.push(root);
            while (!stack.isEmpty()) {
                root = stack.pop();
                res.add(root.val);
                if (root.right != null)
                    stack.push(root.right);
                if (root.left != null)
                    stack.push(root.left);
            }
            return res;
        }

        // Morris Travasal
        public List<Integer> preorderTraversal(TreeNode root) {
            List<Integer> res = new ArrayList<Integer>();
            TreeNode cur = root, prev;
            while (cur != null) {
                if (cur.left == null) {
                    res.add(cur.val);
                    cur = cur.right;
                } else {
                    prev = cur.left;
                    while (prev.right != null && prev.right != cur)
                        prev = prev.right;

                    if (prev.right == null) {
                        // the only difference with inorder-traversal
                        res.add(cur.val);
                        prev.right = cur;
                        cur = cur.left;
                    } else {
                        prev.right = null;
                        cur = cur.right;
                    }
                }
            }
            return res;
        }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 * int val;
 * ListNode next;
 * ListNode(int x) {
 * val = x;
 * next = null;
 * }
 * }
 */
public class Solution {
    public ListNode insertionSortList(ListNode head) {
        if (head == null || head.next == null)
            return head;

        ListNode newHead = new ListNode(head.val);
        ListNode pointer = head.next;
        // loop through each element in the list
        while (pointer != null) {
            // insert this element to the new list

            ListNode innerPointer = newHead;
            ListNode next = pointer.next;

            if (pointer.val <= newHead.val) {
                ListNode oldHead = newHead;
                newHead = pointer;
                newHead.next = oldHead;
            } else {
                while (innerPointer.next != null) {
                    if (pointer.val > innerPointer.val && pointer.val <= innerPointer.next.val) {
                        ListNode oldNext = innerPointer.next;
                        innerPointer.next = pointer;
                        pointer.next = oldNext;
                    }
                    innerPointer = innerPointer.next;
                }

                if (innerPointer.next == null && pointer.val > innerPointer.val) {
                    innerPointer.next = pointer;
                    pointer.next = null;
                }
            }
            // finally
            pointer = next;
        }
        return newHead;
    }
}

/**
 * Definition for singly-linked list.
 * class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode(int x) {
 *         val = x;
 *         next = null;
 *     }
 * }
 */
public class Solution {
    public ListNode sortList(ListNode head) {
        if (head == null || head.next == null)
            return head;

        // count total number of elements
        int count = 0;
        ListNode p = head;
        while (p != null) {
            count++;
            p = p.next;
        }

        // break up to two list
        int middle = count / 2;
        ListNode l = head, r = null;
        ListNode p2 = head;
        int countHalf = 0;
        while (p2 != null) {
            countHalf++;
            ListNode next = p2.next;
            if (countHalf == middle) {
                p2.next = null;
                r = next;
            }
            p2 = next;
        }

        // now we have two parts l and r, recursively sort them
        ListNode h1 = sortList(l);
        ListNode h2 = sortList(r);

        // merge together
        ListNode merged = merge(h1, h2);
        return merged;
    }

    public static ListNode merge(ListNode l, ListNode r) {
        ListNode p1 = l;
        ListNode p2 = r;

        ListNode fakeHead = new ListNode(100);
        ListNode pNew = fakeHead;

        while (p1 != null || p2 != null) {
            if (p1 == null) {
                pNew.next = new ListNode(p2.val);
                p2 = p2.next;
                pNew = pNew.next;
            } else if (p2 == null) {
                pNew.next = new ListNode(p1.val);
                p1 = p1.next;
                pNew = pNew.next;
            } else {
                if (p1.val < p2.val) {
                    // if(fakeHead)
                    pNew.next = new ListNode(p1.val);
                    p1 = p1.next;
                    pNew = pNew.next;
                } else if (p1.val == p2.val) {
                    pNew.next = new ListNode(p1.val);
                    pNew.next.next = new ListNode(p1.val);
                    pNew = pNew.next.next;
                    p1 = p1.next;
                    p2 = p2.next;
                } else {
                    pNew.next = new ListNode(p2.val);
                    p2 = p2.next;
                    pNew = pNew.next;
                }
            }
        }
        // printList(fakeHead.next);
        return fakeHead.next;
    }
}

public class Solution {
    public int evalRPN(String[] tokens) {
        //Start 10:10
        // 1, Push all tokens into stack.
        // 2, Pop one entry each time,
        // 2.1 If it is an operand then push into second stack
        // 2.2 If it is an operator, then pop out two entries from second stack.
        // Evaluate the result and push the result back into the second stack.
        // 3, Stop when the stack is empty.
        // 4, Return the value in the second stack as result.

        Stack<Integer> valueStack = new Stack<Integer>();

        for (String s : tokens) {
            if (s.equals("+") || s.equals("-") || s.equals("*") || s.equals("/")) {
                int firstValue = valueStack.pop();
                int secondValue = valueStack.pop();
                switch (s) {
                    case "+":
                        valueStack.push(firstValue + secondValue);
                        break;
                    case "-":
                        valueStack.push(secondValue - firstValue);
                        break;
                    case "*":
                        valueStack.push(firstValue * secondValue);
                        break;
                    case "/":
                        valueStack.push(secondValue / firstValue);
                        break;
                }
            } else {
                valueStack.push(Integer.parseInt(s));
            }
        }

        return valueStack.pop();
    }
}

public class Solution {

    // Algorithm:
