public class Solution {
    public int maxProduct(int[] A) {
        if (A == null || A.length == 0) return 0;
        if (A.length == 1) return A[0];
        int max_local = A[0];
        int min_local = A[0];
        int global = A[0];
        for (int i = 1; i < A.length; i++) {
            int max_copy = max_local;
            max_local = Math.max(Math.max(A[i] * max_local, A[i]), A[i] * min_local);
            min_local = Math.min(Math.min(A[i] * max_copy, A[i]), A[i] * min_local);
            global = Math.max(global, max_local);
        }
        return global;
    }
}

public class Solution {
    public int findMin(int[] nums) {
        if (nums.length == 0) return 0;
        return getMinFrom(nums, 0, nums.length - 1);
    }

    int getMinFrom(int[] nums, int start, int end) {
        if (nums[start] <= nums[end]) return nums[start];
        if (start == end - 1)
            return (nums[start] < nums[end]) ? nums[start] : nums[end];

        int mid = start + (end - start) / 2;
        if (nums[mid] > nums[start])
            return getMinFrom(nums, mid + 1, end);
        else
            return getMinFrom(nums, start, mid);
    }
}

public class Solution {
    public int findPeakElement(int[] nums) {
        if (nums == null) return -1;
        if (nums.length == 1) return 0;
        int l = 0, r = nums.length - 1;
        while (l <= r) {
            int mid = (l + r) / 2;
            if (mid == 0) {
                if (nums[mid] > nums[mid + 1]) return mid;
                else l = mid + 1;
            } else if (mid == nums.length - 1) {
                if (nums[mid] > nums[mid - 1]) return mid;
                else r = mid - 1;
            } else if (nums[mid] > nums[mid - 1] && nums[mid] > nums[mid + 1]) {
                return mid;
            } else if (nums[mid] <= nums[mid - 1]) {
                r = mid - 1;
            } else { // nums[mid]<=nums[mid+1]
                l = mid + 1;
            }
        }
        return -1;
    }
}

public class Solution {
    public int compareVersion(String version1, String version2) {
        // recursively compare substrings before '.'
        int ver1, ver2;
        ver1 = version1.contains(".") ?
                Integer.parseInt(version1.substring(0, version1.indexOf('.'))) :
                Integer.parseInt(version1);
        ver2 = version2.contains(".") ?
                Integer.parseInt(version2.substring(0, version2.indexOf('.'))) :
                Integer.parseInt(version2);

        if (ver1 > ver2) return 1;
        else if (ver1 < ver2) return -1;

        if (version1.contains(".") && version2.contains("."))
            return compareVersion(version1.substring(version1.indexOf('.') + 1),
                    version2.substring(version2.indexOf('.') + 1));
        else if (version1.contains("."))
            return compareVersion(version1.substring(version1.indexOf('.') + 1), "0");
        else if (version2.contains("."))
            return compareVersion("0", version2.substring(version2.indexOf('.') + 1));
        else return 0;
    }
}

public class Solution {
    public String fractionToDecimal(int numerator, int denominator) {
        StringBuilder res = new StringBuilder();
        long numeratorL = Math.abs((long) numerator);
        long denominatorL = Math.abs((long) denominator);
        // Negative result
        if (numerator > 0 && denominator < 0 || numerator < 0 && denominator > 0)
            res.append("-");
        // Integer part
        long num = numeratorL / denominatorL;
        res.append(num);
        numeratorL = (numeratorL % denominatorL) * 10;
        if (numeratorL > 0) res.append(".");
        Map<Long, Integer> map = new HashMap<Long, Integer>();
        // Decimal part
        while (numeratorL > 0) {
            num = numeratorL / denominatorL;
            if (map.containsKey(numeratorL)) {
                res.insert(map.get(numeratorL).intValue(), "(");
                res.append(")");
                break;
            }
            map.put(numeratorL, res.length());
            numeratorL = (numeratorL % denominatorL) * 10;
            res.append(num);
        }
        return res.toString();
    }
}

/**
 * Definition for binary tree
 * public class TreeNode {
 * int val;
 * TreeNode left;
 * TreeNode right;
 * TreeNode(int x) { val = x; }
 * }
 */

public class BSTIterator {
    Stack<TreeNode> stack;

    public BSTIterator(TreeNode root) {
        stack = new Stack<TreeNode>();
        while (root != null) {
            stack.push(root);
            root = root.left;
        }
    }

    /**
     * @return whether we have a next smallest number
     */
    public boolean hasNext() {
        return !stack.isEmpty();
    }

    /**
     * @return the next smallest number
     */
    public int next() {
        TreeNode node = stack.pop();
        int result = node.val;
        if (node.right != null) {
            node = node.right;
            while (node != null) {
                stack.push(node);
                node = node.left;
            }
        }
        return result;
    }
}

