/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public List<Integer> rightSideView(TreeNode root) {
        List<Integer> res = new ArrayList<Integer>();
        if(root == null) return res;
        Deque<TreeNode> cur = new ArrayDeque<TreeNode>();

        cur.add(root);
        while(!cur.isEmpty()){
        	res.add(cur.getLast().val);
        	Deque<TreeNode> next = new ArrayDeque<TreeNode>();
        	while(!cur.isEmpty()){
        		TreeNode temp = cur.pop();
        		if(temp.left != null)
        			next.add(temp.left);
        		if(temp.right != null)
        			next.add(temp.right);
        	}
        	cur = next;
        }

        return res;
    }
}

public class Solution {
    class Point{
        int row;
        int col;
        public Point(int x, int y){
            this.row = x;
            this.col = y;
        }
    }

    public int numIslands(char[][] grid) {
        if(grid == null || grid.length == 0 || grid[0].length == 0) return 0;
        int rowNum = grid.length;
        int colNum = grid[0].length;

        int res = 0;
        for(int row = 0; row < rowNum; row++){
            for(int col = 0; col < colNum; col++){
                if(grid[row][col] != '1')
                    continue;
                res++;
                Deque<Point> queue = new ArrayDeque<Point>();
                grid[row][col] = 'x';
                queue.offerFirst(new Point(row, col));
                while(!queue.isEmpty()){
                    Point top = queue.pollLast();
                    // go top, down, left, right
                    if(top.row - 1 >= 0 && grid[top.row - 1][top.col] == '1'){
                        grid[top.row - 1][top.col] = 'x';
                        queue.offerFirst(new Point(top.row - 1, top.col));
                    }
                    if(top.row + 1 < rowNum && grid[top.row + 1][top.col] == '1'){
                        grid[top.row + 1][top.col] = 'x';
                        queue.offerFirst(new Point(top.row + 1, top.col));
                    }
                    if(top.col - 1 >= 0 && grid[top.row][top.col - 1] == '1'){
                        grid[top.row][top.col - 1] = 'x';
                        queue.offerFirst(new Point(top.row, top.col - 1));
                    }
                    if(top.col + 1 < colNum && grid[top.row][top.col + 1] == '1'){
                        grid[top.row][top.col + 1] = 'x';
                        queue.offerFirst(new Point(top.row, top.col + 1));
                    }
                }
            }
        }
        return res;
    }
}

public class Solution {
    // The idea is to use a mask to find the leftmost common digits of m and n.
    // Example: m=1110001, n=1110111, and you just need to find 1110000 and it will be the answer.
    public int rangeBitwiseAnd(int m, int n) {
        int r = Integer.MAX_VALUE;
        while((m & r) != (n & r))
            r = r << 1;
        return n & r;
    }
}

public class Solution {
    public boolean canFinish(int numCourses, int[][] prerequisites) {
        ArrayList[] graph = new ArrayList[numCourses];
        int[] indegree = new int[numCourses];
        // 
        for(int i = 0; i < numCourses; i++){
            graph[i] = new ArrayList<Integer>();
        }
        // 
        for(int i = 0; i < prerequisites.length; i++){
            graph[prerequisites[i][1]].add(prerequisites[i][0]);
            indegree[prerequisites[i][0]]++;
        }
        // 
        Queue<Integer> queue = new LinkedList<Integer>();
        for(int i = 0; i < indegree.length; i++){
            if(indegree[i] == 0){
                queue.add(i);
            }
        }
        // 
        int cnt = 0;
        while(!queue.isEmpty()){
            Integer curr = queue.poll();
            cnt++;
            ArrayList<Integer> nexts = graph[curr];
            for(int i = 0; i < nexts.size(); i++){
                int next = nexts.get(i);
                indegree[next]--;
                if(indegree[next] == 0){
                    queue.offer(next);
                }
            }
        }
        return cnt == numCourses;
    }
}

public class Trie {

    class TrieNode {
        String word = "";
        Map<Character, TrieNode> children = null;
        public TrieNode(){
            this.children = new HashMap<>();
        }
    }

    TrieNode root;

    /** Initialize your data structure here. */
    public Trie() {
        this.root = new TrieNode();
    }

    /** Inserts a word into the trie. */
    public void insert(String word) {
        TrieNode cur = root;
        for(int i = 0; i < word.length(); i++){
            char c = word.charAt(i);
            if(!cur.children.containsKey(c)){
                cur.children.put(c, new TrieNode());
            }
            cur = cur.children.get(c);
        }
        cur.word = word;
    }

    /** Returns if the word is in the trie. */
    public boolean search(String word) {
        TrieNode cur = root;
        for(int i = 0; i < word.length(); i++){
            char c = word.charAt(i);
            if(cur.children.containsKey(c)){
                cur = cur.children.get(c);
            } else {
                return false;
            }
        }
        return cur.word.equals(word);
    }

    /** Returns if there is any word in the trie that starts with the given prefix. */
    public boolean startsWith(String prefix) {
        TrieNode cur = root;
        for(int i = 0; i < prefix.length(); i++){
            char c = prefix.charAt(i);
            if(cur.children.containsKey(c)){
                cur = cur.children.get(c);
            } else {
                return false;
            }
        }
        return true;
    }
}

