public class Solution {
    public int minSubArrayLen(int s, int[] nums) {
        if(nums.length == 0) return 0;
        if(nums[0] >= s) return 1;

        int index = 1, curSum = nums[0], startIndex = 0, minLen = 0;
        while(index < nums.length){
            if(nums[index] >= s) return 1;
            curSum += nums[index];
            if(curSum >= s){
                while(startIndex < index && curSum - nums[startIndex] >= s){
                    curSum -= nums[startIndex++];
                }
                if(minLen == 0 || index-startIndex < minLen)
                    minLen = index-startIndex+1;
            }
            index++;
        }
        return minLen;
    }
}

public class Solution {
    public int[] findOrder(int numCourses, int[][] prerequisites) {
        int[] res = new int[numCourses];
        ArrayList<Integer>[] graph = new ArrayList[numCourses];
        int[] indegree = new int[numCourses];
        for(int i = 0; i < prerequisites.length; i++){
            if(graph[prerequisites[i][1]] == null){
                graph[prerequisites[i][1]] = new ArrayList<Integer>();
            }
            graph[prerequisites[i][1]].add(prerequisites[i][0]);
            indegree[prerequisites[i][0]]++;
        }
        Queue<Integer> queue = new LinkedList<Integer>();
        for(int i = 0; i < indegree.length; i++){
            if(indegree[i] == 0){
                queue.add(i);
            }
        }
        // idx
        int idx = 0;
        while(!queue.isEmpty()){
            Integer curr = queue.poll();
            res[idx++] = curr;
            if(graph[curr] == null) continue;
            for(Integer next : graph[curr]){
                if(--indegree[next] == 0){
                    queue.offer(next);
                }
            }
        }
        // 
        return idx != numCourses ? new int[0] : res;
    }
}

public class WordDictionary {
    public class TrieNode {
        public TrieNode[] children = new TrieNode[26];
        public String item = "";
    }

    private TrieNode root = new TrieNode();

    public void addWord(String word) {
        TrieNode node = root;
        for (char c : word.toCharArray()) {
            if (node.children[c - 'a'] == null) {
                node.children[c - 'a'] = new TrieNode();
            }
            node = node.children[c - 'a'];
        }
        node.item = word;
    }

    public boolean search(String word) {
        return match(word.toCharArray(), 0, root);
    }

    private boolean match(char[] chs, int k, TrieNode node) {
        if (k == chs.length)
            return !node.item.equals("");

        if (chs[k] != '.') {
            return node.children[chs[k] - 'a'] != null &&
                    match(chs, k + 1, node.children[chs[k] - 'a']);
        }

        for (int i = 0; i < node.children.length; i++) {
            if (node.children[i] == null) continue;
            if (match(chs, k + 1, node.children[i])) {
                return true;
            }
        }
        return false;
    }
}

public class Solution {
    public int rob(int[] nums) {
        if (nums == null || nums.length == 0)
			return 0;
		int n = nums.length;
		if (n == 1)
			return nums[0];
		if (n == 2)
			return Math.max(nums[1], nums[0]);

		// include 1st element, and not last element
		int[] dp = new int[n + 1];
		dp[0] = 0;
		dp[1] = nums[0];
		for (int i = 2; i < n; i++) {
			dp[i] = Math.max(dp[i - 1], dp[i - 2] + nums[i - 1]);
		}

		// not include frist element, and include last element
		int[] dr = new int[n + 1];
		dr[0] = 0;
		dr[1] = nums[1];
		for (int i = 2; i < n; i++) {
			dr[i] = Math.max(dr[i - 1], dr[i - 2] + nums[i]);
		}
		return Math.max(dp[n - 1], dr[n - 1]);
    }
}

public class Solution {
    // Time complexity = O(n)
    // Discard half each time: n+(n/2)+(n/4)..1 = n + (n-1) = O(2n-1) = O(n),
    // because n/2+n/4+n/8+..1=n-1.
    public int findKthLargest(int[] nums, int k) {
		if (nums == null || nums.length == 0)
			return Integer.MAX_VALUE;
		return findKthLargest(nums, 0, nums.length - 1, nums.length - k);
	}

	// quick select: kth smallest
	private int findKthLargest(int[] nums, int start, int end, int k) {
		if (start > end)
			return Integer.MAX_VALUE;

		int pivot = nums[end];// Take A[end] as the pivot,
		int left = start;
		for (int i = start; i < end; i++) {
			if (nums[i] <= pivot) // Put numbers < pivot to pivot's left
				swap(nums, left++, i);
		}
		swap(nums, left, end);// Finally, swap A[end] with A[left]

		if (left == k)// Found kth smallest number
			return nums[left];
		else if (left < k)// Check right part
			return findKthLargest(nums, left + 1, end, k);
		else // Check left part
			return findKthLargest(nums, start, left - 1, k);
	}

	private void swap(int[] A, int i, int j) {
		int tmp = A[i];
		A[i] = A[j];
		A[j] = tmp;
	}
}

public class Solution {
    public List<List<Integer>> combinationSum3(int k, int n) {
        List<List<Integer>> res = new ArrayList<>();
        List<Integer> subComb = new ArrayList<>();
        findComb(1, k, n, subComb, res);

        return res;
    }

    private void findComb(int start, int k, int curSum,
                          List<Integer> subComb, List<List<Integer>> res){
        if(subComb.size() == k && curSum == 0){
            List<Integer> temp = new ArrayList<>(subComb);
            res.add(temp);
        }

        for(int i=start; i<=9; i++){
            if(curSum - i < 0) return;
            subComb.add(i);
            findComb(i+1, k, curSum-i, subComb, res);
            subComb.remove(subComb.size()-1);
        }
    }
}

