public class Solution {
    public boolean containsNearbyAlmostDuplicate(int[] nums, int k, int t) {
        if (k < 1 || t < 0)  return false;
	    TreeSet<Integer> set = new TreeSet<Integer>();

	    for (int i = 0; i < nums.length; i++) {
		    int c = nums[i];
		    if ((set.floor(c) != null && c <= set.floor(c) + t)
		        || (set.ceiling(c) != null && c >= set.ceiling(c) -t))
		        return true;
		    set.add(c);
		    if (i >= k)
			    set.remove(nums[i - k]);
	    }
	    return false;
    }
}

public class Solution {
    public int maximalSquare(char[][] matrix) {
        if (matrix == null || matrix.length == 0 || matrix[0].length == 0)
		    return 0;

	    int m = matrix.length;
	    int n = matrix[0].length;
	    int[][] t = new int[m][n];

    	//top row
	    for (int i = 0; i < m; i++) {
		    t[i][0] = Character.getNumericValue(matrix[i][0]);
	    }
	    //left column
	    for (int j = 0; j < n; j++) {
		    t[0][j] = Character.getNumericValue(matrix[0][j]);
	    }
    	//cells inside
	    for (int i = 1; i < m; i++) {
		    for (int j = 1; j < n; j++) {
			    if (matrix[i][j] == '1') {
				    int min = Math.min(t[i - 1][j], t[i - 1][j - 1]);
				    min = Math.min(min,t[i][j - 1]);
				    t[i][j] = min + 1;
			    } else {
				    t[i][j] = 0;
			    }
		    }
	    }

	    int max = 0;
	    //get maximal length
	    for (int i = 0; i < m; i++) {
		    for (int j = 0; j < n; j++) {
			    if (t[i][j] > max) {
				    max = t[i][j];
			    }
		    }
	    }
	    return max * max;
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public int countNodes(TreeNode root) {
        if(root == null)
            return 0;

        int left = getLeftHeight(root) + 1;
        int right = getRightHeight(root) + 1;

        if(left == right){
            return (2 << (left-1)) - 1;
        }
        return countNodes(root.left) + countNodes(root.right) + 1;
    }

    private int getLeftHeight(TreeNode n){
        if(n == null) return 0;
        int height=0;
        while(n.left!=null){
            height++;
            n = n.left;
        }
        return height;
    }

    private int getRightHeight(TreeNode n){
        if(n == null) return 0;
        int height=0;
        while(n.right!=null){
            height++;
            n = n.right;
        }
        return height;
    }

    // Better Solution
    public int countNodes(TreeNode root) {
        if (root==null) return 0;
        if (root.left==null) return 1;
        int height = 0;
        int nodesSum = 0;
        TreeNode curr = root;
        while(curr.left!=null) {
            nodesSum += (1<<height);
            height++;
            curr = curr.left;
        }
        return nodesSum + countLastLevel(root, height);
    }

    private int countLastLevel(TreeNode root, int height) {
        if(height == 1){
            if (root.right!=null) return 2;
            if (root.left!=null) return 1;
            return 0;
        }
        TreeNode midNode = root.left;
        int currHeight = 1;
        while(currHeight<height) {
            currHeight++;
            midNode = midNode.right;
        }
        if (midNode==null)
            return countLastLevel(root.left, height-1);
        return (1<<(height-1)) + countLastLevel(root.right, height-1);
    }
}

public class Solution {
    public int computeArea(int A, int B, int C, int D, int E, int F, int G, int H) {
        if(C < E || G < A )
            return (G-E)*(H-F) + (C-A)*(D-B);
         if(D < F || H < B)
            return (G-E)*(H-F) + (C-A)*(D-B);

        int right = Math.min(C,G);
        int left = Math.max(A,E);
        int top = Math.min(H,D);
        int bottom = Math.max(F,B);

        return (G-E)*(H-F) + (C-A)*(D-B) - (right-left)*(top-bottom);
    }
}

public class Solution {
    public int calculate(String s) {
        s = s.trim();
		if (s.length() == 0) return 0;
		int curIndex = 0, total = 0, curNum = 0, sign = 1;
		if (s.charAt(0) == '+' || s.charAt(0) == '-')
			sign = s.charAt(0) == '+' ? 1 : -1;

		while(curIndex < s.length()){
			// 1. if ' '
			if(s.charAt(curIndex) == ' '){
				curIndex++;
				continue;
			}
			// 2. if current char is digit
			if(s.charAt(curIndex) - '0' >= 0 && s.charAt(curIndex) - '0' <= 9){
				curNum = curNum*10 + s.charAt(curIndex) - '0';
			}
			// 3. if +/-
			if(s.charAt(curIndex) == '+' || s.charAt(curIndex) == '-'){
				total += sign*curNum;
				curNum = 0;
				sign = s.charAt(curIndex) == '+' ? 1 : -1;
			}
			// 4. if * or /
			if(s.charAt(curIndex) == '*' || s.charAt(curIndex) == '/'){
				// Find next number
				int nextNum = 0, multipler = s.charAt(curIndex++);
				while(curIndex < s.length() && (s.charAt(curIndex) == ' ' ||
						(s.charAt(curIndex) - '0' >= 0 && s.charAt(curIndex) - '0' <= 9))){
					if (s.charAt(curIndex) == ' '){
						curIndex++;
						continue;
					}
					nextNum = nextNum*10 + s.charAt(curIndex++) - '0';
				}
				curNum = (multipler == '*') ? curNum*nextNum : curNum/nextNum;
				curIndex--;
			}
			curIndex++;
		}
		return total += sign*curNum;
    }
}

public class Solution {
    public List<String> summaryRanges(int[] nums) {
        List<String> res = new ArrayList<String>();
        if(nums.length == 0) return res;
        if(nums.length == 1){
        	res.add(String.valueOf(nums[0]));
        	return res;
        }

        List<Integer> continueNums = new ArrayList<Integer>();
        continueNums.add(nums[0]);
        for(int i=1; i<nums.length; i++){
            if(nums[i-1]+1 == nums[i])
            	continueNums.add(nums[i]);
            else{
            	if(continueNums.size()==1)
            		res.add(continueNums.get(0).toString());
            	else{
            		res.add(continueNums.get(0) + "->" +
            		    continueNums.get(continueNums.size()-1));
            	}
            	continueNums = new ArrayList<Integer>();
        		continueNums.add(nums[i]);
            }
        }

        if(continueNums.size()==1)
    		res.add(continueNums.get(0).toString());
    	else{
    		res.add(continueNums.get(0) + "->" +
    		    continueNums.get(continueNums.size()-1));
    	}
        return res;
    }
}

public class Solution {
    public List<Integer> majorityElement(int[] nums) {
        if (nums == null || nums.length == 0)
		return new ArrayList<Integer>();
	    List<Integer> result = new ArrayList<Integer>();
	    int number1 = nums[0], number2 = nums[0];
	    int count1 = 0, count2 = 0, len = nums.length;
	    for (int i = 0; i < len; i++) {
		    if (nums[i] == number1)
			    count1++;
		    else if (nums[i] == number2)
			    count2++;
		    else if (count1 == 0) {
			    number1 = nums[i];
			    count1 = 1;
		    } else if (count2 == 0) {
			    number2 = nums[i];
			    count2 = 1;
		    } else {
			    count1--;
			    count2--;
		    }
	    }
	    count1 = 0;
	    count2 = 0;
	    for (int i = 0; i < len; i++) {
		    if (nums[i] == number1)
			    count1++;
		    else if (nums[i] == number2)
			    count2++;
	    }
	    if (count1 > len / 3)
		    result.add(number1);
	    if (count2 > len / 3)
		    result.add(number2);
	    return result;
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public int kthSmallest(TreeNode root, int k) {
        // if(root == null || k < 1) return -1;
        int count = countNodes(root.left);
        if (k <= count) {
            return kthSmallest(root.left, k);
        } else if (k > count + 1) { // 1 is counted as current node
            return kthSmallest(root.right, k-1-count);
        }
        return root.val;
    }

    public int countNodes(TreeNode root){
        if(root == null) return 0;
        return 1 + countNodes(root.left) + countNodes(root.right);
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    //To calculate time complexity,
    //  f(n)=2*f(n-1)=2*2*f(n-2)=2^(logn), so time=O(n).
    public TreeNode lowestCommonAncestor(TreeNode root, TreeNode p, TreeNode q) {
        if (root == null || root == p || root == q) return root;
        TreeNode left = lowestCommonAncestor(root.left, p, q);
        TreeNode right = lowestCommonAncestor(root.right, p, q);

        if(left == null)
            return right;
        if(right == null)
            return left;
        return root;
    }
}

public class Solution {
    public int[] productExceptSelf(int[] nums) {
        int[] result = new int[nums.length];

        result[0] = nums[0];
        for(int i=1; i<nums.length; i++)
            result[i] = result[i-1]*nums[i];

        for(int i=nums.length-2; i>-1; i--)
            nums[i] = nums[i+1]*nums[i];

        result[nums.length-1] = result[nums.length-2];
        for(int i=nums.length-2; i>0; i--){
            result[i] = result[i-1]*nums[i+1];
        }
        result[0] = nums[1];
        return result;
    }
}

public class Solution {
    /*
     * We start search the matrix from top right corner, 
     * initialize the current position to top right corner, 
     * if the target is greater than the value in current position, 
     * then the target can not be in entire row of current position because 
     * the row is sorted, if the target is less than the value in current position, 
     * then the target can not in the entire column because the column is sorted too. 
     * We can rule out one row or one column each time, 
     * so the time complexity is O(m+n).
     *
    */
    public boolean searchMatrix(int[][] matrix, int target) {
        if(matrix == null || matrix.length < 1 || matrix[0].length <1) {
            return false;
        }
        int col = matrix[0].length-1;
        int row = 0;
        while(col >= 0 && row <= matrix.length-1) {
            if(target == matrix[row][col]) {
                return true;
            } else if(target < matrix[row][col]) {
                col--;
            } else if(target > matrix[row][col]) {
                row++;
            }
        }
        return false;
    }
}

public class Solution {
    public List<Integer> diffWaysToCompute(String input) {
        List<Integer> res = new ArrayList<Integer>();
        for(int i=0; i<input.length(); i++){
            if(!Character.isDigit(input.charAt(i))){
                String part1 = input.substring(0, i);
                String part2 = input.substring(i+1);
                List<Integer> part1Vals = diffWaysToCompute(part1);
                List<Integer> part2Vals = diffWaysToCompute(part2);
                for(int j = 0; j < part1Vals.size(); j++){
                    for(int k = 0; k < part2Vals.size(); k++){
                        switch(input.charAt(i)){
                            case '-':
                                res.add(part1Vals.get(j) - part2Vals.get(k));
                                break;
                            case '+':
                                res.add(part1Vals.get(j) + part2Vals.get(k));
                                break;
                            case '*':
                                res.add(part1Vals.get(j) * part2Vals.get(k));
                                break;
                        }
                    }
                }
            } 
        }
        if(res.size() == 0){
            res.add(Integer.parseInt(input));
        }
        return res;
    }
}

public class Solution {
    public List<String> findStrobogrammatic(int n) {
        List<String> res = new ArrayList<>();
        if(n == 0) return res;
        if(n % 2 == 1){
            res.add("0");
            res.add("1");
            res.add("8");
        }
        for(int i = (n % 2) + 2; i <= n; i++){
            List<String> temp = new ArrayList<>();
            for(String s : res){
                temp.add("1" + s + "1");
                temp.add("6" + s + "9");
                temp.add("9" + s + "6");
                temp.add("8" + s + "8");
            }
            res = temp;
        }
        return res;
    }
}

public class Solution {
    public int minMeetingRooms(Interval[] intervals) {
        if (intervals == null || intervals.length == 0)
            return 0;

        Arrays.sort(intervals, new Comparator<Interval>() {
            public int compare(Interval i1, Interval i2) {
                return i1.start - i2.start;
            }
        });

        PriorityQueue<Integer> queue = new PriorityQueue<>();
        int count = 1;
        queue.offer(intervals[0].end);

        for (int i = 1; i < intervals.length; i++) {
            if (intervals[i].start < queue.peek()) {
                count++;
            } else {
                queue.poll();
            }
            queue.offer(intervals[i].end);
        }
        return count;
    }
}

public class Solution {
    public int[] singleNumber(int[] nums) {
        int A = 0;
        int B = 0;
        int AXORB = 0;
        for(int i = 0; i<nums.length; i++){
            AXORB ^= nums[i];
        }

        // AXORB = (AXORB & (AXORB - 1)) ^ AXORB; //find the different bit
        int lowBit = AXORB & -AXORB;
        for(int i = 0; i<nums.length; i++){
            if((lowBit & nums[i]) == 0)
                A ^= nums[i];
            else
                B ^= nums[i];
        }
        return new int[]{A, B};
    }
}

public class Solution {
    public int nthUglyNumber(int n) {
        if(n == 0 || n == 1) return n;

        int h = 1;
        Deque<Integer> d2 = new ArrayDeque<Integer>();
        Deque<Integer> d3 = new ArrayDeque<Integer>();
        Deque<Integer> d5 = new ArrayDeque<Integer>();

        for(int i=0; i<n-1; i++){
            d2.offer(2 * h);
            d3.offer(3 * h);
            d5.offer(5 * h);
            h = Math.min(Math.min(d2.peek(), d3.peek()), d5.peek());
            if(h == d2.peek())
                d2.poll();
            if(h == d3.peek())
                d3.poll();
            if(h == d5.peek())
                d5.poll();
        }
        return h;
    }
}

public class Solution {
    public int hIndex(int[] citations) {
        if (citations.length == 0)
			return 0;
		if (citations.length == 1)
			return citations[0] > 0 ? 1 : 0;

		// Map for citations numbers with paper count
		Map<Integer, Integer> map = new HashMap<Integer, Integer>();
		int currentHIdex = 0, resHIndex;
		for (int c : citations) {
			if (c == 0)
				continue;
			if (map.containsKey(c))
				map.put(c, map.get(c) + 1);
			else
				map.put(c, 1);
		}

		if (map.size() == 0)
			return 0;
		int[] uniqueCitations = new int[map.size()];
		int index = 0;
		for (int i : map.keySet()) {
			uniqueCitations[index++] = i;
		}
		Arrays.sort(uniqueCitations);

		if (uniqueCitations[uniqueCitations.length - 1] <= 
		    map.get(uniqueCitations[uniqueCitations.length - 1]))
			return uniqueCitations[uniqueCitations.length - 1];
		resHIndex = currentHIdex = map.get(uniqueCitations[uniqueCitations.length - 1]);
		for (int i = uniqueCitations.length - 2; i >= 0; i--) {
			map.put(uniqueCitations[i], 
			    map.get(uniqueCitations[i]) + map.get(uniqueCitations[i + 1]));
			currentHIdex = uniqueCitations[i] <= map.get(uniqueCitations[i]) ? 
			    uniqueCitations[i] : map.get(uniqueCitations[i]);
			resHIndex = currentHIdex > resHIndex ? currentHIdex : resHIndex;
		}
		return resHIndex;
    }
}

public class Solution {
    public int hIndex(int[] citations) {
        int res = 0, len = citations.length;
		if(len == 0 || citations[len-1] == 0) return res;
		if(len == 1) return 1;

		int start = 0, end = len-1, mid = (end-start)/2 + start;				
		while(start <= end){
			if(citations[mid] == len - mid)	return citations[mid];

			if(citations[mid] < len - mid)
				start = mid+1;
			else
				end = mid-1;
			mid = (end-start)/2 + start;
		}				
		return len - end -1;
    }
}

public class Solution {
    public int numSquares(int n) {
        if(n <= 1) return 1;
        int[] dpArray = new int[n+1];
        dpArray[1] = 1;
        for(int i=2; i<n+1; i++){
            int sqareRoot = (int)Math.sqrt(i);
            if(sqareRoot*sqareRoot == i){
                dpArray[i] = 1;
                continue;
            }

            int minNumSquares = Integer.MAX_VALUE;
            int scanTo = (i % 2) == 0 ? i/2 : i/2 +1;
            for(int j=1; j<scanTo+1; j++){
                minNumSquares = (dpArray[i-j] + dpArray[j]) < minNumSquares ? 
                    (dpArray[i-j] + dpArray[j]) : minNumSquares;
            }
            dpArray[i] = minNumSquares;
        }
        return dpArray[n];
    }
}

public class Solution {
    // Solution 1: sort first 
    // Time Complexity: O(NlogN)
    // Space Complexity: O(1)
    public void wiggleSort(int[] nums) {
        // Sort first
        Arrays.sort(nums);
        // Swap in pair
        for(int i = 2; i < nums.length; i+=2){
            int tmp = nums[i-1];
            nums[i-1] = nums[i];
            nums[i] = tmp;
        }
    }

    // Solution 2: Swap if any of following conditions are true:
    // 1. Odd index and nums[i] < nums[i - 1]
    // 2. Even index and nums[i] > nums[i - 1]
    // Time: O(N); Space: O(1)
    public void wiggleSort(int[] nums) {
        for(int i = 1; i < nums.length; i++){
            if((i % 2 == 1 && nums[i] < nums[i-1]) || 
                (i % 2 == 0 && nums[i] > nums[i-1])){
                int tmp = nums[i-1];
                nums[i-1] = nums[i];
                nums[i] = tmp;
            }
        }
    }
}

// Java Iterator interface reference:
// https://docs.oracle.com/javase/8/docs/api/java/util/Iterator.html
class PeekingIterator implements Iterator<Integer> {

    private final Iterator<Integer> iterator;
    private boolean hasPeeked;
    private Integer peekedElement;

	public PeekingIterator(Iterator<Integer> iterator) {
	    // initialize any member here.
	    this.iterator = iterator;
	}

    // Returns the next element in the iteration without advancing the iterator.
	public Integer peek() {
        if (!hasPeeked) {
            peekedElement = iterator.next();
            hasPeeked = true;
        }
        return peekedElement;
	}

	// hasNext() and next() should behave the same as in the Iterator interface.
	// Override them if needed.
	@Override
	public Integer next() {
	    if (!hasPeeked) {
            return iterator.next();
        }
        Integer result = peekedElement;
        hasPeeked = false;
        peekedElement = null;
        return result;
	}

	@Override
	public boolean hasNext() {
	    return hasPeeked || iterator.hasNext();
	}
}

public class Solution {
    class TreeNode {
        int val;
        TreeNode left;
        TreeNode right;
    }

    /**
     * Time is O(log(n)) and space is O(1).
     * @param root
     * @param p
     * @return
     */
    public TreeNode inorderSuccessor(TreeNode root, TreeNode p) {
        if (root == null)
            return null;

        // Try to find the node p and track node larger than it
        TreeNode next = null;
        TreeNode c = root;
        while (c != null && c.val != p.val) {
            if (c.val > p.val) {
                next = c;
                c = c.left;
            } else {
                c = c.right;
            }
        }

        // Node not found
        if (c == null)
            return null;
        // Node don't has right child
        if (c.right == null)
            return next;
        // Return left most node of right child
        c = c.right;
        while (c.left != null)
            c = c.left;

        return c;
    }
}

public class Solution {
    // https://segmentfault.com/a/1190000003817671
    public int findDuplicate(int[] nums) {
        int slow = 0;
        int fast = 0;
        // Find where the slow pointer meet fast pointer
        do{
            slow = nums[slow];
            fast = nums[nums[fast]];
        } while(slow != fast);

        // Use a new pointer to start from begining until
        // it meet with the slow pointer
        int find = 0;
        while(find != slow){
            slow = nums[slow];
            find = nums[find];
        }
        return find;
    }
}

public class Solution {
    public void gameOfLife(int[][] board) {
		if (board == null || board.length == 0)
			return;
		int m = board.length, n = board[0].length;

		for (int i = 0; i < m; i++) {
			for (int j = 0; j < n; j++) {
				int lives = liveNeighbors(board, m, n, i, j);

				// In the beginning, every 2nd bit is 0;
				// So we only need to care about when the 2nd bit will become 1.
				if (board[i][j] == 1 && lives >= 2 && lives <= 3) {
					board[i][j] = 3; // Make the 2nd bit 1: 01 ---> 11
				}
				if (board[i][j] == 0 && lives == 3) {
					board[i][j] = 2; // Make the 2nd bit 1: 00 ---> 10
				}
			}
		}

		for (int i = 0; i < m; i++) {
			for (int j = 0; j < n; j++) {
				board[i][j] >>= 1; // Get the 2nd state.
			}
		}
	}

	public int liveNeighbors(int[][] board, int m, int n, int i, int j) {
		int lives = 0;
		for (int x = Math.max(i - 1, 0); x <= Math.min(i + 1, m - 1); x++) {
			for (int y = Math.max(j - 1, 0); y <= Math.min(j + 1, n - 1); y++) {
				lives += board[x][y] & 1;
			}
		}
		lives -= board[i][j] & 1;
		return lives;
	}
}

public class Solution {
    public String getHint(String secret, String guess) {
        int bull, cow;
        bull = cow = 0;

        //store the digits of secrete
        int[] counter_secret = new int[10]; 
        //store the digits of guess
        int[] counter_guess = new int[10]; 

        int L = secret.length();
        for(int i=0; i<L; i++) {
            counter_secret[secret.charAt(i)-'0']++;
            // if meets a bull, secrete counter should not change
            if(secret.charAt(i)==guess.charAt(i)) {
                bull++;
                counter_secret[secret.charAt(i)-'0']--;
            } else {
                counter_guess[guess.charAt(i)-'0']++;
            }
        }

        // check the two arrays item by item and sum up the cows
        for(int i = 0; i <= 9; i++) {
            cow += counter_secret[i] >= counter_guess[i] ? 
            counter_guess[i]:counter_secret[i];
        } 
        return bull + "A" + cow + "B";
    }
}

public class Solution {
    public int lengthOfLIS(int[] nums) {
        if(nums==null || nums.length==0) return 0;
        List<Integer> list = new ArrayList<>();

        for(int num : nums){
            // if(list.size() == 0 or num > last element in list)
            if(list.size() == 0 || num > list.get(list.size()-1)){
                list.add(num);
                continue;
            }
            // replace element which is the smallest but bigger than num
            int i = 0, j = list.size() - 1;
            while(i < j){
                int mid = i + (j - i)/2;
                if(list.get(mid) < num){
                    i = mid + 1;
                } else {
                    j = mid;
                }
            }
            list.set(j, num);
        }
        return list.size();
    }
}

public class NumMatrix {
    private int[][] matrix;
    private int[][] sum;

    public NumMatrix(int[][] matrix) {
        this.matrix = matrix;

        if (matrix == null || matrix.length == 0) {
            return;
        }

        int m = matrix.length;
        int n = matrix[0].length;

        sum = new int[m + 1][n + 1];

        for (int i = 1; i <= m; i++) {
            for (int j = 1; j <= n; j++) {
                sum[i][j] = sum[i - 1][j] + sum[i][j - 1] - 
                            sum[i - 1][j - 1] + matrix[i - 1][j - 1];
            }
        }
    }

    public int sumRegion(int row1, int col1, int row2, int col2) {
        return sum[row2 + 1][col2 + 1] - sum[row2 + 1][col1] - 
               sum[row1][col2 + 1] + sum[row1][col1];
    }
}


