public class Solution {
    public int maxProfit(int[] prices) {
        if(prices.length <= 1) return 0;
		int[] buy = new int[prices.length];
		int[] sell = new int[prices.length];

		buy[0] = -prices[0];
		buy[1] = prices[1] > prices[0] ? - prices[0] : - prices[1];
		sell[0] = 0;
		sell[1] = prices[1] > prices[0] ? (prices[1] - prices[0]) : 0;
		for(int i = 2; i < prices.length; i++){
			buy[i] = Math.max(sell[i-2]-prices[i], buy[i-1]);
			sell[i] = Math.max(buy[i-1]+prices[i], sell[i-1]);
		}
		return sell[prices.length - 1];
    }
}

public class Solution {
    public List<Integer> findMinHeightTrees(int n, int[][] edges) {
        List<Integer> result = new ArrayList<>();
        if (n <= 0) {
            return result;
        }

        // Corner case: there is a single node and no edge at all
        if (n == 1 && edges.length == 0) {
            result.add(0);
            return result;
        }

        // Step 1: construct the graph
        List<Set<Integer>> adjList = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            adjList.add(new HashSet<>());
        }

        for (int[] edge : edges) {
            int from = edge[0];
            int to = edge[1];
            adjList.get(from).add(to);
            adjList.get(to).add(from);
        }

        // Remove leaf nodes
        List<Integer> leaves = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            if (adjList.get(i).size() == 1) {
                leaves.add(i);
            }
        }

        while (n > 2) {
            // identify and remove all leaf nodes
            n -= leaves.size();
            List<Integer> newLeaves = new ArrayList<>();
            for (int leaf : leaves) {
                int neighbor = adjList.get(leaf).iterator().next();
                adjList.get(neighbor).remove(leaf);

                if (adjList.get(neighbor).size() == 1) {
                    newLeaves.add(neighbor);
                }
            }

            leaves = newLeaves;
        }

        return leaves;
    }
}

public class Solution {
    /***
     * A sparse matrix can be represented as a sequence of rows,
     * each of which is a sequence of (column-number, value)
     * pairs of the nonzero values in the row.
     */
    public int[][] multiply(int[][] A, int[][] B) {
        int m = A.length, n = A[0].length, nB = B[0].length;
        int[][] result = new int[m][nB];

        List[] indexA = new List[m];
        for (int i = 0; i < m; i++) {
            List<Integer> numsA = new ArrayList<>();
            for (int j = 0; j < n; j++) {
                if (A[i][j] != 0) {
                    numsA.add(j);
                    numsA.add(A[i][j]);
                }
            }
            indexA[i] = numsA;
        }

        for (int i = 0; i < m; i++) {
            List<Integer> numsA = indexA[i];
            for (int p = 0; p < numsA.size() - 1; p += 2) {
                int colA = numsA.get(p);
                int valA = numsA.get(p + 1);
                for (int j = 0; j < nB; j++) {
                    int valB = B[colA][j];
                    result[i][j] += valA * valB;
                }
            }
        }

        return result;
    }

    // Solution 2 using map
    public int[][] multiply(int[][] A, int[][] B) {
        if (A == null || A.length == 0 ||
                B == null || B.length == 0) {
            return new int[0][0];
        }

        int m = A.length;
        int n = A[0].length;
        int l = B[0].length;

        int[][] C = new int[m][l];

        // Step 1: convert the sparse A to dense format
        Map<Integer, Map<Integer, Integer>> denseA = new HashMap<>();
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (A[i][j] != 0) {
                    if (!denseA.containsKey(i)) {
                        denseA.put(i, new HashMap<>());
                    }
                    denseA.get(i).put(j, A[i][j]);
                }
            }
        }

        // Step 2: convert the sparse B to dense format
        Map<Integer, Map<Integer, Integer>> denseB = new HashMap<>();
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < l; j++) {
                if (B[i][j] != 0) {
                    if (!denseB.containsKey(i)) {
                        denseB.put(i, new HashMap<>());
                    }
                    denseB.get(i).put(j, B[i][j]);
                }
            }
        }

        // Step3: calculate the denseA * denseB
        for (int i : denseA.keySet()) {
            for (int j : denseA.get(i).keySet()) {
                if (!denseB.containsKey(j)) {
                    continue;
                }

                for (int k : denseB.get(j).keySet()) {
                    C[i][k] += denseA.get(i).get(j) * denseB.get(j).get(k);
                }
            }
        }

        return C;
    }
}

public class Solution {
    class Node implements Comparable<Node> {
        int index;
        int val;
        int prime;

        Node(int index, int val, int prime) {
            this.val = val;
            this.index = index;
            this.prime = prime;
        }

        public int compareTo(Node x) {
            return this.val - x.val ;
        }
    }

    public int nthSuperUglyNumber(int n, int[] primes) {
        int[] ugly_number = new int[n];
        ugly_number[0] = 1;
        PriorityQueue<Node> q = new PriorityQueue<Node>();
        for (int i = 0; i < primes.length; i++)
            q.add(new Node(0, primes[i], primes[i]));
        for (int i = 1; i < n; i++) {
            Node cur = q.peek();
            ugly_number[i] = cur.val;
            do {
                cur = q.poll();
                cur.val = ugly_number[++cur.index] * cur.prime;
                q.add(cur);
            } while (!q.isEmpty() && q.peek().val == ugly_number[i]);
        }
        return ugly_number[n - 1];
    }
}

public class Solution {
    public int maxProduct(String[] words) {
        int n = words.length;
        int[] elements = new int[n];
        for (int i=0;i<n;i++){
            for(int j=0;j<words[i].length();j++){
                elements[i] |= 1 << (words[i].charAt(j) - 'a');
            }
        }

        int ans = 0;
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                if ((elements[i] & elements[j]) == 0)
                    ans = Math.max(ans,words[i].length() * words[j].length());
            }
        }
        return ans;
    }
}

public class Solution {
    /**
     *  A bulb ends up on iff it is switched an odd number of times.
     *   Call them bulb 1 to bulb n. 
     *   Bulb i is switched in round d if and only if d divides i. 
     *   So bulb i ends up on if and only if it has an odd number of divisors.
     *   Divisors come in pairs, like i=12 has divisors 1 and 12, 2 and 6, and 3 and 4. 
     *   Except when i is a square, like 36 has divisors 1 and 36, 2 and 18, 
     *   3 and 12, 4 and 9, and double divisor 6. 
     *   So bulb i ends up on if and only if i is a square.
     *   So just count the square numbers. 
     * 
    */
    public int bulbSwitch(int n) {
        return (int)Math.sqrt(n);
    }
}

public class Solution {
    /****************
     * 
     * Let dp[v] to be the minimum number of coins required to get the amount v. 
     * dp[i+a_coin] = min(dp[i+a_coin], dp[i]+1) if dp[i] is reachable. 
     * dp[i+a_coin] = dp[i+a_coin] is dp[i] is not reachable. 
     * We initially set dp[i] to be MAX_VALUE.
     * 
     *************/

     public int coinChange(int[] coins, int amount) {
         if(amount==0) return 0;

        int[] dp = new int [amount+1];
        dp[0]=0; // do not need any coin to get 0 amount
        for(int i=1;i<=amount; i++)
            dp[i]= Integer.MAX_VALUE;

        for(int i=0; i<=amount; i++){
            for(int coin: coins){
            if(i+coin >= 0 && i+coin <=amount && dp[i]!=Integer.MAX_VALUE){
                    dp[i+coin] = Math.min(dp[i+coin], dp[i]+1);
                }
            }
        }
        if(dp[amount] == Integer.MAX_VALUE)
            return -1;
        return dp[amount];
    }
}

public class Solution {
    /**
     * 1. Find the median of the input array
     * 2. 3-way partition the array around median so that left contains greater than median, 
     *      middle contains equal to median, and right contains less than median.
     * 3. Take one from each partition in the order of middle --> left --> right 
     *      (as middle < left > right is wiggle order).  
    **/
    public void wiggleSort(int[] nums) {
        if (nums == null || nums.length <= 1) {
            return;
        }

        // Step 1: Find median of the array, return the index of the median
        // Step 2: 3-way sort, put median in the middle, 
        // numbers less than median on the left, 
        // numbers greater than median on the right
        int n = nums.length;
        int median = findKthLargest(nums, (n + 1) / 2);
        int i = 0;
        int left = 0;
        int right = n - 1;

        /**
           DNF order: [9,8,7,6  5,  4,3,2,1]
           DNF Index: [0,1,2,3, 4,  5,6,7,8]
           Wiggle order: [5, 9, 4, 8, 3, 7, 2, 6, 1]
           Wiggle Index: [0, 1, 2, 3, 4, 5, 6, 7, 8]

            Index Mapping: 
            ---------------
            DNF --> Wiggle (value)
            -----------------------
            0 --> 1 (9)
            1 --> 3 (8)
            2 --> 5 (7)
            3 --> 7 (6)
            4 --> 0 (5)
            5 --> 2 (4)
            6 --> 4 (3)
            7 --> 6 (2)
            8 --> 8 (1) 

            that is DNF(i) = WIGGLE((1+2*i)%n)
        **/
        while (i <= right) {
            if (nums[newIndex(i,n)] > median) {
                swap(nums, newIndex(left++,n), newIndex(i++,n));
            } else if (nums[newIndex(i,n)] < median) {
                swap(nums, newIndex(right--,n), newIndex(i,n));
            } else {
                i++;
            }
        }
    }

    private int newIndex(int index, int n) {
        return (1 + 2*index) % (n | 1);
    }

    private int findKthLargest(int[] nums, int k) {
		if (nums == null || nums.length == 0)
			return Integer.MAX_VALUE;
		return findKthLargest(nums, 0, nums.length - 1, nums.length - k);
	}

	// quick select: kth smallest
	private int findKthLargest(int[] nums, int start, int end, int k) {
		if (start > end)
			return Integer.MAX_VALUE;

		int pivot = nums[end];// Take A[end] as the pivot,
		int left = start;
		for (int i = start; i < end; i++) {
			if (nums[i] <= pivot) // Put numbers < pivot to pivot's left
				swap(nums, left++, i);
		}
		swap(nums, left, end);// Finally, swap A[end] with A[left]

		if (left == k)// Found kth smallest number
			return nums[left];
		else if (left < k)// Check right part
			return findKthLargest(nums, left + 1, end, k);
		else // Check left part
			return findKthLargest(nums, start, left - 1, k);
	}

	private void swap(int[] A, int i, int j) {
		int tmp = A[i];
		A[i] = A[j];
		A[j] = tmp;
	}
}

class Solution {
    public int maxSubArrayLen(int[] nums, int k) {
        Map<Integer, Integer> map = new HashMap<>();
        int max = 0;
        int sum = 0;
        for (int i = 0; i < nums.length; i++) {
            sum += nums[i];
            if (sum == k) {
                max = Math.max(max, i + 1);
            }
            int diff = sum - k;
            if (map.containsKey(diff)) {
                max = Math.max(max, i - map.get(diff));
            }
            if (!map.containsKey(sum)) {
                map.put(sum, i);
            }
        }
        return max;
    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode(int x) { val = x; }
 * }
 */
public class Solution {
    public ListNode oddEvenList(ListNode head) {
        if(head == null || head.next == null || head.next.next == null) return head;
        ListNode cur = head, tail = head;
        int len = 1;
        while(tail.next != null){
        	tail = tail.next;
        	len++;
        }

        int count = 1;
        while(count <= len/2){
            ListNode even = cur.next;
            cur.next = cur.next.next;
            tail.next = even;
            tail = tail.next;
            tail.next = null;
            cur = cur.next;
            count++;
        }
        return head;
    }
}

public class Solution {
    public boolean isValidSerialization(String preorder) {
        List<String> list = new ArrayList<String>();
		for(String x : preorder.split(",")){
			list.add(x);
			while(list.size() >= 3 && list.get(list.size()-1).equals("#") && 
					list.get(list.size()-2).equals("#") && 
					!list.get(list.size()-3).equals("#")){
				list.remove(list.size()-1);
				list.remove(list.size()-1);
				list.remove(list.size()-1);
				list.add("#");
			}
		}		        
        return (list.size() == 1) && (list.get(list.size()-1).equals("#"));
    }
}

public class Solution {
    HashMap<String, PriorityQueue<String>> map = new HashMap<String, PriorityQueue<String>>();
	LinkedList<String> result = new LinkedList<String>();

	public List<String> findItinerary(String[][] tickets) {
		for (String[] ticket : tickets) {
			if (!map.containsKey(ticket[0])) {
				PriorityQueue<String> q = new PriorityQueue<String>();
				map.put(ticket[0], q);
			}
			map.get(ticket[0]).offer(ticket[1]);
		}

		dfs("JFK");
		return result;
	}

	public void dfs(String s) {
		PriorityQueue<String> q = map.get(s);
		while (q != null && !q.isEmpty()) {
			dfs(q.poll());
		}
		result.addFirst(s);
	}
}

public class Solution {
    public boolean increasingTriplet(int[] nums) {
        int smallest = Integer.MAX_VALUE, mid = Integer.MAX_VALUE;
	    for (int i = 0; i < nums.length; i++) {
		    if (nums[i] <= smallest) {
			    smallest = nums[i];// update x to be a smaller value
		    } else if (nums[i] <= mid) {
			    mid = nums[i]; // update so that mid > smallest  
		    } else {
			    return true;
		    }
	    }
    	return false;
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public int rob(TreeNode root) {
        if(root == null) return 0;
        int[] result = helper(root);
        return Math.max(result[0], result[1]);
    }

    public int[] helper(TreeNode root){
        if(root == null){
            int[] result = {0, 0};
            return result;
        }

        int[] result = new int[2];
        int[] left = helper(root.left);
        int[] right = helper(root.right);

        // result[0] is when root is selected, result[1] is when not. 
        result[0] = root.val + left[1] + right[1];
        result[1] = Math.max(left[0], left[1]) + Math.max(right[0], right[1]);
        return result;
    }
}

public class Solution {
    public int[] countBits(int num) {
        int[] result = new int[num+1];
        //p tracks the index for number x
        int p = 1; 
        int pow = 1;
        for(int i = 1; i <= num; i++){
            if(i == pow){
                result[i] = 1;
                pow <<= 1;
                p = 1;
            }else{
                result[i] = result[p]+1;
                p++;
            }
        }
        return result;
    }
}

