public class Solution {
    // Let dp[i] to be the max production value for breaking the number i.
    // Since dp[i+j] can be i*j, 
    //  dp[i+j] = max(max(dp[i], i) * max(dp[j], j)), dp[i+j]).
    public int integerBreak(int n) {
        int[] dp = new int[n+1];
        for(int i = 1; i < n; i++){
            for(int j = 1; j < i+1; j++){
                if(i + j > n)
                    continue;
                dp[i+j] = Math.max(Math.max(dp[i],i) * 
                                Math.max(dp[j],j), dp[i+j]);
            }
        }
        return dp[n];
    }
}

public class Solution {

    public List<Integer> topKFrequent(int[] nums, int k) {
        // map to store element and its freqency
        Map<Integer, Integer> elemMap = new HashMap<>();
        for(int num : nums){
            elemMap.put(num, elemMap.getOrDefault(num, 0) + 1);
        }

        // MaxHeap to get most frequent element
        PriorityQueue<Map.Entry<Integer, Integer>> pq =
                new PriorityQueue<Map.Entry<Integer, Integer>>(elemMap.size(), new Comparator<Map.Entry<Integer, Integer>>(){

                    @Override
                    public int compare(Map.Entry<Integer, Integer> elem1,
                                       Map.Entry<Integer, Integer> elem2) {
                        return elem2.getValue() - elem1.getValue();
                    }

                });

        pq.addAll(elemMap.entrySet());
        List<Integer> res = new ArrayList<Integer>();
        // Get elements
        while(!pq.isEmpty() && k > 0){
            res.add(pq.poll().getKey());
            k--;
        }

        return res;
    }
}

public class Twitter {
    class Tweet {
        public int time;
        public int tweetId;
        public Tweet(int tweetId,int time){
            this.time = time;
            this.tweetId = tweetId;
        }

    }
    int timeStamp ;
    // Each person's tweets
    Map<Integer,List<Tweet>> timelines;
    // People's relationship
    Map<Integer,HashSet<Integer>> relations;
    /** Initialize your data structure here. */
    public Twitter() {
        this.timelines = new HashMap<>();
        this.relations = new HashMap<>();
    }

    /** Compose a new tweet. */
    public void postTweet(int userId, int tweetId) {
        if(timelines.containsKey(userId) == false){
            timelines.put(userId, new ArrayList<Tweet>());
        }
        timelines.get(userId).add(new Tweet(tweetId,timeStamp++));

    }

    /** Retrieve the 10 most recent tweet ids in the user's news feed. 
        Each item in the news feed must be posted by users who the user followed or 
        by the user herself. Tweets must be ordered from most recent to least recent. 
    ***/
    public List<Integer> getNewsFeed(int userId) {
        HashSet<Integer> followees = relations.get(userId);
        List<Tweet> candidates = new ArrayList<Tweet>();
        // Choose each person's top 10 tweets
         List<Tweet> timeline = timelines.get(userId);
         if(timeline!=null){
             for(int i=timeline.size()-1;i>=Math.max(0,timeline.size()-10);i--){
                    candidates.add(timeline.get(i));
                }
         }
        if(followees != null){ 
            for(Integer followee:followees){
                 timeline = timelines.get(followee);
                 if(timeline == null)
                    continue;
                 for(int i=timeline.size()-1;i>=Math.max(0,timeline.size()-10);i--){
                    candidates.add(timeline.get(i));
                }
            }
        }
        Collections.sort(candidates,new Comparator<Tweet> (){
             public int compare(Tweet o1, Tweet o2) {
                 return o2.time - o1.time;
                }
            });
        List<Integer> list = new ArrayList<Integer>();
        for(int i=0;i<Math.min(10,candidates.size());i++){
            list.add(candidates.get(i).tweetId);
        }
        return list;
    }

    /** Follower follows a followee. If the operation is invalid, it should be a no-op. */
    public void follow(int followerId, int followeeId) {
        if(followerId == followeeId) return;
        if(relations.containsKey(followerId)==false){
            relations.put(followerId,new HashSet<Integer>());
        }
        relations.get(followerId).add(followeeId);

    }

    /** Follower unfollows a followee. If the operation is invalid, it should be a no-op. */
    public void unfollow(int followerId, int followeeId) {
        HashSet<Integer> list = relations.get(followerId);
        if(list == null) return ;
        list.remove(followeeId);
    }
}

