public class Solution {
    public boolean validUtf8(int[] data) {
        if(data == null || data.length == 0)
            return false;
        boolean isValid = true;
        for(int i = 0; i < data.length; i++) {
            // 1 after 8th digit, 100000000
            if(data[i] > 255)
                return false;
            int numberOfBytes = 0;
            // 0xxxxxxx, 1 byte, 128(10000000)
            if((data[i] & 128) == 0) {
                numberOfBytes = 1;
            } else if((data[i] & 224) == 192) {
                // 110xxxxx, 2 bytes, 224(11100000), 192(11000000)
                numberOfBytes = 2;
            } else if((data[i] & 240) == 224) {
                // 1110xxxx, 3 bytes, 240(11110000), 224(11100000)
                numberOfBytes = 3;
            } else if((data[i] & 248) == 240) {
                // 11110xxx, 4 bytes, 248(11111000), 240(11110000)
                numberOfBytes = 4;
            } else {
                return false;
            }
            // check that the next n bytes start with 10xxxxxx
            for(int j = 1; j < numberOfBytes; j++) {
                if(i + j >= data.length)
                    return false;
                if((data[i+j] & 192) != 128)
                    return false; // 192(11000000), 128(10000000)
            }
            i += numberOfBytes-1;
        }
        return isValid;
    }
}

public class Solution {
    public String decodeString(String s) {
        Stack<Integer> count = new Stack<>();
        Stack<String> result = new Stack<>();
        int i = 0;
        result.push("");
        while (i < s.length()) {
            char ch = s.charAt(i);
            if (ch >= '0' && ch <= '9') {
                int start = i;
                while (s.charAt(i + 1) >= '0' && s.charAt(i + 1) <= '9') i++;
                count.push(Integer.parseInt(s.substring(start, i + 1)));
            } else if (ch == '[') {
                result.push("");
            } else if (ch == ']') {
                String str = result.pop();
                StringBuilder sb = new StringBuilder();
                int times = count.pop();
                for (int j = 0; j < times; j += 1) {
                    sb.append(str);
                }
                result.push(result.pop() + sb.toString());
            } else {
                result.push(result.pop() + ch);
            }
            i += 1;
        }
        return result.pop();
    }
}

public class Solution {
    public int longestSubstring(String s, int k) {
        char[] str = s.toCharArray();

        int max = 0; // result
        // Try substrings with 1 to 26 unique chars
        for (int h = 1; h <= 26; h++) {
            int[] counts = new int[26];
            int i = 0; // slow pointer
            int j = 0; // fast pointer
            int unique = 0; // count of unique chars
            int noLessThanK = 0; // count of char more than K
            while (j < str.length) {
                // When uniqu chars count less than h
                // fast pointer advance
                if (unique <= h) {
                    int idx = str[j] - 'a';
                    if (counts[idx] == 0)
                        unique++;
                    counts[idx]++;
                    if (counts[idx] == k)
                        noLessThanK++;
                    j++;
                } else {
                    int idx = str[i] - 'a';
                    if (counts[idx] == k)
                        noLessThanK--;
                    counts[idx]--;
                    if (counts[idx] == 0)
                        unique--;
                    i++;
                }
                if (unique == h && unique == noLessThanK)
                    max = Math.max(j - i, max);
            }
        }

        return max;
    }
}

public class Solution {
    public int maxRotateFunction(int[] A) {
        if(A.length == 0) return 0;
        int n = A.length, res = Integer.MIN_VALUE;
        for(int start = 0; start < n; start++){
            int cur = 0, curStart = start, newStart = 0;
            for(int i = 0; i < n; i++){
                if(curStart < n){
                    cur += curStart*A[i];
                    curStart++;
                } else {
                    cur += newStart*A[i];
                    newStart++;
                }
            }
            res = Math.max(cur, res);
        }
        return res;
    }
}

public class Solution {
    public int integerReplacement(int n) {
        int c = 0;
        while (n != 1) {
            if ((n & 1) == 0) {
                n >>>= 1;
            } else if (n == 3 || ((n >>> 1) & 1) == 0) {
                // if a number ends with 01 or 3
                --n;
            } else {
                ++n;
            }
            ++c;
        }
        return c;
    }
}

