public class Solution {
    // Similar to backpack problem
    public boolean canPartition(int[] nums) {
        // check edge case
        if (nums == null || nums.length == 0) {
            return true;
        }
        // preprocess
        int volumn = 0;
        for (int num : nums) {
            volumn += num;
        }
        if (volumn % 2 != 0) {
            return false;
        }
        volumn /= 2;
        // dp def
        boolean[] dp = new boolean[volumn + 1];
        // dp init
        dp[0] = true;
        // dp transition
        for (int i = 1; i <= nums.length; i++) {
            for (int j = volumn; j >= nums[i - 1]; j--) {
                dp[j] = dp[j] || dp[j - nums[i - 1]];
            }
        }
        return dp[volumn];
    }
}

public class Solution {
    public List<int[]> pacificAtlantic(int[][] matrix) {
        List<int[]> result = new ArrayList<int[]>();
        if (matrix.length == 0 || matrix[0].length == 0) return result;
        // the pacific boolean table
        boolean[][] pacific = new boolean[matrix.length][matrix[0].length];
        // the atlantic booean table
        boolean[][] atlantic = new boolean[matrix.length][matrix[0].length];
        //initially, all the top and left cells are flooded with pacific water
        //and all the right and bottom cells are flooded with atlantic water
        for (int i = 0; i < matrix.length; i++) {
            pacific[i][0] = true;
            atlantic[i][matrix[0].length - 1] = true;
        }
        for (int i = 0; i < matrix[0].length; i++) {
            pacific[0][i] = true;
            atlantic[matrix.length - 1][i] = true;
        }
        //we go around the matrix and try to flood the matrix from 4 side.
        for (int i = 0; i < matrix.length; i++) {
            boolean[][] pacificVisited = new boolean[matrix.length][matrix[0].length];
            boolean[][] atlanticVisited = new boolean[matrix.length][matrix[0].length];
            water(pacific, pacificVisited, matrix, i, 0);
            water(atlantic, atlanticVisited, matrix, i, matrix[0].length - 1);
        }
        for (int i = 0; i < matrix[0].length; i++) {
            boolean[][] pacificVisited = new boolean[matrix.length][matrix[0].length];
            boolean[][] atlanticVisited = new boolean[matrix.length][matrix[0].length];
            water(pacific, pacificVisited, matrix, 0, i);
            water(atlantic, atlanticVisited, matrix, matrix.length - 1, i);
        }
        //check the shared points among 2 tables
        for (int i = 0; i < matrix.length; i++) {
            for (int j = 0; j < matrix[0].length; j++) {
                if (pacific[i][j] && atlantic[i][j]) {
                    int[] element = {i, j};
                    result.add(element);
                }
            }
        }
        return result;
    }

    //the flood function
    private void water(boolean[][] wet, boolean[][] visited, int[][] matrix, int i, int j) {
        wet[i][j] = true;
        visited[i][j] = true;
        int[] x = {0, 0, 1, -1};
        int[] y = {1, -1, 0, 0};
        for (int k = 0; k < 4; k++) {
            if (i + y[k] >= 0 && i + y[k] < matrix.length && j + x[k] >= 0 && j + x[k] < matrix[0].length
                    && !visited[i + y[k]][j + x[k]] && matrix[i + y[k]][j + x[k]] >= matrix[i][j]) {
                water(wet, visited, matrix, i + y[k], j + x[k]);
            }
        }
    }
}

public class Solution {
    public int countBattleships(char[][] board) {
        if(board.length == 0) return 0;
        int totalCount = 0;
        for(int row = 0; row < board.length; row++){
            for(int col = 0; col < board[0].length; col++){
                // Case 1: it is a .
                if(board[row][col] == '.') continue;
                // Case 2: it is invalid
                if(row - 1 >= 0 && board[row-1][col] == 'X' &&
                    col - 1 >= 0 && board[row][col-1] == 'X')
                    return -1;
                // Case 3: adjacent to X
                if(row - 1 >= 0 && board[row-1][col] == 'X' ||
                    col - 1 >= 0 && board[row][col-1] == 'X')
                    continue;
                // Case 4: a new ship
                totalCount++;
            }
        }
        return totalCount;
    }
}

public class Solution {

    class Trie {
        Trie[] children;

        public Trie() {
            children = new Trie[2];
        }
    }

    public int findMaximumXOR(int[] nums) {
        if (nums == null || nums.length == 0) {
            return 0;
        }
        // Init Trie.
        Trie root = new Trie();
        for (int num : nums) {
            Trie curNode = root;
            for (int i = 31; i >= 0; i--) {
                int curBit = (num >>> i) & 1;
                if (curNode.children[curBit] == null) {
                    curNode.children[curBit] = new Trie();
                }
                curNode = curNode.children[curBit];
            }
        }
        int max = Integer.MIN_VALUE;
        for (int num : nums) {
            Trie curNode = root;
            int curSum = 0;
            for (int i = 31; i >= 0; i--) {
                int curBit = (num >>> i) & 1;
                if (curNode.children[curBit ^ 1] != null) {
                    curSum += (1 << i);
                    curNode = curNode.children[curBit ^ 1];
                } else {
                    curNode = curNode.children[curBit];
                }

                // for this case: even if all left bits results are 1s,
                // curSum still cannot catch up max value
                if (curSum < max && max - curSum >= (1 << i) - 1) {
                    break;
                }
            }
            max = Math.max(curSum, max);
        }
        return max;
    }
}

public class Solution {
    public String originalDigits(String s) {
        int[] count = new int[10];
        for (int i = 0; i < s.length(); i++){
            char c = s.charAt(i);
            if (c == 'z') count[0]++;
            if (c == 'w') count[2]++;
            if (c == 'x') count[6]++;
            if (c == 's') count[7]++; //7-6
            if (c == 'g') count[8]++;
            if (c == 'u') count[4]++;
            if (c == 'f') count[5]++; //5-4
            if (c == 'h') count[3]++; //3-8
            if (c == 'i') count[9]++; //9-8-5-6
            if (c == 'o') count[1]++; //1-0-2-4
        }
        count[7] -= count[6];
        count[5] -= count[4];
        count[3] -= count[8];
        count[9] = count[9] - count[8] - count[5] - count[6];
        count[1] = count[1] - count[0] - count[2] - count[4];
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i <= 9; i++){
            for (int j = 0; j < count[i]; j++){
                sb.append(i);
            }
        }
        return sb.toString();
    }
}

public class Solution {
    /**

     The problem says that we can make at most k changes to the string
     (any character can be replaced with any other character).
     So, let's say there were no constraints like the k.
     Given a string convert it to a string with all same characters with
     minimal changes. The answer to this is

     length of the entire string - number of times of the maximum occurring character in the string
     Given this, we can apply the at most k changes constraint and maintain a sliding window such that

     (length of substring - number of times of the maximum occurring character in the substring) <= k

     **/
    public int characterReplacement(String s, int k) {
        int len = s.length();
        int[] count = new int[26];
        int start = 0, maxCount = 0, maxLength = 0;
        for (int end = 0; end < len; end++) {
            maxCount = Math.max(maxCount, ++count[s.charAt(end) - 'A']);
            while (end - start + 1 - maxCount > k) {
                count[s.charAt(start) - 'A']--;
                start++;
            }
            maxLength = Math.max(maxLength, end - start + 1);
        }
        return maxLength;
    }
}

class Solution {
    public int minMutation(String start, String end, String[] bank) {
        List<Integer>[] graph = new List[bank.length];
        for (int i = 0; i < bank.length; i++) {
            graph[i] = new ArrayList<>();
            for (int j = 0; j < bank.length; j++)
                if (canMutate(bank[i], bank[j])) graph[i].add(j);
        }

        int targetIdx = -1;
        for (int i = 0; i < bank.length; i++) {
            if (bank[i].equals(end)) {
                targetIdx = i;
                break;
            }
        }
        if (targetIdx == -1) return -1;

        // endXXX means something calculated from end to start
        int[] endDist = new int[bank.length];
        Arrays.fill(endDist, -1);
        endDist[targetIdx] = 0;
        Queue<Integer> endQ = new ArrayDeque<>();
        for (int idx : graph[targetIdx]) {
            endQ.add(idx);
            endDist[idx] = 1;
        }

        // startXXX means something calculated from start to end
        int[] startDist = new int[bank.length];
        Arrays.fill(startDist, -1);
        Queue<Integer> startQ = new ArrayDeque<>();
        for (int i = 0; i < bank.length; i++) {
            if (canMutate(start, bank[i])) {
                if (bank[i].equals(end)) return 1;
                startQ.add(i);
                startDist[i] = 1;
            }
        }

        while (!startQ.isEmpty() && !endQ.isEmpty()) {
            int startIdx = startQ.poll();
            for (int nextIdx : graph[startIdx]) {
                if (startDist[nextIdx] != -1) continue;
                else if (endDist[nextIdx] >= 0) return startDist[startIdx] + endDist[nextIdx] + 1;
                else {
                    startDist[nextIdx] = startDist[startIdx] + 1;
                    startQ.add(nextIdx);
                }
            }

            int endIdx = endQ.poll();
            for (int nextIdx : graph[endIdx]) {
                if (endDist[nextIdx] != -1) continue;
                else if (startDist[nextIdx] > 0) return startDist[nextIdx] + endDist[endIdx] + 1;
                else {
                    endDist[nextIdx] = endDist[endIdx] + 1;
                    endQ.add(nextIdx);
                }
            }
        }

        return -1;
    }

    private boolean canMutate(String gen1, String gen2) {
        int diff = 0;
        for (int i = 0; i < gen1.length(); i++)
            diff += gen1.charAt(i) == gen2.charAt(i) ? 0 : 1;
        return diff == 1;
    }
}

/**
 * Definition for an interval.
 * public class Interval {
 * int start;
 * int end;
 * Interval() { start = 0; end = 0; }
 * Interval(int s, int e) { start = s; end = e; }
 * }
 */
public class Solution {
    public int eraseOverlapIntervals(Interval[] intervals) {
        Arrays.sort(intervals, new Comparator<Interval>() {
            @Override
            public int compare(Interval o1, Interval o2) {
                return o1.end - o2.end;  //first sort by end
            }
        });

        int end = Integer.MIN_VALUE;
        int count = 0;
        for (Interval interval : intervals) {
            if (interval.start >= end) end = interval.end;
            else count++;
        }

        return count;
    }
}

/**
 * Definition for an interval.
 * public class Interval {
 * int start;
 * int end;
 * Interval() { start = 0; end = 0; }
 * Interval(int s, int e) { start = s; end = e; }
 * }
 */
public class Solution {
    public int[] findRightInterval(Interval[] intervals) {
        int[] result = new int[intervals.length];
        TreeMap<Integer, Integer> intervalMap = new TreeMap<>();

        for (int i = 0; i < intervals.length; ++i) {
            intervalMap.put(intervals[i].start, i);
        }

        for (int i = 0; i < intervals.length; ++i) {
            Map.Entry<Integer, Integer> entry =
                    intervalMap.ceilingEntry(intervals[i].end);
            result[i] = (entry != null) ? entry.getValue() : -1;
        }

        return result;
    }
}

public class Solution {
    public List<Integer> findDuplicates(int[] nums) {
        // creating a new List
        List<Integer> newList = new ArrayList<Integer>();
        for (int i = 0; i < nums.length; i++) {
            // Taking the absolute value to find index
            int index = Math.abs(nums[i]);
            if (nums[index - 1] > 0) {
                nums[index - 1] = -nums[index - 1];
            } else {
                // If it is not greater than 0 (i.e)
                // negative then the number is a duplicate
                newList.add(Math.abs(nums[i]));
            }
        }
        return newList;
    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 * int val;
 * ListNode next;
 * ListNode(int x) { val = x; }
 * }
 */
public class Solution {
    public ListNode addTwoNumbers(ListNode l1, ListNode l2) {
        Stack<Integer> s1 = new Stack<Integer>();
        Stack<Integer> s2 = new Stack<Integer>();

        while (l1 != null) {
            s1.push(l1.val);
            l1 = l1.next;
        }
        ;
        while (l2 != null) {
            s2.push(l2.val);
            l2 = l2.next;
        }

        int sum = 0;
        ListNode list = new ListNode(0);
        while (!s1.empty() || !s2.empty()) {
            if (!s1.empty()) sum += s1.pop();
            if (!s2.empty()) sum += s2.pop();
            list.val = sum % 10;
            ListNode head = new ListNode(sum / 10);
            head.next = list;
            list = head;
            sum /= 10;
        }

        return list.val == 0 ? list.next : list;
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 * int val;
 * TreeNode left;
 * TreeNode right;
 * TreeNode(int x) { val = x; }
 * }
 */
public class Codec {
    private static final String SEP = ",";
    private static final String NULL = "null";

    // Encodes a tree to a single string.
    public String serialize(TreeNode root) {
        StringBuilder sb = new StringBuilder();
        if (root == null) return NULL;
        //traverse it recursively if you want to, I am doing it iteratively here
        Stack<TreeNode> st = new Stack<>();
        st.push(root);
        while (!st.empty()) {
            root = st.pop();
            sb.append(root.val).append(SEP);
            if (root.right != null) st.push(root.right);
            if (root.left != null) st.push(root.left);
        }
        return sb.toString();
    }

    // Decodes your encoded data to tree.
    // pre-order traversal
    public TreeNode deserialize(String data) {
        if (data.equals(NULL)) return null;
        String[] strs = data.split(SEP);
        Queue<Integer> q = new LinkedList<>();
        for (String e : strs) {
            q.offer(Integer.parseInt(e));
        }
        return getNode(q);
    }

    // some notes:
    //   5
    //  3 6
    // 2   7
    private TreeNode getNode(Queue<Integer> q) { //q: 5,3,2,6,7
        if (q.isEmpty()) return null;
        TreeNode root = new TreeNode(q.poll());//root (5)
        Queue<Integer> samllerQueue = new LinkedList<>();
        while (!q.isEmpty() && q.peek() < root.val) {
            samllerQueue.offer(q.poll());
        }
        //smallerQueue : 3,2   storing elements smaller than 5 (root)
        root.left = getNode(samllerQueue);
        //q: 6,7   storing elements bigger than 5 (root)
        root.right = getNode(q);
        return root;
    }
}

