/**
 * Definition for a binary tree node.
 * public class TreeNode {
 * int val;
 * TreeNode left;
 * TreeNode right;
 * TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public TreeNode deleteNode(TreeNode root, int key) {
        if (root == null) return null;
        if (root.val == key) return replaceNode(root);

        TreeNode parent = root;
        TreeNode current = (key < root.val) ? root.left : root.right;

        while (current != null) {
            if (current.val == key) {
                if (current == parent.left) {
                    parent.left = replaceNode(current);
                } else {
                    parent.right = replaceNode(current);
                }
                return root;
            } else {
                parent = current;
                current = (key < current.val) ? current.left : current.right;
            }
        }
        return root;
    }

    TreeNode replaceNode(TreeNode node) {
        if (node.left == null && node.right == null) return null;
        if (node.left != null && node.right == null) return node.left;
        if (node.left == null && node.right != null) return node.right;

        TreeNode parent = node, current = node.right;
        while (current.left != null) {
            parent = current;
            current = current.left;
        }

        if (node.right == current) {
            current.left = node.left;
        } else {
            parent.left = current.right;
            current.right = node.right;
            current.left = node.left;
        }
        return current;

    }
}

public class Solution {
    public String frequencySort(String s) {
        if (s == null) return null;

        Map<Character, Integer> map = new HashMap();
        char[] charArray = s.toCharArray();
        int max = 0;
        for (Character c : charArray) {
            if (!map.containsKey(c)) {
                map.put(c, 0);
            }
            map.put(c, map.get(c) + 1);
            max = Math.max(max, map.get(c));
        }

        List<Character>[] array = buildArray(map, max);
        return buildString(array);
    }

    private List<Character>[] buildArray(Map<Character, Integer> map, int maxCount) {
        List<Character>[] array = new List[maxCount + 1];
        for (Character c : map.keySet()) {
            int count = map.get(c);
            if (array[count] == null) {
                array[count] = new ArrayList();
            }
            array[count].add(c);
        }
        return array;
    }

    private String buildString(List<Character>[] array) {
        StringBuilder sb = new StringBuilder();
        for (int i = array.length - 1; i > 0; i--) {
            List<Character> list = array[i];
            if (list != null) {
                for (Character c : list) {
                    for (int j = 0; j < i; j++) {
                        sb.append(c);
                    }
                }
            }
        }
        return sb.toString();
    }
}

public class Solution {
    /***

     Idea:
     We know that eventually we have to shoot down every balloon, so for each ballon
     there must be an arrow whose position is between balloon[0] and balloon[1].
     Given that, we can sort the array of balloons by their ending position.
     Then we make sure that while we take care of each balloon from the beginning,
     we can shoot as many following balloons as possible.

     So what position should we pick? We should shoot as right as possible,
     because all balloons' end position is to the right of the current one.
     Therefore the position should be currentBalloon[1],
     because we still need to shoot down the current one.

     This is exactly what I do in the for loop:
     check how many balloons I can shoot down with one shot aiming at
     the ending position of the current balloon.
     Then I skip all these balloons and start again from the next one
     (or the leftmost remaining one) that needs another arrow.

     ***/
    public int findMinArrowShots(int[][] points) {
        if (points == null || points.length == 0 || points[0].length == 0)
            return 0;
        Arrays.sort(points, new Comparator<int[]>() {
            public int compare(int[] a, int[] b) {
                if (a[0] == b[0])
                    return a[1] - b[1];
                return a[0] - b[0];
            }
        });

        int minArrows = 1;
        int arrowLimit = points[0][1];
        for (int i = 1; i < points.length; i++) {
            int[] baloon = points[i];
            if (baloon[0] <= arrowLimit) {
                arrowLimit = Math.min(arrowLimit, baloon[1]);
            } else {
                minArrows++;
                arrowLimit = baloon[1];
            }
        }
        return minArrows;
    }
}

public class Solution {
    // Time complexity:  O(n^2)
    // Space complexity: O(n^2)
    public int fourSumCount(int[] A, int[] B, int[] C, int[] D) {
        Map<Integer, Integer> map = new HashMap<>();

        for (int i = 0; i < C.length; i++) {
            for (int j = 0; j < D.length; j++) {
                int sum = C[i] + D[j];
                map.put(sum, map.getOrDefault(sum, 0) + 1);
            }
        }

        int res = 0;
        for (int i = 0; i < A.length; i++) {
            for (int j = 0; j < B.length; j++) {
                res += map.getOrDefault(-1 * (A[i] + B[j]), 0);
            }
        }
        return res;
    }
}

public class Solution {

    class Pair {
        int min, max;

        public Pair(int min, int max) {
            this.min = min;
            this.max = max;
        }
    }

    public boolean find132pattern(int[] nums) {
        Stack<Pair> stack = new Stack();
        for (int n : nums) {
            if (stack.isEmpty() || n < stack.peek().min)
                stack.push(new Pair(n, n));
            else if (n > stack.peek().min) {
                Pair last = stack.pop();
                if (n < last.max)
                    return true;
                last.max = n;
                while (!stack.isEmpty() && n >= stack.peek().max)
                    stack.pop();
                // At this time, n < stack.peek().max (if stack not empty)
                if (!stack.isEmpty() && stack.peek().min < n)
                    return true;
                stack.push(last);
            }
        }
        return false;
    }
}

public class Solution {
    public int minMoves2(int[] nums) {
        int sum = 0;
        int median = quickselect(nums, nums.length / 2 + 1, 0, nums.length - 1);
        for (int i = 0; i < nums.length; i++)
            sum += Math.abs(nums[i] - median);
        return sum;
    }

    private int quickselect(int[] A, int k, int start, int end) {
        int l = start, r = end, pivot = A[(l + r) / 2];
        while (l <= r) {
            while (A[l] < pivot) l++;
            while (A[r] > pivot) r--;
            if (l >= r) break;
            swap(A, l++, r--);
        }
        if (l - start + 1 > k)
            return quickselect(A, k, start, l - 1);
        if (l - start + 1 == k && l == r)
            return A[l];
        return quickselect(A, k - r + start - 1, r + 1, end);
    }

    private void swap(int[] A, int i, int j) {
        int temp = A[i];
        A[i] = A[j];
        A[j] = temp;
    }
}

public class Solution {
    public boolean canIWin(int maxChoosableInteger, int desiredTotal) {
        int sum = (1+maxChoosableInteger)*maxChoosableInteger/2;
        if(sum < desiredTotal) return false;
        if(desiredTotal <= 0) return true;

        Map<Integer, Boolean> map = new HashMap();
        boolean[] used = new boolean[maxChoosableInteger+1];
        return helper(desiredTotal, used, map);
    }

    private boolean helper(int desiredTotal, boolean[] used, Map<Integer, Boolean> map){
        if(desiredTotal <= 0) return false;
        int key = format(used);
        if(!map.containsKey(key)){
            // try every unchosen number as next step
            for(int i = 1; i < used.length; i++){
                if(used[i]) continue;
                used[i] = true;
                // check whether this lead to a win (i.e. the other player lose)
                if(!helper(desiredTotal - i, used, map)){
                    map.put(key, true);
                    used[i] = false;
                    return true;
                }
                used[i] = false;
            }
            map.put(key, false);
        }
        return map.get(key);
    }

    // transfer boolean[] to an Integer
    private int format(boolean[] used){
        int num = 0;
        for(boolean b: used){
            num <<= 1;
            if(b) num |= 1;
        }
        return num;
    }
}

public class Solution {
    public String validIPAddress(String IP) {
        if (isValidIPv4(IP))
            return "IPv4";
        else if (isValidIPv6(IP))
            return "IPv6";
        else
            return "Neither";
    }

    private boolean isValidIPv4(String ip) {
        String[] tokens = ip.split("\\.");
        if (ip.length() < 7 || ip.charAt(0) == '.' ||
                ip.charAt(ip.length() - 1) == '.' || tokens.length != 4)
            return false;
        for (String token : tokens) {
            if (token.startsWith("0") && token.length() > 1)
                return false;
            try {
                int parsedInt = Integer.parseInt(token);
                if (parsedInt < 0 || parsedInt > 255 ||
                        parsedInt == 0 && token.charAt(0) != '0')
                    return false;
            } catch (NumberFormatException nfe) {
                return false;
            }
        }
        return true;
    }

    private boolean isValidIPv6(String ip) {
        String[] tokens = ip.split(":");
        if (ip.length() < 15 || ip.charAt(0) == ':' ||
                ip.charAt(ip.length() - 1) == ':' || tokens.length != 8)
            return false;
        for (String token : tokens) {
            if (token.length() > 4 || token.length() == 0)
                return false;
            char[] chars = token.toCharArray();
            for (char c : chars) {
                if (!Character.isDigit(c) && !((c - 'A') >= 0 && (c - 'A') <= 5) &&
                        !((c - 'a') >= 0 && (c - 'a') <= 5))
                    return false;
            }
        }
        return true;
    }
}

public class Solution {
    public boolean makesquare(int[] nums) {
        if (nums == null || nums.length < 4) return false;
        int sum = 0;
        for (int num : nums) sum += num;
        if (sum % 4 != 0) return false;

        Arrays.sort(nums);
        reverse(nums);

        return dfs(nums, new int[4], 0, sum / 4);
    }

    private boolean dfs(int[] nums, int[] sums, int index, int target) {
        if (index == nums.length) {
            if (sums[0] == target && sums[1] == target && sums[2] == target) {
                return true;
            }
            return false;
        }

        for (int i = 0; i < 4; i++) {
            if (sums[i] + nums[index] < target) continue;
            sums[i] += nums[index];
            if (dfs(nums, sums, index + 1, target)) return true;
            sums[i] -= nums[index];
        }
        return false;
    }

    private void reverse(int[] nums) {
        int i = 0, j = nums.length - 1;
        while (i < j) {
            int temp = nums[i];
            nums[i] = nums[j];
            nums[j] = temp;
            i++;
            j--;
        }
    }
}

public class Solution {
    public int findMaxForm(String[] strs, int m, int n) {
        int[][] max = new int[m + 1][n + 1];
        for (int i = 0; i < strs.length; i++) {
            String str = strs[i];
            int neededZero = 0;
            int neededOne = 0;
            for (int j = 0; j < str.length(); j++) {
                if (str.charAt(j) == '0') {
                    neededZero++;
                } else {
                    neededOne++;
                }
            }

            int[][] newMax = new int[m + 1][n + 1];
            for (int zero = 0; zero <= m; zero++) {
                for (int one = 0; one <= n; one++) {
                    if (zero >= neededZero && one >= neededOne) {
                        int zeroLeft = zero - neededZero;
                        int oneLeft = one - neededOne;
                        // Since we have one valid combination so +1
                        newMax[zero][one] = Math.max(1 + max[zeroLeft][oneLeft], max[zero][one]);
                    } else {
                        newMax[zero][one] = max[zero][one];
                    }
                }
            }
            max = newMax;
        }
        return max[m][n];
    }
}

public class Solution {
    public int totalHammingDistance(int[] nums) {
        int total = 0, n = nums.length;
        for (int j = 0; j < 32; j++) {
            int bitCount = 0;
            for (int i = 0; i < n; i++)
                bitCount += (nums[i] >> j) & 1;
            total += bitCount * (n - bitCount);
        }
        return total;
    }
}

public class Solution {
    public int magicalString(int n) {
        if (n <= 0) return 0;
        if (n <= 3) return 1;

        // Create an int array a and initialize the first 3 elements
        int[] a = new int[n + 1];
        a[0] = 1;
        a[1] = 2;
        a[2] = 2;
        // head is the index to generate new numbers
        int head = 2;
        // tail is the index to put the new number
        int tail = 3;
        int num = 1; // current number
        int result = 1;

        while (tail < n) {
            // Generate number with count of a[head]
            for (int i = 0; i < a[head]; i++) {
                a[tail] = num;
                if (num == 1 && tail < n)
                    result++;
                tail++;
            }
            // flip number back and forth between 1 and 2
            num = num ^ 3;
            head++;
        }

        return result;
    }
}

public class Solution {
    // if a % c == b % cthen (a-b) / c == 0
    public boolean checkSubarraySum(int[] nums, int k) {
        Map<Integer, Integer> map = new HashMap<>();
        map.put(0, -1);
        int runningSum = 0;
        for (int i = 0; i < nums.length; i++) {
            runningSum += nums[i];
            if (k != 0)
                runningSum %= k;
            Integer prev = map.get(runningSum);
            if (prev != null) {
                if (i - prev > 1) return true;
            } else
                map.put(runningSum, i);
        }
        return false;
    }
}

public class Solution {
    private int gcd(int x, int y){
        if(y == 0) return x;
        return gcd(y, x % y);
    }

    public String fractionAddition(String expression) {
        StringBuilder res = new StringBuilder();
        // Split the expression to get fracs
        if(expression.charAt(0) != '-' && expression.charAt(0) != '+')
            expression = '+' + expression;
        String[] fracs = expression.split("[+-]");
        // Get signs of each fracs
        int[] signs = new int[fracs.length];
        int signInd = 0;
        for(int i = 0; i < expression.length(); i++){
            if(expression.charAt(i) == '+' || expression.charAt(i) == '-')
                signs[signInd++] = expression.charAt(i) == '+' ? 1 : -1;
        }
        // x is sum of nomerator and y is total denominator
        // starts with x/y = 0/1
        int x = 0, y = 1;
        for(int i = 1; i < fracs.length; i++){
            String f = fracs[i];
            String[] ft = f.split("/");
            // numerator
            int a = Integer.valueOf(ft[0])*signs[i-1];
            // denominator
            int b = Integer.valueOf(ft[1]);
            x = x*b + a*y;
            y *= b;
        }
        // If x <= 0
        if(x == 0){
            y = 1;
        } else if (x < 0){
            res.append('-');
            x *= -1;
        }
        // x and y > 0
        if (x > 0 && y > 0){
            int g = gcd(x, y);
            x /= g;
            y /= g;
        }
        res.append(String.valueOf(x) + '/' + String.valueOf(y));
        return res.toString();
    }
}

public class Solution {

    public boolean validSquare(int[] p1, int[] p2, int[] p3, int[] p4) {
        int[][] p = {p1,p2,p3,p4};
        return checkAllPermutations(p, 0);
    }

    private boolean checkAllPermutations(int[][] p, int l) {
        if (l == 4) {
            return check(p[0], p[1], p[2], p[3]);
        }

        boolean res = false;
        for (int i = l; i < 4; i++) {
            swap(p, l, i);
            res |= checkAllPermutations(p, l + 1);
            swap(p, l, i);
        }
        return res;
    }

    private boolean check(int[] p1, int[] p2, int[] p3, int[] p4) {
        return dist(p1,p2) > 0 && dist(p1, p2) == dist(p2, p3) &&
            dist(p2, p3) == dist(p3, p4) && dist(p3, p4) == dist(p4, p1) &&
            dist(p1, p3) == dist(p2, p4);
    }

    private double dist(int[] p1, int[] p2) {
        return (p2[1] - p1[1]) * (p2[1] - p1[1]) +
            (p2[0] - p1[0]) * (p2[0] - p1[0]);
    }

    private void swap(int[][] p, int x, int y) {
        int[] temp = p[x];
        p[x] = p[y];
        p[y] = temp;
    }
}

public class Solution {
    // Time complexity : O(n*x). n strings of average length x is parsed.
    // Space complexity : O(n*x). map and res size grows upto n*x.
    public List<List<String>> findDuplicate(String[] paths) {
        Map<String, List<String>> map = new HashMap<>();
        for (String path: paths) {
            String[] values = path.split(" ");
            for (int i = 1; i < values.length; i++) {
                String[] name_cont = values[i].split("\\(");
                name_cont[1] = name_cont[1].replace(")", "");
                List<String> list = map.getOrDefault(name_cont[1], new ArrayList<String>());
                list.add(values[0] + "/" + name_cont[0]);
                map.put(name_cont[1], list);
            }
        }
        List<List<String>> res = new ArrayList<>();
        for (String key: map.keySet()) {
            if (map.get(key).size() > 1)
                res.add(map.get(key));
        }
        return res;
    }
}

public class Solution {
    public int triangleNumber(int[] nums) {
        int result = 0;
        if (nums.length < 3) return result;

        Arrays.sort(nums);

        for (int i = 2; i < nums.length; i++) {
            int left = 0, right = i - 1;
            while (left < right) {
                if (nums[left] + nums[right] > nums[i]) {
                    result += (right - left);
                    right--;
                }
                else {
                    left++;
                }
            }
        }

        return result;
    }
}

public class Solution {
    public int leastInterval(char[] tasks, int n) {
        // Map task to number of tasks
        Map<Character, Integer> map = new HashMap<>();
        for(char task : tasks){
            map.put(task, map.getOrDefault(task, 0) + 1);
        }
        // Sort tasks based on frequency
        PriorityQueue<Map.Entry<Character, Integer>> pq = new PriorityQueue<>(
                (a, b) -> a.getValue() != b.getValue() ?
                        b.getValue() - a.getValue() : a.getKey() - b.getKey()
        );
        pq.addAll(map.entrySet());

        int count = 0;
        while(!pq.isEmpty()){
            int cycle = n + 1; // total time to execute a task
            List<Map.Entry<Character, Integer>> remainingTasks = new ArrayList<>();
            while(cycle > 0 && !pq.isEmpty()){
                Map.Entry<Character, Integer> top = pq.poll();
                // Update count of this task if still has left
                if(top.getValue() - 1 > 0){
                    top.setValue(top.getValue() - 1);
                    remainingTasks.add(top);
                }
                cycle--; // Update time left in this cycle
                count++; // Successfully executed task
            }
            for(Map.Entry<Character, Integer> t : remainingTasks){
                pq.add(t);
            }
            if(pq.isEmpty()) break;
            count += cycle;
        }
        return count;
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public TreeNode addOneRow(TreeNode root, int v, int d) {
        if (d == 1) {
			TreeNode newRoot = new TreeNode(v);
			newRoot.left = root;
			return newRoot;
		}
        int curDepth = 1;
        Deque<TreeNode> parentLevel = new ArrayDeque<>();
        parentLevel.offerFirst(root);
        while(curDepth < d - 1){
            Deque<TreeNode> nextLevel = new ArrayDeque<>();
            while(!parentLevel.isEmpty()){
                TreeNode top = parentLevel.pollFirst();
                if(top.left != null)
                    nextLevel.offerFirst(top.left);
                if(top.right != null)
                    nextLevel.offerFirst(top.right);
            }
            parentLevel = nextLevel;
            curDepth++;
        }

        while(!parentLevel.isEmpty()){
            TreeNode top = parentLevel.pollFirst();
            TreeNode temp = top.left;
            top.left = new TreeNode(v);
            top.left.left = temp;
            temp = top.right;
            top.right = new TreeNode(v);
            top.right.right = temp;
        }
        return root;
    }
}

public class Solution {
    public int smallestFactorization(int n) {
        // Case 1: If number is smaller than 10
        if (n < 10) return n;

        // Case 2: Start with 9 and try every possible digit
        List<Integer> res = new ArrayList<>();
        for (int i = 9; i > 1; i--) {
            // If current digit divides n, then store all
            // occurrences of current digit in res
            while (n % i == 0) {
                n = n / i;
                res.add(i);
            }
        }

        // If n could not be broken in form of digits
        if (n != 1) return 0;

        // Get the result from the array in reverse order
        long result = 0;
        for (int i = res.size() - 1; i >= 0; i--) {
            result = result * 10 + res.get(i);
            if (result > Integer.MAX_VALUE) return 0;
        }

        return (int)result;
    }
}

public class Solution {
    /**
     * dp[n][k] denotes the number of arrays that have k inverse pairs for array composed of 1 to n
we can establish the recursive relationship between dp[n][k] and dp[n-1][i]:

if we put n as the last number then all the k inverse pair should come from the first n-1 numbers
if we put n as the second last number then there's 1 inverse pair involves n so the rest k-1 comes from the first n-1 numbers
...
if we put n as the first number then there's n-1 inverse pairs involve n so the rest k-(n-1) comes from the first n-1 numbers

dp[n][k] = dp[n-1][k]+dp[n-1][k-1]+dp[n-1][k-2]+...+dp[n-1][k+1-n+1]+dp[n-1][k-n+1]

It's possible that some where in the right hand side the second array index become negative,
since we cannot generate negative inverse pairs we just treat them as 0,
but still leave the item there as a place holder.

dp[n][k] = dp[n-1][k]+dp[n-1][k-1]+dp[n-1][k-2]+...+dp[n-1][k+1-n+1]+dp[n-1][k-n+1]
dp[n][k+1] = dp[n-1][k+1]+dp[n-1][k]+dp[n-1][k-1]+dp[n-1][k-2]+...+dp[n-1][k+1-n+1]

so by deducting the first line from the second line, we have

dp[n][k+1] = dp[n][k]+dp[n-1][k+1]-dp[n-1][k+1-n]
     *
    **/
    public int kInversePairs(int n, int k) {
        int mod = 1000000007;
        if (k > n*(n-1)/2 || k < 0) return 0;
        if (k == 0 || k == n*(n-1)/2) return 1;
        long[][] dp = new long[n+1][k+1];
        dp[2][0] = 1;
        dp[2][1] = 1;
        for (int i = 3; i <= n; i++) {
            dp[i][0] = 1;
            for (int j = 1; j <= Math.min(k, i*(i-1)/2); j++) {
                dp[i][j] = dp[i][j-1] + dp[i-1][j];
                if (j >= i) dp[i][j] -= dp[i-1][j-i];
                dp[i][j] = (dp[i][j]+mod) % mod;
            }
        }
        return (int) dp[n][k];
    }
}

public class Solution {
    public int scheduleCourse(int[][] courses) {
        // Sort the courses by their deadlines
        // (Greedy! We have to deal with courses with early deadlines first)
        Arrays.sort(courses, (a, b) -> a[1] - b[1]);
        PriorityQueue<Integer> queue = new PriorityQueue<>((a, b) -> b - a);
        int time = 0;
        for (int[] c: courses) {
            if (time + c[0] <= c[1]) {
                queue.offer(c[0]);
                time += c[0];
            } else if (!queue.isEmpty() && queue.peek() > c[0]) {
                // If time exceeds, drop the previous course which costs the most time.
                // (That must be the best choice!)
                time += c[0] - queue.poll();
                queue.offer(c[0]);
            }
        }
        return queue.size();
    }
}

public class Solution {
    /***
     * Time Complexity: O(log(n+m))
     * Space Complexity: O(1)
     *
     * if total count is odd then find the one in the middle
     * else find the another one on the left and get average
     *
     * the method of find middle can be converted to find the kth
     * element recursively
     *
     */
    public double findMedianSortedArrays(int[] nums1, int[] nums2) {
        int totalCount = nums1.length + nums2.length;
        int right = getKthElement(totalCount/2 + 1, nums1, nums2, 0, 0);
        if(totalCount %2 != 0){
            return 1.0*right;
        }
        int left = getKthElement(totalCount/2, nums1, nums2, 0, 0);
        return (left + right)/2.0;
    }

    private int getKthElement(int k, int[] nums1, int[] nums2, int start1, int start2){
        if(start1 >= nums1.length){
            return nums2[start2 + k - 1];
        }
        if(start2 >= nums2.length){
            return nums1[start1 + k - 1];
        }

        if(k == 1)
            return Math.min(nums1[start1], nums2[start2]);
        int mid1 = (start1 + k/2 - 1 < nums1.length) ? nums1[start1 + k/2 - 1] : Integer.MAX_VALUE;
        int mid2 = (start2 + k/2 - 1 < nums2.length) ? nums2[start2 + k/2 - 1] : Integer.MAX_VALUE;

        // Key is to use k - k/2 not k/2
        if(mid1 < mid2)
            return getKthElement(k - k/2, nums1, nums2, start1 + k/2, start2);
        return getKthElement(k - k/2, nums1, nums2, start1, start2 + k/2);
    }
}

public class Solution {
    /**
     1, If p.charAt(j) == s.charAt(i) :  dp[i][j] = dp[i-1][j-1];
     2, If p.charAt(j) == '.' : dp[i][j] = dp[i-1][j-1];
     3, If p.charAt(j) == '*': here are two sub conditions:
     3.1, if p.charAt(j-1) != s.charAt(i) : dp[i][j] = dp[i][j-2]  //in this case, a* only counts as empty
     3.2, if p.charAt(i-1) == s.charAt(i) or p.charAt(i-1) == '.':
     dp[i][j] = dp[i-1][j]    //in this case, a* counts as multiple a
     or dp[i][j] = dp[i][j-1]   // in this case, a* counts as single a
     or dp[i][j] = dp[i][j-2]   // in this case, a* counts as empty
     ***/
    public boolean isMatch(String s, String p) {
        if (s == null || p == null) {
            return false;
        }
        boolean[][] dp = new boolean[s.length() + 1][p.length() + 1];
        dp[0][0] = true;
        for (int i = 1; i < p.length(); i++) {
            if (p.charAt(i) == '*' && dp[0][i-1]) {
                dp[0][i+1] = true;
            }
        }
        for (int i = 0 ; i < s.length(); i++) {
            for (int j = 0; j < p.length(); j++) {
                if (p.charAt(j) == '.' || p.charAt(j) == s.charAt(i)) {
                    dp[i + 1][j + 1] = dp[i][j];
                }
                if (p.charAt(j) == '*') {
                    if (p.charAt(j-1) != s.charAt(i) && p.charAt(j-1) != '.') {
                        dp[i+1][j+1] = dp[i+1][j-1];
                    } else {
                        dp[i+1][j+1] = (dp[i+1][j] || dp[i][j+1] || dp[i+1][j-1]);
                    }
                }
            }
        }
        return dp[s.length()][p.length()];
    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 * int val;
 * ListNode next;
 * ListNode(int x) { val = x; }
 * }
 */
public class Solution {
    public ListNode mergeKLists(ListNode[] lists) {
        if (lists == null || lists.length == 0) {
            return null;
        }
        PriorityQueue<ListNode> queue = new PriorityQueue<>(lists.length, new Comparator<ListNode>() {
            @Override
            public int compare(ListNode o1, ListNode o2) {
                if (o1.val < o2.val)
                    return -1;
                else if (o1.val == o2.val)
                    return 0;
                else
                    return 1;
            }
        });

        ListNode dummy = new ListNode(0);
        ListNode tail = dummy;

        for (ListNode node : lists)
            if (node != null)
                queue.add(node);

        while (!queue.isEmpty()) {
            tail.next = queue.poll();
            tail = tail.next;

            if (tail.next != null)
                queue.add(tail.next);
        }
        return dummy.next;
    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode(int x) { val = x; }
 * }
 */
public class Solution {
    public ListNode reverseKGroup(ListNode head, int k) {
        ListNode begin;
        if (head == null || head.next == null || k == 1)
            return head;
        ListNode dummyhead = new ListNode(-1);
        ListNode cur = head;
        dummyhead.next = cur;
        begin = dummyhead;
        int i = 0;
        while (cur != null) {
            i++;
            if (i % k == 0) {
                begin = reverse(begin, cur.next);
                cur = begin.next;
            } else {
                cur = cur.next;
            }
        }
        return dummyhead.next;
    }

    private ListNode reverse(ListNode begin, ListNode end) {
        ListNode curr = begin.next;
        while (curr.next != end) {
            ListNode temp = begin.next;
            begin.next = curr.next;
            curr.next = curr.next.next;
            begin.next.next = temp;
        }
        return curr;
    }
}

public class Solution {
    public List<Integer> findSubstring(String s, String[] words) {
        List<Integer> ans = new ArrayList<>();
        int n = s.length(), cnt = words.length;
        if (n <= 0 || cnt <= 0) {
            return ans;
        }

        // init word occurence
        Map<String, Integer> dict = new HashMap<>();
        for (int i = 0; i < cnt; ++i) {
            if (dict.containsKey(words[i])) {
                dict.put(words[i], dict.get(words[i]) + 1);
            } else {
                dict.put(words[i], 1);
            }
        }

        // travel all sub string combinations
        int wl = words[0].length();
        for (int i = 0; i < wl; ++i) {
            int left = i, count = 0;
            Map<String, Integer> tdict = new HashMap<>();
            for (int j = i; j <= n - wl; j += wl) {
                String str = s.substring(j, j + wl);
                // a valid word, accumulate results
                if (dict.containsKey(str)) {
                    if (tdict.containsKey(str)) {
                        tdict.put(str, tdict.get(str) + 1);
                    } else {
                        tdict.put(str, 1);
                    }
                    if (tdict.get(str) <= dict.get(str))
                        count++;
                    else {
                        // a more word, advance the window left side possiablly
                        while (tdict.get(str) > dict.get(str)) {
                            String str1 = s.substring(left, left + wl);
                            tdict.put(str1, tdict.get(str1) - 1);
                            if (tdict.get(str1) < dict.get(str1)) {
                                count--;
                            }
                            left += wl;
                        }
                    }
                    // come to a result
                    if (count == cnt) {
                        ans.add(left);
                        // advance one word
                        tdict.put(s.substring(left, left + wl), dict.get(s.substring(left, left + wl)) - 1);
                        count--;
                        left += wl;
                    }
                } else {
                    tdict.clear();
                    count = 0;
                    left = j + wl;
                }
            }
        }
        return ans;
    }
}

public class Solution {
    // Solution with Stack
    public int longestValidParentheses(String s) {
        int res = 0;
        Stack<Integer> indexStack = new Stack<Integer>();
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == ')' && !indexStack.isEmpty() &&
                    s.charAt(indexStack.peek()) == '(') {
                indexStack.pop();
                if (indexStack.isEmpty())
                    res = i + 1;
                else
                    res = Math.max(res, i - indexStack.peek());
            } else {
                indexStack.push(i);
            }
        }
        return res;
    }

    Solution without stack
    public int longestValidParentheses(String s) {
        int left = 0, right = 0, maxlength = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '(') {
                left++;
            } else {
                right++;
            }
            if (left == right) {
                maxlength = Math.max(maxlength, 2 * right);
            } else if (right >= left) {
                left = right = 0;
            }
        }
        left = right = 0;
        for (int i = s.length() - 1; i >= 0; i--) {
            if (s.charAt(i) == '(') {
                left++;
            } else {
                right++;
            }
            if (left == right) {
                maxlength = Math.max(maxlength, 2 * left);
            } else if (left >= right) {
                left = right = 0;
            }
        }
        return maxlength;
    }
}

public class Solution {
    public void solveSudoku(char[][] board) {
        solve(board, 0, 0);
    }

    private boolean solve(char[][] board, int row, int col) {
        //all cells satisfy the constraints and valid, board is full
        if (row == 9) {
            return true;
        }

        int nextRow = (col == 8) ? row + 1 : row;
        int nextCol = (col == 8) ? 0 : col + 1;
        //already filled, fill rest of board and return truw if sol exists
        if (board[row][col] != '.'){
            return solve(board, nextRow, nextCol);
        }

        //if cur char is '.', try diff numbers and see if sol possible, if doesnt, backtrack
        for (int i = 1; i <= 9; i++) {
            board[row][col] = (char) ('0' + i);
            if (isValid(board, row, col) && solve(board, nextRow, nextCol)) {
                return true;
            }
        }
        //none of the above solutions fitted, so set to its initital value
        board[row][col] = '.';
        return false;
    }

    // Method to validate current board
    public boolean isValid(char[][] board, int row, int col) {
        char cur = board[row][col];
        for (int i = 0; i < 9; i++) {
            if (board[row][i] == cur && i != col || board[i][col] == cur && i != row)
                return false;
        }

        int bx = row / 3;
        int by = col / 3;
        for (int i = bx * 3; i < bx * 3 + 3; i++){
            for (int j = by * 3; j < by * 3 + 3; j++) {
                if (board[i][j] == cur && !(i == row && j == col))
                    return false;
            }
        }
        return true;
    }
}

public class Solution {
    public int firstMissingPositive(int[] A) {
        int i = 0;
        while(i < A.length){
            if(A[i] == i+1 || A[i] <= 0 || A[i] > A.length) i++;
            else if(A[A[i]-1] != A[i]) swap(A, i, A[i]-1);
            else i++;
        }
        i = 0;
        while(i < A.length && A[i] == i+1) i++;
        return i+1;
    }

    private void swap(int[] A, int i, int j){
        int temp = A[i];
        A[i] = A[j];
        A[j] = temp;
    }
}

public class Solution {
    public int trap(int[] A) {
        int left = 0, right = height.length - 1;
        int ans = 0;
        int left_max = 0, right_max = 0;
        while (left < right) {
            // Find shorter line to iterate
            if (height[left] < height[right]) {
                if(height[left] >= left_max)
                    left_max = height[left];
                else
                    ans += left_max - height[left];
                ++left;
            } else {
                if(height[right] >= right_max)
                    right_max = height[right];
                else
                    ans += right_max - height[right];
                --right;
            }
        }
        return ans;
    }
}

public class Solution {
    // DP solution
    public boolean isMatch(String s, String p) {
        boolean[][] dp = new boolean[s.length() + 1][p.length()+1];
        dp[0][0] = true;

        int j = 1;
        while(j <= p.length() && p.charAt(j-1)=='*')
            dp[0][j++] = true;

        for(int i = 1; i <= s.length(); i++) {
            for(j = 1; j <= p.length(); j++) {
                if (p.charAt(j-1)!='*') {
                    dp[i][j] = dp[i-1][j-1] && (s.charAt(i-1)==p.charAt(j-1) || p.charAt(j-1)=='?');
                } else {
                    dp[i][j] = dp[i-1][j] || dp[i][j-1];
                }
            }
        }
        return dp[s.length()][p.length()];
    }

    // Two pointer solution
    public boolean isMatch(String s, String p) {
        public boolean isMatch(String str, String pattern) {
            int s = 0, p = 0, match = 0, starIdx = -1;
            while (s < str.length()){
                // advancing both pointers
                if (p < pattern.length()  &&
                        (pattern.charAt(p) == '?' || str.charAt(s) == pattern.charAt(p))){
                    s++;
                    p++;
                } else if (p < pattern.length() && pattern.charAt(p) == '*'){
                    // * found, only advancing pattern pointer
                    starIdx = p;
                    match = s;
                    p++;
                } else if (starIdx != -1){
                    // set pattern pointer to after * and advance string pointer
                    p = starIdx + 1;
                    match++;
                    s = match;
                } else {
                    return false;
                }
            }

            //check for remaining characters in pattern
            while (p < pattern.length() && pattern.charAt(p) == '*')
                p++;

            return p == pattern.length();
        }
    }
}

public class Solution {
    public int jump(int[] A) {
        int count = 0, max = 0, nextMax = 0;
        for (int i = 0; i < A.length - 1; i++) {
            nextMax = Math.max(nextMax, i + A[i]);
            if (i == max) {
                max = nextMax;
                count++; // Only need to more steps when reach the max
            }
        }
        // if there is no way to get to the end, return -1
        return max >= A.length - 1 ? count : -1;
    }
}

public class Solution {
    public List<List<String>> solveNQueens(int n) {
        List<List<String>> re = new ArrayList<>();
        if (n <= 0){
            return re;
        }
        int[][] place = new int[n][n];
        placeQueens(re, place, n, 0);
        return re;
    }

    void placeQueens(List<List<String>> re, int[][] place, int n, int level) {
        if (level == n) {
            re.add(shape(place, n));
            return;
        }

        for (int i = 0; i < n; i++) {
            if (canplace(level, i, place, n)) {
                place[level][i] = 1;
                placeQueens(re, place, n, level + 1);
                place[level][i] = 0;
            }
        }
    }

    boolean canplace(int i, int j, int[][] place, int n) {
        for (int k = 0; k < n; k++) {
            if (k == i){
                continue;
            }
            if (place[k][j] == 1){
                return false;
            }
            for (int m = 0; m < n; m++) {
                if (Math.abs(k - i) == Math.abs(m - j) && place[k][m] == 1)
                    return false;
            }
        }
        return true;
    }

    List<String> shape(int[][] p, int n) {
        List<String> strs = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            StringBuilder temp = new StringBuilder();
            for (int j = 0; j < n; j++) {
                if (p[i][j] == 0)
                    temp.append(".");
                else
                    temp.append("Q");
            }
            strs.add(temp.toString());
        }
        return strs;
    }
}

public class Solution {
    public int totalNQueens(int n) {
        if(n<=0) return 0;
        int[] place = new int[n];
        int[] count = new int[1];

        placeQueens(count,place,n,0);
        return count[0];
    }

    public void placeQueens(int[] count, int[] place, int n, int level){
        if(level == n){
            count[0]++;
            return;
        }

        for(int i=0; i<n; i++){
            if(canplace(level,place,i)){
                place[level] = i;
                placeQueens(count,place,n,level+1);
            }
        }
    }

    public boolean canplace(int i, int[] place, int n){
        for(int j=0; j<i; j++){
            if(place[j]==n)
                return false;
            if(Math.abs(n-place[j]) == i-j)
                return false;
        }
        return true;
    }
}

/**
 * Definition for an interval.
 * public class Interval {
 *     int start;
 *     int end;
 *     Interval() { start = 0; end = 0; }
 *     Interval(int s, int e) { start = s; end = e; }
 * }
 */
public class Solution {
    public List<Interval> insert(List<Interval> intervals, Interval newInterval) {
        List<Interval> result = new ArrayList<Interval>();

        for(Interval interval: intervals){
            // new interval is behind cur interval
            if(interval.end < newInterval.start){
                result.add(interval);
            }else if(interval.start > newInterval.end){
                // cur interval is behind new interval
                result.add(newInterval);
                newInterval = interval;
            }else if(interval.end >= newInterval.start || interval.start <= newInterval.end){
                // merge the current interval with new interval as new interval
                newInterval = new Interval(Math.min(interval.start, newInterval.start),
                        Math.max(newInterval.end, interval.end));
            }
        }
        result.add(newInterval);
        return result;
    }
}

public class Solution {
    public boolean isNumber(String s) {
        s = s.trim();
        int len = s.length();
        if(len == 0) return false;

        boolean hasdot = false;
        boolean hase = false;
        boolean hasnum = false;
        int i = 0;

        // deal with "+" and "-"
        if(s.charAt(0) == '+' || s.charAt(0) == '-')
            i++;
        else if(s.charAt(0) == 'e')
            return false;

        // start validation
        while(i < len){
            // deal with '.'
            if(s.charAt(i) == '.'){
                if(hasdot)
                    return false;
                hasdot = true;
            } else if (s.charAt(i) == 'e'){
                //different invalid casse: e34, 34ee, 34e+
                if(!hasnum || hase || i == len - 1 ||
                        (i == len - 2 && (s.charAt(i+1)=='+' || s.charAt(i+1)=='-'))){
                    return false;
                }
                if(s.charAt(i+1) == '-' || s.charAt(i+1) == '+')
                    i++;
                hase = true;
                hasdot = true; // after "e" we cannot have any "."
            } else if (s.charAt(i) >= '0' && s.charAt(i) <= '9'){
                hasnum = true;
            } else {
                return false;
            }
            i++;
        }
        return hasnum;
    }
}

public class Solution {
    public List<String> fullJustify(String[] words, int maxWidth) {
        List<String> res = new ArrayList<String>();
        int i = 0, count = 0, len = 0;
        while (i < words.length) {
            len += words[i].length() + 1;
            if (len - 1 <= maxWidth) {
                i++;
                count++;
                continue;
            }
            fillString(words, res, count, i - count, maxWidth);
            len = 0;
            count = 0;
        }
        fillString(words, res, count, i - count, maxWidth);
        return res;
    }

    void fillString(String[] words, List<String> res, int count, int start, int maxWidth) {
        StringBuilder sb = new StringBuilder(maxWidth);
        int totalspace = maxWidth;
        for (int i = start; i < start + count; i++)
            totalspace -= words[i].length();

        // Only one word in this line or last line
        if (count == 1 || start + count == words.length) {
            for (int i = start; i < start + count; i++) {
                sb.append(words[i]);
                if (totalspace > 0) {
                    sb.append(" ");
                    totalspace--;
                }
            }
            while (totalspace > 0) {
                sb.append(" ");
                totalspace--;
            }
            res.add(sb.toString());
            return;
        }

        int extraspacelen = totalspace % (count - 1);
        StringBuilder evenSpaces = new StringBuilder(totalspace / (count - 1));
        for (int i = 0; i < totalspace / (count - 1); i++)
            evenSpaces.append(" ");
        for (int i = start; i < start + count - 1; i++) {
            sb.append(words[i]);
            sb.append(evenSpaces);
            if (extraspacelen > 0) {
                sb.append(" ");
                extraspacelen--;
            }
        }
        sb.append(words[start + count - 1]);
        res.add(sb.toString());
    }
}

public class Solution {
    public int minDistance(String word1, String word2) {
        int len1 = word1.length() + 1;
        int len2 = word2.length() + 1;

        if (len1 == 1) return len2 - 1;
        if (len2 == 1) return len1 - 1;

        int[][] mat = new int[len1][len2];
        for (int i = 0; i < len2; i++) {
            mat[0][i] = i;
        }

        for (int i = 0; i < len1; i++) {
            mat[i][0] = i;
        }

        for (int i = 1; i < len1; i++) {
            for (int j = 1; j < len2; j++) {
                mat[i][j] = word1.charAt(i - 1) == word2.charAt(j - 1) ? mat[i - 1][j - 1] :
                        Math.min(mat[i - 1][j - 1], Math.min(mat[i - 1][j], mat[i][j - 1])) + 1;
            }
        }
        return mat[len1 - 1][len2 - 1];
    }
}

public class Solution {
    public String minWindow(String s, String t) {
        String result = "";
        if (s.length() == 0 || t.length() == 0) {
            return result;
        }

        int[] map = new int[256];
        int[] window = new int[256];
        for (int i = 0; i < t.length(); i++) {
            map[t.charAt(i)]++;
        }

        int minLength = Integer.MAX_VALUE;
        int letterCounter = 0;
        for (int slow = 0, fast = 0; fast < s.length(); fast++) {
            if (map[s.charAt(fast)] > 0) {
                window[s.charAt(fast)]++;
                if (window[s.charAt(fast)] <= map[s.charAt(fast)]) {
                    letterCounter++;
                }
            }

            if (letterCounter >= t.length()) {
                while (map[s.charAt(slow)] == 0 || window[s.charAt(slow)] > map[s.charAt(slow)]) {
                    window[s.charAt(slow)]--;
                    slow++;
                }
                if (fast - slow + 1 < minLength) {
                    minLength = fast - slow + 1;
                    result = s.substring(slow, slow + minLength);
                }
            }
        }
        return result;
    }
}

public class Solution {
    public int largestRectangleArea(int[] heights) {
        if (heights == null || heights.length == 0) return 0;

        Stack<Integer> stack = new Stack<Integer>();
        int max = 0, i = 0;

        while (i < heights.length) {
            //push index to stack when the current height is larger than the previous one
            if (stack.isEmpty() || heights[i] >= heights[stack.peek()]) {
                stack.push(i);
                i++;
            } else { //calculate max value when the current height is less than the previous one
                int p = stack.pop();
                max = Math.max(max, heights[p]*(stack.isEmpty() ? i : i - stack.peek() - 1));
            }
        }

        while (!stack.isEmpty()) {
            int p = stack.pop();
            max = Math.max(max, heights[p]*(stack.isEmpty() ? i : i - stack.peek() - 1));
        }
        return max;
    }
}

public class Solution {
    // This problem can be converted to the "Largest Rectangle in Histogram" problem.
    public int maximalRectangle(char[][] matrix) {
        int m = matrix.length;
        int n = m == 0 ? 0 : matrix[0].length;
        int[][] height = new int[m][n + 1];

        int maxArea = 0;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (matrix[i][j] == '0') {
                    height[i][j] = 0;
                } else {
                    height[i][j] = i == 0 ? 1 : height[i - 1][j] + 1;
                }
            }
        }

        for (int i = 0; i < m; i++) {
            int area = maxAreaInHist(height[i]);
            if (area > maxArea) {
                maxArea = area;
            }
        }
        return maxArea;
    }

    int maxAreaInHist(int[] height) {
        Stack<Integer> stack = new Stack<Integer>();
        int i = 0, max = 0;

        while (i < height.length) {
            if (stack.isEmpty() || height[stack.peek()] <= height[i]) {
                stack.push(i++);
            } else {
                int t = stack.pop();
                max = Math.max(max, height[t]*(stack.isEmpty() ? i : i - stack.peek() - 1));
            }
        }
        return max;
    }
}

public class Solution {
    /*************
     * memo[i][j][k] means state: for s1.substring(i, i + k) and s2.substring(j, j + k), if they are scramble string
     * Two conditions we can regard as scramble, for range of word1(i -> i+k) or word2(j -> j+k):
     * i -> i + split = j -> j + split (len = split) and split + i -> i + k = split + i -> j + k (len = k - split)
     * i -> i + split = j + (k - split) -> j+k [len = split] and i + split -> i+k = j -> j + (k - split)(len = k - split)
     * Consider about the initialization:
     * for k == 1, we only check if word1[i] == word2[j]
     ***********/
    public boolean isScramble(String s1, String s2) {
        // check length
        if(s1 == null || s2 == null || s1.length() != s2.length()) return false;
        // check anagram
        char[] c1 = s1.toCharArray();
        char[] c2 = s2.toCharArray();
        Arrays.sort(c1);
        Arrays.sort(c2);
        if (!Arrays.equals(c1, c2))  return false;
        if(s1.length() != s2.length()) return false;

        int len = s1.length();
        // state: for s1.substring(i, i + k) and s2.substring(j, j + k), if they are scramble string
        boolean[][][] memo = new boolean[len][len][len+1];

        // initial, only check if s1[i] == s2[j]
        for(int i = 0; i < s1.length(); i++)
            for(int j = 0; j < s2.length(); j++)
                memo[i][j][1] = s1.charAt(i) == s2.charAt(j);

        for(int k = 2; k <= len; k++) {
            for(int i = 0; i <= len - k; i++) {
                for(int j = 0; j <= len - k; j++) {
                    // split point should start from 1 to k - 1
                    for(int split = 1; split < k; split++) {
                        memo[i][j][k] = memo[i][j][k] ||
                                (memo[i][j][split] && memo[i + split][j + split][k - split]) ||
                                (memo[i][j + (k - split)][split] && memo[i + split][j][k - split]);
                    }
                }
            }
        }
        return memo[0][0][len];
    }
}

public class Solution {
    /***
     * Match[i][j]  =   (s3.lastChar == s1.lastChar) && Match[i-1][j] || (s3.lastChar == s2.lastChar) && Match[i][j-1]
     * Initial Condition
     *    i=0 && j=0Match[0][0] = true;
     *    i=0 s3[j] = s2[j], Match[0][j] |= Match[0][j-1]
     *           s3[j] != s2[j], Match[0][j] = false;
     *
     *   j=0 s3[i] = s1[i], Match[i][0] |= Match[i-1][0]
     *          s3[i] != s1[i], Match[i][0] = false;
     *******************/
    public boolean isInterleave(String s1, String s2, String s3) {
        int l1 = s1.length(), l2 = s2.length(), l3 = s3.length();
        if (l1 + l2 != l3) return false;
        int[][] dptable = new int[l2 + 1][l1 + 1];
        // Initial condition 1
        dptable[0][0] = 1;
        // Initial condition 2
        for (int i = 1; i <= l1; i++) {
            if (dptable[0][i - 1] == 1 && s3.charAt(l3 - i) == s1.charAt(l1 - i))
                dptable[0][i] = 1;
            else
                dptable[0][i] = 0;
        }
        // Initial condition 3
        for (int i = 1; i <= l2; i++) {
            if (dptable[i - 1][0] == 1 && s3.charAt(l3 - i) == s2.charAt(l2 - i))
                dptable[i][0] = 1;
            else
                dptable[i][0] = 0;
        }

        for (int i = 1; i <= l2; i++)
            for (int j = 1; j <= l1; j++) {
                if (dptable[i - 1][j] == 0 && dptable[i][j - 1] == 0)
                    dptable[i][j] = 0;
                else if (dptable[i - 1][j] == 1) {
                    if (s3.charAt(l3 - i - j) == s2.charAt(l2 - i)) {
                        dptable[i][j] = 1;
                        continue;
                    }
                } else if (dptable[i][j - 1] == 1) {
                    if (s3.charAt(l3 - i - j) == s1.charAt(l1 - j)) {
                        dptable[i][j] = 1;
                        continue;
                    }
                } else
                    dptable[i][j] = 0;
            }

        return dptable[l2][l1] == 1 ? true : false;
    }
}

/**
 * Definition for binary tree
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public void recoverTree(TreeNode root) {
        TreeNode pre = null;
        TreeNode first = null, second = null;
        // Morris Traversal
        TreeNode temp = null;
        while(root!=null){
            if(root.left!=null){
                // connect threading for root
                temp = root.left;
                while(temp.right!=null && temp.right != root)
                    temp = temp.right;
                // the threading already exists
                if(temp.right!=null){
                    if(pre!=null && pre.val > root.val){
                        if(first==null){first = pre;second = root;}
                        else{second = root;}
                    }
                    pre = root;

                    temp.right = null;
                    root = root.right;
                }else{
                    // construct the threading
                    temp.right = root;
                    root = root.left;
                }
            }else{
                if(pre!=null && pre.val > root.val){
                    if(first==null){first = pre;second = root;}
                    else{second = root;}
                }
                pre = root;
                root = root.right;
            }
        }
        // swap two node values;
        if(first!= null && second != null){
            int t = first.val;
            first.val = second.val;
            second.val = t;
        }
    }
}

public class Solution {
    public int numDistinct(String S, String T) {
        int si = S.length(), ti = T.length();
        if (si <= 0 || ti <= 0 || si < ti) return 0;

        int[][] dptable = new int[si][ti];
        dptable[0][0] = S.charAt(0) == T.charAt(0) ? 1 : 0;
        for (int j = 0; j < ti; j++) {
            for (int i = 1; i < si; i++) {
                dptable[i][j] = dptable[i-1][j];
                if (S.charAt(i) == T.charAt(j)) {
                    dptable[i][j] += j == 0 ? 1 : dptable[i - 1][j - 1];
                }
            }
        }
        return dptable[si - 1][ti - 1];
    }
}

public class Solution {
    public int maxProfit(int[] prices) {
        if (prices == null || prices.length < 2) {
            return 0;
        }

        //highest profit in 0 ... i
        int[] left = new int[prices.length];
        int[] right = new int[prices.length];

        // DP from left to right
        left[0] = 0;
        int min = prices[0];
        for (int i = 1; i < prices.length; i++) {
            min = Math.min(min, prices[i]);
            left[i] = Math.max(left[i - 1], prices[i] - min);
        }

        // DP from right to left
        right[prices.length - 1] = 0;
        int max = prices[prices.length - 1];
        for (int i = prices.length - 2; i >= 0; i--) {
            max = Math.max(max, prices[i]);
            right[i] = Math.max(right[i + 1], max - prices[i]);
        }

        int profit = 0;
        for (int i = 0; i < prices.length; i++) {
            profit = Math.max(profit, left[i] + right[i]);
        }
        return profit;
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 * int val;
 * TreeNode left;
 * TreeNode right;
 * TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    public int maxPathSum(TreeNode root) {
        int max[] = new int[1];
        max[0] = Integer.MIN_VALUE;
        calculateSum(root, max);
        return max[0];
    }

    int calculateSum(TreeNode root, int[] max) {
        if (root == null) return 0;

        int left = calculateSum(root.left, max);
        int right = calculateSum(root.right, max);

        int current = Math.max(root.val, Math.max(root.val + left, root.val + right));
        max[0] = Math.max(max[0], Math.max(current, left + root.val + right));

        return current;
    }
}

public class Solution {

    class WordNode {
        String word;
        int numSteps;
        WordNode pre;

        public WordNode(String word, int numSteps, WordNode pre) {
            this.word = word;
            this.numSteps = numSteps;
            this.pre = pre;
        }
    }

    // Single direction BFS
    public List<List<String>> findLadders(String beginWord, String endWord, List<String> wordList) {
        List<List<String>> result = new ArrayList<>();
        LinkedList<WordNode> queue = new LinkedList<>();
        queue.add(new WordNode(beginWord, 1, null));
        int minStep = 0;

        HashSet<String> visited = new HashSet<>();
        HashSet<String> unvisited = new HashSet<>();
        unvisited.addAll(wordList);
        int preNumSteps = 0;

        while (!queue.isEmpty()) {
            WordNode top = queue.remove();
            String word = top.word;
            int currNumSteps = top.numSteps;

            if (word.equals(endWord)) {
                if (minStep == 0) {
                    minStep = top.numSteps;
                }

                if (top.numSteps == minStep && minStep != 0) {
                    ArrayList<String> t = new ArrayList<>();
                    t.add(top.word);
                    while (top.pre != null) {
                        t.add(0, top.pre.word);
                        top = top.pre;
                    }
                    result.add(t);
                    continue;
                }
            }

            // The used word is only removed when steps change.
            // Start of the next level
            if (preNumSteps < currNumSteps) {
                unvisited.removeAll(visited);
            }
            preNumSteps = currNumSteps;

            char[] arr = word.toCharArray();
            for (int i = 0; i < arr.length; i++) {
                for (char c = 'a'; c <= 'z'; c++) {
                    char temp = arr[i];
                    if (arr[i] != c) {
                        arr[i] = c;
                    }

                    String newWord = new String(arr);
                    if (unvisited.contains(newWord)) {
                        queue.add(new WordNode(newWord, top.numSteps + 1, top));
                        visited.add(newWord);
                    }
                    arr[i] = temp;
                }
            }
        }
        return result;
    }

    // Bi-direction BFS
    //flag of whether we have connected two parts
    boolean isConnected = false;
    public List<List<String>> findLadders(String beginWord, String endWord, Set<String> wordList) {
        //we use bi-directional BFS to find shortest path
        Set<String> fwd = new HashSet<>();
        fwd.add(beginWord);
        Set<String> bwd = new HashSet<>();
        bwd.add(endWord);

        Map<String, List<String>> hs = new HashMap<>();
        BFS(fwd, bwd, wordList, false, hs);
        List<List<String>> result = new ArrayList<>();
        //if two parts cannot be connected, then return empty list
        if(!isConnected) return result;

        //we need to add start node to temp list as there is no other node can get start node
        List<String> temp = new ArrayList<>();
        temp.add(beginWord);

        DFS(result, temp, beginWord, endWord, hs);
        return result;
    }

    public void BFS(Set<String> forward, Set<String> backward, Set<String> dict, boolean swap, Map<String, List<String>> hs){
        //boundary check
        if(forward.isEmpty() || backward.isEmpty()){
            return;
        }

        //we always do BFS on direction with less nodes
        //here we assume forward set has less nodes, if not, we swap them
        if(forward.size() > backward.size()){
            BFS(backward, forward, dict, !swap, hs);
            return;
        }

        //remove all forward/backward words from dict to avoid duplicate addition
        dict.removeAll(forward);
        dict.removeAll(backward);

        //new set contains all new nodes from forward set
        Set<String> set3 = new HashSet<String>();

        //do BFS on every node of forward direction
        for(String str : forward){
            //try to change each char of str
            for(int i = 0; i < str.length(); i++){
                //try to replace current char with every chars from a to z
                char[] ary = str.toCharArray();
                for(char j = 'a'; j <= 'z'; j++){
                    ary[i] = j;
                    String temp = new String(ary);

                    //we skip this string if it is not in dict nor in backward
                    if(!backward.contains(temp) && !dict.contains(temp)){
                        continue;
                    }

                    //we follow forward direction
                    String key = !swap? str : temp;
                    String val = !swap? temp : str;

                    if(!hs.containsKey(key))
                        hs.put(key, new ArrayList<String>());
                    //if temp string is in backward set, then it will connect two parts
                    if(backward.contains(temp)){
                        hs.get(key).add(val);
                        isConnected = true;
                    }

                    //if temp is in dict, then we can add it to set3 as new nodes in next layer
                    if(!isConnected && dict.contains(temp)){
                        hs.get(key).add(val);
                        set3.add(temp);
                    }
                }

            }
        }

        //to force our path to be shortest, we will not do BFS if we have found shortest path(isConnected = true)
        if(!isConnected){
            BFS(set3, backward, dict, swap, hs);
        }
    }

    public void DFS(List<List<String>> result, List<String> temp, String start, String end, Map<String, List<String>> hs){
        //we will use DFS, more specifically backtracking to build paths
        //boundary case
        if(start.equals(end)){
            result.add(new ArrayList<String>(temp));
            return;
        }

        //not each node in hs is valid node in shortest path, if we found current node does not have children node,
        //then it means it is not in shortest path
        if(!hs.containsKey(start)){
            return;
        }

        for(String s : hs.get(start)){
            temp.add(s);
            DFS(result, temp, s, end, hs);
            temp.remove(temp.size()-1);

        }
    }
}

public class Solution {
    /***
     *  Use a map to store ranges
     *  Get lower bound with smaller value
     *  Get upper bound with larger value
     *  Update max length with new bound
     *  Put all possible ranges into map
     *  1. num, num
     *  2. low, upp
     *  3. upp, low
     *
     */
    public int longestConsecutive(int[] nums) {
        if(nums == null || nums.length == 0) return 0;
        Map<Integer, Integer> rangeMap = new HashMap<>();
        int maxLen = 0;
        for(int num : nums){
            // Ignore duplicates
            if(rangeMap.containsKey(num)) continue;
            int low = num;
            int upp = num;
            if(rangeMap.containsKey(num - 1))
                low = rangeMap.get(num - 1);
            if(rangeMap.containsKey(num + 1))
                upp = rangeMap.get(num + 1);
            maxLen = Math.max(maxLen, upp - low + 1);
            // All possible ranges
            rangeMap.put(num, num);
            rangeMap.put(low, upp);
            rangeMap.put(upp, low);
        }
        return maxLen;
    }
}

public class Solution {
    public int minCut(String s) {
        int n = s.length();
        boolean dp[][] = new boolean[n][n];
        int cut[] = new int[n];

        for (int j = 0; j < n; j++) {
            cut[j] = j; //set maximum # of cut
            for (int i = 0; i <= j; i++) {
                if (s.charAt(i) != s.charAt(j) || (j - i > 1 && !dp[i + 1][j - 1]))
                    continue;
                dp[i][j] = true;
                // if [0...j] is palindrome, no need to cut
                // else need to cut, add 1 to the previous cut[i-1]
                cut[j] = (i == 0) ? 0 : Math.min(cut[j], cut[i - 1] + 1);
            }
        }
        return cut[n - 1];
    }
}

public class Solution {
    public int candy(int[] ratings) {
        int[] candys = new int[ratings.length];
        // Each person get one
        Arrays.fill(candys, 1);
        // This ensure the right advanced child has more candy than child on left
        for(int i = 1; i < ratings.length; i++) {
            if(ratings[i] > ratings[i - 1]) {
                candys[i] = candys[i - 1] + 1;
            }
        }

        // This ensure the advanced child on the right has more than child on right
        for(int i = ratings.length - 2; i >= 0; i--) {
            if(ratings[i] > ratings[i + 1] && candys[i] <= candys[i + 1]) {
                candys[i] = candys[i + 1] + 1;
            }
        }

        int n = 0;
        for(int i = 0; i < candys.length; i++) {
            n += candys[i];
        }
        return n;
    }

    // One pass and constant space
    public int candy(int[] ratings) {
        if (ratings.length <= 1) {
            return ratings.length;
        }
        int candies = 0;
        int up = 0;
        int down = 0;
        int old_slope = 0;
        for (int i = 1; i < ratings.length; i++) {
            int new_slope = (ratings[i] > ratings[i - 1]) ? 1 : (ratings[i] < ratings[i - 1] ? -1 : 0);
            if ((old_slope > 0 && new_slope == 0) || (old_slope < 0 && new_slope >= 0)) {
                candies += count(up) + count(down) + Math.max(up, down);
                up = 0;
                down = 0;
            }
            if (new_slope > 0)
                up++;
            if (new_slope < 0)
                down++;
            if (new_slope == 0)
                candies++;

            old_slope = new_slope;
        }
        candies += count(up) + count(down) + Math.max(up, down) + 1;
        return candies;
    }

    private int count(int n) {
        return (n * (n + 1)) / 2;
    }
}

public class Solution {
    // http://www.programcreek.com/2014/03/leetcode-word-break-ii-java/
    public List<String> wordBreak(String s, Set<String> wordDict) {
        List<String>[] pos = new ArrayList[s.length() + 1];
        pos[0] = new ArrayList<String>();
        // Construct a graph for positions with related words
        for (int i = 0; i < s.length(); i++) {
            if (pos[i] == null) continue;
            for (int j = i + 1; j <= s.length(); j++) {
                String sub = s.substring(i, j);
                if (!wordDict.contains(sub)) continue;
                if (pos[j] == null) {
                    pos[j] = new ArrayList<String>();
                }
                pos[j].add(sub);
            }
        }

        List<String> result = new ArrayList<String>();
        if (pos[s.length()] != null)
            dfs(pos, result, "", s.length());
        return result;
    }

    public void dfs(List<String>[] pos, List<String> result, String curr, int curInd) {
        if (curInd == 0) {
            result.add(curr.trim());
            return;
        }

        for (String s : pos[curInd]) {
            String combined = s + " " + curr;
            dfs(pos, result, combined, curInd - s.length());
        }
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    // Recursive Solution
    public ArrayList<Integer> postorderTraversal(TreeNode root) {
        List<Integer> re = new ArrayList<Integer>();
        if (root == null) return re;
        re.addAll(postorderTraversal(root.left));
        re.addAll(postorderTraversal(root.right));
        re.add(root.val);
        return re;
    }

    // Iterative Solution
    public List<Integer> postorderTraversal(TreeNode root) {
        List<Integer> res = new ArrayList<>();
        if (root == null) return res;
        Stack<TreeNode> parentStack = new Stack<>();
        TreeNode lastnodevisited = root, peekNode;
        while (!parentStack.isEmpty() || root != null) {
            if (root!= null) {
                parentStack.push(root);
                root = root.left;
            } else {
                peekNode = parentStack.peek();
                // If right child exists AND traversing node from left child, move right
                if (peekNode.right != null && lastnodevisited != peekNode.right) {
                    root = peekNode.right;
                } else {
                    parentStack.pop();
                    res.add(peekNode.val);
                    lastnodevisited = peekNode;
                }
            }
        }
        return res;
    }
}

public class LRUCache {
    class DoubleLinkedListNode {
        int val, key;
        DoubleLinkedListNode pre, post;

        public DoubleLinkedListNode(int key, int value) {
            this.val = value;
            this.key = key;
        }
    }

    Map<Integer, LRUCache.DoubleLinkedListNode> table;
    DoubleLinkedListNode head, end;
    int capacity, len;

    public LRUCache(int capacity) {
        this.table = new HashMap<Integer, LRUCache.DoubleLinkedListNode>();
        this.capacity = capacity;
        this.len = 0;
    }

    public int get(int key) {
        if (!table.containsKey(key)) return -1;
        removeNode(table.get(key));
        setHead(table.get(key));
        return table.get(key).val;
    }

    public void put(int key, int value) {
        if (table.containsKey(key)) {
            DoubleLinkedListNode cur = table.get(key);
            cur.val = value;
            removeNode(cur);
            setHead(cur);
        } else {
            DoubleLinkedListNode cur = new DoubleLinkedListNode(key, value);
            if (len < capacity) {
                setHead(cur);
                table.put(key, cur);
                len++;
            } else {
                table.remove(end.key);
                end = end.pre;
                if (end != null)
                    end.post = null;
                setHead(cur);
                table.put(key, cur);
            }
        }
    }

    void removeNode(DoubleLinkedListNode node) {
        DoubleLinkedListNode cur = node, pre = cur.pre, post = cur.post;
        // Link to pre
        if (pre != null) {
            pre.post = post;
        } else {
            head = post;
        }
        // Link to post
        if (post != null) {
            post.pre = pre;
        } else {
            end = pre;
        }
    }

    void setHead(DoubleLinkedListNode node) {
        node.post = head;
        node.pre = null;
        if (head != null) head.pre = node; // check head
        if (end == null) end = node; // check end
        head = node;
    }
}

package maxPointsOnaLine;

import java.util.HashMap;
import java.util.Map;

public class Solution {
    /*
     *  A line is determined by two factors,say y=ax+b  
     *  If two points(x1,y1) (x2,y2) are on the same line(Of course). 
     *  Consider the gap between two points.
     *  We have (y2-y1)=a(x2-x1),a=(y2-y1)/(x2-x1) a is a rational, 
     *  b is canceled since b is a constant.
     *  If a third point (x3,y3) are on the same line. So we must have y3=ax3+b
     *  Thus,(y3-y1)/(x3-x1)=(y2-y1)/(x2-x1)=a
     *  Since a is a rational, there exists y0 and x0, y0/x0=(y3-y1)/(x3-x1)=(y2-y1)/(x2-x1)=a
     *  So we can use y0&x0 to track a line;
     */
    public int maxPoints(Point[] points) {
        if (points == null)
            return 0;
        if (points.length <= 2)
            return points.length;

        Map<Integer, Map<Integer, Integer>> map = new HashMap<>();
        int result = 0;
        for (int i = 0; i < points.length; i++) {
            map.clear();
            int overlap = 0, max = 0;
            for (int j = i + 1; j < points.length; j++) {
                int x = points[j].x - points[i].x;
                int y = points[j].y - points[i].y;
                if (x == 0 && y == 0) {
                    overlap++;
                    continue;
                }
                int gcd = generateGCD(x, y);
                if (gcd != 0) {
                    x /= gcd;
                    y /= gcd;
                }

                if (map.containsKey(x)) {
                    if (map.get(x).containsKey(y)) {
                        map.get(x).put(y, map.get(x).get(y) + 1);
                    } else {
                        map.get(x).put(y, 1);
                    }
                } else {
                    Map<Integer, Integer> m = new HashMap<>();
                    m.put(y, 1);
                    map.put(x, m);
                }
                max = Math.max(max, map.get(x).get(y));
            }
            result = Math.max(result, max + overlap + 1);
        }
        return result;
    }

    private int generateGCD(int a, int b) {
        return b == 0 ? a : generateGCD(b, a % b);
    }
}

public class Solution {
    public int findMin(int[] num) {
        if (num.length == 0) return 0;
        int l = 0;
        int r = num.length - 1;
        int min = num[0];
        while (l < r - 1) {
            int mid = (l + r) / 2;
            if (num[l] < num[mid]) {
                min = Math.min(min, num[l]);
                l = mid + 1;
            } else if (num[l] > num[mid]) {
                min = Math.min(min, num[mid]);
                r = mid - 1;
            } else {
                l++;
            }
        }
        return Math.min(Math.min(min, num[l]), num[r]);
    }
}

public class Solution extends Reader4 {
    //  O(N)  O(1)
    Queue<Character> remain = new LinkedList<Character>();
    public int read(char[] buf, int n) {
        int i = 0;
        // 
        while (i < n && !remain.isEmpty()) {
            buf[i] = remain.poll();
            i++;
        }
        for (; i < n; i += 4) {
            char[] tmp = new char[4];
            int len = read4(tmp);
            // 
            if (len > n - i) {
                System.arraycopy(tmp, 0, buf, i, n - i);
                // 
                for (int j = n - i; j < len; j++) {
                    remain.offer(tmp[j]);
                }
                // 
            } else {
                System.arraycopy(tmp, 0, buf, i, len);
            }
            // 4
            if (len < 4) return Math.min(i + len, n);
        }
        // n
        return n;
    }
}

public class Solution {
    /**
     * Suppose there are N elements in the array, the min value is min and the max value is max.
     * Then the maximum gap will be no smaller than ceiling[(max - min ) / (N - 1)].
     * Let gap = ceiling[(max - min ) / (N - 1)].
     * We divide all numbers in the array into n-1 buckets,
     * where k-th bucket contains all numbers in [min + (k-1)gap, min + k*gap).
     * Since there are n-2 numbers that are not equal min or max and there are n-1 buckets,
     * at least one of the buckets are empty.
     * We only need to store the largest number and the smallest number in each bucket.
     * After we put all the numbers into the buckets.
     * We can scan the buckets sequentially and get the max gap.
     */
    public int maximumGap(int[] nums) {
        if (nums == null || nums.length < 2)
            return 0;
        // get the max and min value of the array
        int min = nums[0];
        int max = nums[0];
        for (int i : nums) {
            min = Math.min(min, i);
            max = Math.max(max, i);
        }
        // the minimum possibale gap, ceiling of the integer division
        int gap = (int) Math.ceil((double) (max - min) / (nums.length - 1));
        int[] bucketsMIN = new int[nums.length - 1]; // store the min value in that bucket
        int[] bucketsMAX = new int[nums.length - 1]; // store the max value in that bucket
        Arrays.fill(bucketsMIN, Integer.MAX_VALUE);
        Arrays.fill(bucketsMAX, Integer.MIN_VALUE);
        // put numbers into buckets
        for (int i : nums) {
            if (i == min || i == max)
                continue;
            int idx = (i - min) / gap; // index of the right position in the buckets
            bucketsMIN[idx] = Math.min(i, bucketsMIN[idx]);
            bucketsMAX[idx] = Math.max(i, bucketsMAX[idx]);
        }
        // scan the buckets for the max gap
        int maxGap = Integer.MIN_VALUE;
        int previous = min;
        for (int i = 0; i < nums.length - 1; i++) {
            if (bucketsMIN[i] == Integer.MAX_VALUE && bucketsMAX[i] == Integer.MIN_VALUE)
                // empty bucket
                continue;
            // min value minus the previous value is the current gap
            maxGap = Math.max(maxGap, bucketsMIN[i] - previous);
            // update previous bucket value
            previous = bucketsMAX[i];
        }
        maxGap = Math.max(maxGap, max - previous); // updata the final max value gap
        return maxGap;
    }
}

public class Solution {
    public int calculateMinimumHP(int[][] dungeon) {
        int m = dungeon.length;
        int n = dungeon[0].length;

        //init dp table
        int[][] h = new int[m][n];
        h[m - 1][n - 1] = Math.max(1 - dungeon[m - 1][n - 1], 1);
        //init last row
        for (int i = m - 2; i >= 0; i--) {
            h[i][n - 1] = Math.max(h[i + 1][n - 1] - dungeon[i][n - 1], 1);
        }
        //init last column
        for (int j = n - 2; j >= 0; j--) {
            h[m - 1][j] = Math.max(h[m - 1][j + 1] - dungeon[m - 1][j], 1);
        }

        //calculate dp table
        for (int i = m - 2; i >= 0; i--) {
            for (int j = n - 2; j >= 0; j--) {
                int down = Math.max(h[i + 1][j] - dungeon[i][j], 1);
                int right = Math.max(h[i][j + 1] - dungeon[i][j], 1);
                h[i][j] = Math.min(right, down);
            }
        }
        return h[0][0];
    }
}

public class Solution {
    public int maxProfit(int k, int[] prices) {
        if (prices.length < 2) return 0;
        if (k >= prices.length / 2) return maxProfit2(prices);

        int[] local = new int[k + 1];
        int[] global = new int[k + 1];

        for (int i = 1; i < prices.length; i++) {
            int diff = prices[i] - prices[i - 1];
            for (int j = k; j > 0; j--) {
                local[j] = Math.max(global[j - 1], local[j] + diff);
                global[j] = Math.max(global[j], local[j]);
            }
        }
        return global[k];
    }

    public int maxProfit2(int[] prices) {
        int maxProfit = 0;
        for (int i = 1; i < prices.length; i++) {
            if (prices[i] > prices[i - 1]) {
                maxProfit += prices[i] - prices[i - 1];
            }
        }
        return maxProfit;
    }
}

public class Solution {
    class TrieNode {
        TrieNode[] next = new TrieNode[26];
        String word;
    }

    public List<String> findWords(char[][] board, String[] words) {
        List<String> res = new ArrayList<>();
        TrieNode root = buildTrie(words);
        for (int i = 0; i < board.length; i++) {
            for (int j = 0; j < board[0].length; j++) {
                dfs (board, i, j, root, res);
            }
        }
        return res;
    }

    public void dfs(char[][] board, int i, int j, TrieNode p, List<String> res) {
        char c = board[i][j];
        if (c == '#' || p.next[c - 'a'] == null) return;
        p = p.next[c - 'a'];
        if (p.word != null) {   // found one
            res.add(p.word);
            p.word = null;     // de-duplicate
        }

        board[i][j] = '#';
        if (i > 0) dfs(board, i - 1, j ,p, res);
        if (j > 0) dfs(board, i, j - 1, p, res);
        if (i < board.length - 1) dfs(board, i + 1, j, p, res);
        if (j < board[0].length - 1) dfs(board, i, j + 1, p, res);
        board[i][j] = c;
    }

    public TrieNode buildTrie(String[] words) {
        TrieNode root = new TrieNode();
        for (String w : words) {
            TrieNode p = root;
            for (char c : w.toCharArray()) {
                int i = c - 'a';
                if (p.next[i] == null) p.next[i] = new TrieNode();
                p = p.next[i];
            }
            p.word = w;
        }
        return root;
    }
}

public class Solution {
    public String shortestPalindrome(String s) {
        // Find out explaination on https://www.youtube.com/channel/UCMNkvKnD3mo3Jj9eTwJllWw
        String rev_s = new StringBuilder(s).reverse().toString();
        //use special character to avoid overlap
        String l = s + "#" + rev_s;

        int[] p = getOverlay(l);
        return rev_s.substring(0, s.length() - p[l.length() - 1]) + s;
    }

    // Generate the prefix function for pattern itself
    int[] getOverlay(String pattern){
        int[] res = new int[pattern.length()];
        res[0] = 0;

        for(int i = 1; i < pattern.length(); i++){
            int index = res[i-1];
            while (index > 0 && pattern.charAt(index) != pattern.charAt(i))
                index = res[index-1];
            res[i] = (pattern.charAt(index) == pattern.charAt(i)) ? index + 1 : 0;
        }
        return res;
    }
}

public class Solution {
    public List<int[]> getSkyline(int[][] buildings) {
        if (buildings.length==0) return new ArrayList<int[]>();
        return divide(buildings, 0, buildings.length-1);
    }

    public List<int[]> divide(int[][] buildings,int i,int j){
        if(i == j){
            List<int[]> tmp=new ArrayList<>();
            tmp.add(new int[]{buildings[i][0], buildings[i][2]});
            tmp.add(new int[]{buildings[i][1], 0});
            return tmp;
        }
        List<int[]> left = divide(buildings, i, i+(j-i)/2);
        List<int[]> right = divide(buildings, i+(j-i)/2+1, j);
        return merge(left,right);
    }

    public List<int[]> merge(List<int[]> left,List<int[]> right){
        List<int[]> ans = new ArrayList<>();
        int l1 = 0, l2 = 0, curh1 = 0, curh2 = 0, curlocation = 0, skyline = 0;
        while(l1 < left.size() && l2 < right.size()){
            int[] cur1 = left.get(l1);
            int[] cur2 = right.get(l2);

            // Compare x to see which line to be cur
            if(cur1[0] < cur2[0]){
                curh1 = cur1[1];
                curlocation = cur1[0];
                l1++;
            } else if(cur1[0] > cur2[0]){
                curh2 = cur2[1];
                curlocation = cur2[0];
                l2++;
            } else {
                curh1 = cur1[1];
                curh2 = cur2[1];
                curlocation = cur1[0];
                l1++;
                l2++;
            }

            // If max same as skyline no need to add new point
            // Keep track of pre skyline
            if(skyline != Math.max(curh1, curh2)){
                skyline = Math.max(curh1, curh2);
                ans.add(new int[]{curlocation, skyline});
            }
        }
        // Add those points left out
        for(int i = l1; i < left.size(); i++) ans.add(left.get(i));
        for(int i = l2; i < right.size(); i++) ans.add(right.get(i));
        return ans;
    }
}

public class Solution {
    public int calculate(String s) {
        Stack<Integer> stack = new Stack<Integer>();
	    int result = 0, number = 0, sign = 1;
	    for(int i = 0; i < s.length(); i++){
	        char c = s.charAt(i);
	        if(Character.isDigit(c)){
	            number = 10 * number + (int)(c - '0');
	        }else if(c == '+'){
	            result += sign * number;
	            number = 0;
	            sign = 1;
	        }else if(c == '-'){
	            result += sign * number;
	            number = 0;
	            sign = -1;
	        }else if(c == '('){
	            //we push the result first, then sign;
	            stack.push(result);
	            stack.push(sign);
	            //reset the sign and result for the value in the parenthesis
	            sign = 1;
	            result = 0;
	        }else if(c == ')'){
	            result += sign * number;
	            number = 0;
	            result *= stack.pop();    //stack.pop() is the sign before the parenthesis
	            result += stack.pop();   //stack.pop() now is the result calculated before the parenthesis
	        }
	    }
	    if(number != 0) result += sign * number;
	    return result;
    }
}

public class Solution {
    /*********************
     *   There are three cases according to number in digit:
     *    1. cur > 1: i.e. n = 1223. cur at tens-digit
     *      (nums[:cur] + 1)*pow(10, digit) '1' at cur:
     *          130 '1' at tens-digit. (10...19, 110...119,......,1210...1219)
     *    2. cur == 1:  i.e. n = 1213. cur at tens-digit
     *      (nums[:cur])*pow(10, digit) '1' at cur:
     *          120 '1' at tens-digit. (10...19, 110...119,......,1110...1119)
     *      (nums[cur+1:] + 1) '1' at cur:
     *          Four '1' at tens-digit. (1210...1213)
     *    3. cur < 1:  i.e. n = 1203. cur at tens-digit
     *      (nums[:cur])*pow(10, digit) '1' at cur.
     *          120 '1' at tens-digit. (10...19, 110...119,......,1110...1119)
     *************/
    public int countDigitOne(int n) {
        if(n <= 0) return 0;
        int totalDigits = String.valueOf(n).length();
        int digit = 0, res = 0;
        while(digit < totalDigits){
            int cur = (int)((n / Math.pow(10, digit)) % 10);
            // Case 3
            res += (int)(n / Math.pow(10, digit+1))*Math.pow(10, digit);
            // Case 1 additional to Case 3
            res += (cur > 1) ? Math.pow(10, digit) : 0;
            // Case 2 additonal
            res += (cur == 1) ? (int)(n % Math.pow(10, digit)) + 1 : 0;
            digit++;
        }
        return res;
    }
}

public class Solution {
    public int[] maxSlidingWindow(int[] nums, int k) {
        if(nums.length == 0) return nums;
        final int[] max_left = new int[nums.length];
        final int[] max_right = new int[nums.length];

        max_left[0] = nums[0];
        max_right[nums.length - 1] = nums[nums.length - 1];

        for (int i = 1; i < nums.length; i++) {
            max_left[i] = (i % k == 0) ? nums[i] : Math.max(max_left[i - 1], nums[i]);

            final int j = nums.length - i - 1;
            max_right[j] = (j % k == 0) ? nums[j] : Math.max(max_right[j + 1], nums[j]);
        }

        final int[] sliding_max = new int[nums.length - k + 1];
        for (int i = 0, j = 0; i + k <= nums.length; i++) {
            sliding_max[j++] = Math.max(max_right[i], max_left[i + k - 1]);
        }

        return sliding_max;
    }
}

public class Solution {
    private int count = 0;
    private Map<Character, Character> map = new HashMap<>();

    public int strobogrammaticInRange(String low, String high) {
        if (low == null || low.length() == 0 || high == null || high.length() == 0) {
            return 0;
        }

        fillMap();

        for (int n = low.length(); n <= high.length(); n++) {
            char[] arr = new char[n];
            getStrobogrammaticNumbers(arr, 0, n - 1, low, high);
        }

        return count;
    }

    private void getStrobogrammaticNumbers(char[] arr, int start, int end, String low, String high) {
        if (start > end) {
            String s = new String(arr);
            if ((s.length() == 1 || s.charAt(0) != '0') && compare(low, s) && compare(s, high)) {
                count++;
            }
            return;
        }

        for (char c : map.keySet()) {
            arr[start] = c;
            arr[end] = map.get(c);

            if ((start < end) || (start == end && map.get(c) == c)) {
                getStrobogrammaticNumbers(arr, start + 1, end - 1, low, high);
            }
        }
    }

    // Return true if s1 <= s2
    private boolean compare(String s1, String s2) {
        if (s1.length() == s2.length()) {
            if (s1.compareTo(s2) <= 0) {
                return true;
            } else {
                return false;
            }
        }

        return true;
    }

    private void fillMap() {
        map.put('0', '0');
        map.put('1', '1');
        map.put('8', '8');
        map.put('6', '9');
        map.put('9', '6');
    }
}

public class Solution {
    private final String[] lessThan20 = {"", "One", "Two", "Three", "Four", "Five",
            "Six", "Seven", "Eight", "Nine", "Ten", "Eleven", "Twelve", "Thirteen",
            "Fourteen", "Fifteen", "Sixteen", "Seventeen", "Eighteen", "Nineteen"};
    private final String[] tens = {"", "Ten", "Twenty", "Thirty", "Forty", "Fifty",
            "Sixty", "Seventy", "Eighty", "Ninety"};
    private final String[] thousands = {"", "Thousand", "Million", "Billion"};

    public String numberToWords(int num) {
        if (num == 0) return "Zero";
        int i = 0;
        String words = "";

        while (num > 0) {
            if (num % 1000 != 0)
                words = helper(num % 1000) + thousands[i] + " " + words;
            num /= 1000;
            i++;
        }
        return words.trim();
    }

    String helper(int num) {
        if (num == 0)
            return "";
        else if (num < 20)
            return lessThan20[num] + " ";
        else if (num < 100)
            return tens[num / 10] + " " + helper(num % 10);
        else
            return lessThan20[num / 100] + " Hundred " + helper(num % 100);
    }
}

public class Solution {

    public List<String> addOperators(String num, int target) {
        List<String> res = new ArrayList<String>();
        helper(num, target, "", 0, 0, res);
        return res;
    }

    private void helper(String num, int target, String tmp, long currRes, long prevNum, List<String> res){
        // If result = target and all numbers are used, then it is a valid result
        if(currRes == target && num.length() == 0){
            String exp = new String(tmp);
            res.add(exp);
            return;
        }
        // Search for all possible divided situations
        for(int i = 1; i <= num.length(); i++){
            String currStr = num.substring(0, i);
            // If starts with 0 then not valid
            if(currStr.length() > 1 && currStr.charAt(0) == '0'){
                return;
            }
            // Get current number
            long currNum = Long.parseLong(currStr);
            // Get rid of the current number and what to search in next round
            String next = num.substring(i);
            // If it isn't first char, then operations else it is a number
            if(tmp.length() != 0){
                // multiply
                helper(next, target, tmp+"*"+currNum, (currRes - prevNum) + prevNum * currNum, prevNum * currNum, res);
                // plus
                helper(next, target, tmp+"+"+currNum, currRes + currNum, currNum, res);
                // minus
                helper(next, target, tmp+"-"+currNum, currRes - currNum, -currNum, res); 
            } else {
                // first number
                helper(next, target, currStr, currNum, currNum, res);
            }
        }
    }
}

class MedianFinder {
    // minHeap
    Queue<Integer> largePQ = new PriorityQueue<>();  
    // max Heap
    Queue<Integer> smallPQ = new PriorityQueue<>(10, revComp); 

    // Adds a number into the data structure.
    public void addNum(int num) {
        if(smallPQ.size() <= largePQ.size()){
            if(largePQ.isEmpty() || num <= largePQ.peek())
                smallPQ.offer(num);
            else{
                smallPQ.offer(largePQ.poll());
                largePQ.offer(num);
            }
        } else {
            if(num < smallPQ.peek()){
                largePQ.offer(smallPQ.poll());
                smallPQ.offer(num);
            } else {
                largePQ.offer(num);
            }
        }
    }

    // Returns the median of current data stream
    public double findMedian() {
        if(smallPQ.size() == largePQ.size())
            return (smallPQ.peek() + largePQ.peek())/2.0;
        return smallPQ.peek();
    }

    static final Comparator<Integer> revComp = new Comparator<Integer>(){
        @Override
        public int compare(Integer left, Integer right){
            return right - left;
        }
    };
};

