public class Solution {
    public List<String> removeInvalidParentheses(String s) {
        Set<String> res = new HashSet<>();
        int rmL = 0, rmR = 0;
        for(int i = 0; i < s.length(); i++) {
            if(s.charAt(i) == '(') rmL++;
            if(s.charAt(i) == ')') {
                if(rmL != 0) rmL--;
                else rmR++;
            }
        }
        DFS(res, s, 0, rmL, rmR, 0, new StringBuilder());
        return new ArrayList<String>(res);  
    }

    public void DFS(Set<String> res, String s, int i, int rmL, 
                        int rmR, int open, StringBuilder sb) {
        if(i == s.length() && rmL == 0 && rmR == 0 && open == 0) {
            res.add(sb.toString());
            return;
        }
        if(i == s.length() || rmL < 0 || rmR < 0 || open < 0) 
            return;

        char c = s.charAt(i);
        int len = sb.length();

        if(c == '(') {
            DFS(res, s, i + 1, rmL - 1, rmR, open, sb);
            DFS(res, s, i + 1, rmL, rmR, open + 1, sb.append(c));
        } else if(c == ')') {
            DFS(res, s, i + 1, rmL, rmR - 1, open, sb);
            DFS(res, s, i + 1, rmL, rmR, open - 1, sb.append(c));
        } else {
            DFS(res, s, i + 1, rmL, rmR, open, sb.append(c)); 
        }
        sb.setLength(len);
    }
}

public class Solution {
    // dp[i][j] = max(dp[i][j], 
    // dp[i][x  1] + nums[i  1] * nums[x] * nums[j + 1] + dp[x + 1][j]);
    public int DP(int i, int j, int[] nums, int[][] dp) {
        if (dp[i][j] > 0) return dp[i][j];
        for (int x = i; x <= j; x++) {
            dp[i][j] = Math.max(dp[i][j], DP(i, x - 1, nums, dp) + 
                nums[i - 1] * nums[x] * nums[j + 1] + DP(x + 1, j, nums, dp));
        }
        return dp[i][j];
    }

    public int maxCoins(int[] iNums) {
        int n = iNums.length;
        int[] nums = new int[n + 2];
        for (int i = 0; i < n; i++) nums[i + 1] = iNums[i];
        nums[0] = nums[n + 1] = 1;
        int[][] dp = new int[n + 2][n + 2];
        return DP(1, n, nums, dp);
    }
}

public class Solution {
    private void add(int[] bit, int i, int val) {
        for (; i < bit.length; i += i&(-i)) bit[i] += val;
    }

    private int query(int[] bit, int i) {
        int ans = 0;
        for (; i > 0; i -= i&(-i)) ans += bit[i];
        return ans;
    }

    public List<Integer> countSmaller(int[] nums) {
        int[] tmp = nums.clone();
        Arrays.sort(tmp);
        int[] indexes = new int[nums.length];
        for (int i = 0; i < indexes.length; i++)
            indexes[i] = Arrays.binarySearch(tmp, nums[i]);
        int[] bit = new int[indexes.length];
        int[] ans = new int[indexes.length];
        for (int i = indexes.length - 1; i >= 0; i--) {
            ans[i] = query(bit, indexes[i]);
            add(bit, indexes[i]+1, 1);
        }
        List<Integer> res = new ArrayList();
        for(int n : ans)
            res.add(n);
        return res;
    }
}

public class Solution {
    public String removeDuplicateLetters(String s) {
         //will contain number of occurences of character (i+'a')
        int[] res = new int[26];
        //will contain if character (i+'a') is present in current result Stack
        boolean[] visited = new boolean[26]; 
        char[] ch = s.toCharArray();
        for(char c : ch){  //count number of occurences of character 
            res[c-'a']++;
        }
        Stack<Character> st = new Stack<>(); // answer stack
        int index;
        for(char c : ch){ 
            index = c - 'a';
            //decrement number of characters remaining in the string to be analysed
            res[index]--;   
            if(visited[index]) //if character is already present in stack, dont bother
                continue;
            //if current character is smaller than last character in stack which occurs later in the string again
            //it can be removed and added later e.g stack = bc remaining string abc then a can pop b and then c
            while(!st.isEmpty() && c < st.peek() && res[st.peek()-'a'] != 0){ 
                visited[st.pop()-'a']=false;
            }
            st.push(c); //add current character and mark it as visited
            visited[index]=true;
        }

        StringBuilder sb = new StringBuilder();
        //pop character from stack and build answer string from back
        while(!st.isEmpty()){
            sb.insert(0,st.pop());
        }
        return sb.toString();
    }
}

public class Solution {
    // http://blog.csdn.net/u010025211/article/details/50527279
    public int[] maxNumber(int[] nums1, int[] nums2, int k) {
        int[] ans = new int[k];
        for(int i = Math.max(0, k-nums2.length); i <= k && i <= nums1.length; ++i){
            int[] candidate = merge(maxArray(nums1, i), maxArray(nums2, k-i), k);
            if(greater(candidate, 0, ans, 0))
                ans = candidate;
        }
        return ans;
    }
    // Given one array of length n, create the maximum number of length k
    public int[] maxArray(int[] nums,int k){
        int[] ans = new int[k];
        int j = 0;
        for(int i = 0; i < nums.length; ++i){
            while(nums.length-i+j > k && j > 0 && ans[j-1] < nums[i]) j--;
            if(j < k)
                ans[j++] = nums[i];
        }
        return ans;
    }
    //Given two array of length m and n, create maximum number of length k = m + n.
    private int[] merge(int[] nums1, int[] nums2, int k){
        int[] ans = new int[k];
        int i = 0, j = 0, r = 0;
        while(r < k)
            ans[r++] = greater(nums1, i, nums2, j) ? nums1[i++] : nums2[j++];
        return ans;
    }
    // Compare nums1 with nums2 from index i and j
    boolean greater(int[] nums1, int i, int[] nums2, int j){
        while(i < nums1.length && j < nums2.length && nums1[i] == nums2[j]){
            i++;
            j++;
        }
        return j == nums2.length || (i < nums1.length && nums1[i] > nums2[j]);
    }
}

public class Solution {
    public int countRangeSum(int[] nums, int lower, int upper) {
        int n = nums.length;
        long[] sums = new long[n + 1];
        for (int i = 0; i < n; ++i)
            sums[i + 1] = sums[i] + nums[i];
        return countWhileMergeSort(sums, 0, n + 1, lower, upper);
    }

    private int countWhileMergeSort(long[] sums, int start, int end, int lower, int upper) {
        if (end - start <= 1) return 0;
        int mid = (start + end) / 2;
        int count = countWhileMergeSort(sums, start, mid, lower, upper) 
              + countWhileMergeSort(sums, mid, end, lower, upper);
        int j = mid, k = mid, t = mid;
        long[] cache = new long[end - start];
        for (int i = start, r = 0; i < mid; ++i, ++r) {
            while (k < end && sums[k] - sums[i] < lower) k++;
            while (j < end && sums[j] - sums[i] <= upper) j++;
            while (t < end && sums[t] < sums[i]) cache[r++] = sums[t++];
            cache[r] = sums[i];
            count += j - k;
        }
        System.arraycopy(cache, 0, sums, start, t - start);
        return count;
    }
}

public class Solution {
    private int dfs(int[][] matrix, int i, int j, int[][] cache) {
        if(cache[i][j] > 0)
            return cache[i][j];
        int longest = 0;
        if(i > 0 && matrix[i][j] < matrix[i-1][j]) 
            longest = Math.max(longest, dfs(matrix, i-1, j, cache));
        if(j > 0 && matrix[i][j] < matrix[i][j-1])
            longest = Math.max(longest, dfs(matrix, i, j-1, cache));
        if(i < matrix.length-1 && matrix[i][j] < matrix[i+1][j])
            longest = Math.max(longest, dfs(matrix, i+1, j, cache));
        if(j < matrix[0].length-1 && matrix[i][j] < matrix[i][j+1])
            longest = Math.max(longest, dfs(matrix, i, j+1, cache));
        cache[i][j] = longest+1;
        return longest+1;
    }

    public int longestIncreasingPath(int[][] matrix) {
        if(matrix.length==0)
            return 0;
        int[][] cache = new int[matrix.length][matrix[0].length];
        int longest = 0;
        for(int i=0; i<matrix.length; i++)
            for(int j=0; j<matrix[0].length; j++)
                longest = Math.max(longest, dfs(matrix, i, j, cache));
        return longest;
    }
}

public class Solution {
    public int minPatches(int[] nums, int n) {
        long miss = 1;
        int count = 0, i = 0;

        while(miss <= n){
            if(i < nums.length && nums[i] <= miss){
                miss = miss + nums[i];
                i++;
            } else {
                miss += miss;
                count++;
            }
        }
        return count;
    }
}

public class Solution {
    public boolean isSelfCrossing(int[] x) {
        if (x.length <= 3) {
            return false;
        }
        int i = 2;
        // keep spiraling outward
        while (i < x.length && x[i] > x[i - 2]) {
            i++;
        }
        if (i >= x.length) {
            return false;
        }
        // transition from spiraling outward to spiraling inward
        if ((i >= 4 && x[i] >= x[i - 2] - x[i - 4]) ||
                (i == 3 && x[i] == x[i - 2])) {
            x[i - 1] -= x[i - 3];
        }
        i++;
        // keep spiraling inward
        while (i < x.length) {
            if (x[i] >= x[i - 2]) {
                return true;
            }
            i++;
        }
        return false;
    }
}

public class Solution {
    public List<List<Integer>> palindromePairs(String[] words) {
        List<List<Integer>> result = new ArrayList<List<Integer>>();
        Map<String, Integer> map = new HashMap<String, Integer>();
        for(int i = 0; i < words.length; i++)
            map.put(words[i], i);

        for(int i = 0; i < words.length; i++){
            String s = words[i];
            //if the word is a palindrome, get index of ""
            if(isPalindrome(s) && map.containsKey("") && map.get("") != i){
                List<Integer> l = new ArrayList<Integer>();
                l.add(i);
                l.add(map.get(""));
                result.add(l);

                l = new ArrayList<Integer>();
                l.add(map.get(""));
                l.add(i);
                result.add(l);
            }

            //if the reversed word exists, it is a palindrome
            String reversed = new StringBuilder(s).reverse().toString();
            if(map.containsKey(reversed) && map.get(reversed) != i){
                List<Integer> l = new ArrayList<Integer>();
                l.add(i);
                l.add(map.get(reversed));
                result.add(l);
            }

            for(int k = 1; k < s.length(); k++){
                String left = s.substring(0, k);
                String right= s.substring(k);

                //if left part is palindrome, find reversed right part
                if(isPalindrome(left)){
                    String reversedRight = new StringBuilder(right).reverse().toString();
                    if(map.containsKey(reversedRight) && map.get(reversedRight) != i){
                        List<Integer> l = new ArrayList<Integer>();
                        l.add(map.get(reversedRight));
                        l.add(i);
                        result.add(l);
                    }
                }

                //if right part is a palindrome, find reversed left part
                if(isPalindrome(right)){
                    String reversedLeft = new StringBuilder(left).reverse().toString();
                    if(map.containsKey(reversedLeft) && map.get(reversedLeft)!=i){
                        List<Integer> l = new ArrayList<Integer>();
                        l.add(i);
                        l.add(map.get(reversedLeft));
                        result.add(l);
                    }
                }
            }
        }
        return result;
    }

    boolean isPalindrome(String s){
        int i = 0, j = s.length()-1;
        while(i < j){
            if(s.charAt(i++) != s.charAt(j--))
                return false;
        }
        return true;
    }
}

/**
 * Definition for an interval.
 * public class Interval {
 *     int start;
 *     int end;
 *     Interval() { start = 0; end = 0; }
 *     Interval(int s, int e) { start = s; end = e; }
 * }
 */
public class SummaryRanges {

    TreeSet<Interval> intervalSet;

    /** Initialize your data structure here. */
    public SummaryRanges() {
        intervalSet = new TreeSet<Interval>(new Comparator<Interval>(){
            public int compare(Interval a, Interval b){
                return a.start - b.start;
            }
        });
    }

    public void addNum(int val) {
        Interval valInterval = new Interval(val, val);
        Interval floor = intervalSet.floor(valInterval);
        if(floor != null){
            if(floor.end >= val) return;
            if(floor.end + 1 == val){
                valInterval.start = floor.start;
                intervalSet.remove(floor);
            }
        }
        Interval higher = intervalSet.higher(valInterval);
        if(higher != null && higher.start == val+1){
            valInterval.end = higher.end;
            intervalSet.remove(higher);
        }
        intervalSet.add(valInterval);
    }

    public List<Interval> getIntervals() {
        List<Interval> res = new ArrayList<Interval>();
        res.addAll(intervalSet);
        return res;
    }
}

