public class Solution {
    public int findKthNumber(int n, int k) {
        int curr = 1;
        k = k - 1;
        while (k > 0) {
            int steps = calSteps(n, curr, curr + 1);
            if (steps <= k) {
                curr += 1;
                k -= steps;
            } else {
                curr *= 10;
                k -= 1;
            }
        }
        return curr;
    }

    //use long in case of overflow
    public int calSteps(int n, long n1, long n2) {
        int steps = 0;
        while (n1 <= n) {
            steps += Math.min(n + 1, n2) - n1;
            n1 *= 10;
            n2 *= 10;
        }
        return steps;
    }
}

public class Solution {
    public int numberOfArithmeticSlices(int[] A) {
        int re = 0;
        HashMap<Integer, Integer>[] maps = new HashMap[A.length];
        for (int i = 0; i < A.length; i++) {
            maps[i] = new HashMap<>();
            int num = A[i];
            for (int j = 0; j < i; j++) {
                if ((long) num - A[j] > Integer.MAX_VALUE) continue;
                if ((long) num - A[j] < Integer.MIN_VALUE) continue;
                int diff = num - A[j];
                int count = maps[j].getOrDefault(diff, 0);
                maps[i].put(diff, maps[i].getOrDefault(diff, 0) + count + 1);
                re += count;
            }
        }
        return re;
    }
}

public class LFUCache {

    HashMap<Integer, Integer> vals; // key to val
    HashMap<Integer, Integer> counts; // key to count
    // count to list of keys
    HashMap<Integer, LinkedHashSet<Integer>> lists;
    int cap; // capacity of the cache
    int min = -1; // track the min value

    public LFUCache(int capacity) {
        cap = capacity;
        vals = new HashMap<>();
        counts = new HashMap<>();
        lists = new HashMap<>();
        lists.put(1, new LinkedHashSet<>());
    }

    public int get(int key) {
        // Can't find val for the key
        if (!vals.containsKey(key))
            return -1;
        // Update count of a key in count to key map
        int count = counts.get(key);
        counts.put(key, count + 1);
        lists.get(count).remove(key);
        // If least frequent key is removed
        if (count == min && lists.get(count).size() == 0)
            min++;
        // Update key to list map
        if (!lists.containsKey(count + 1))
            lists.put(count + 1, new LinkedHashSet<>());
        lists.get(count + 1).add(key);
        return vals.get(key);
    }

    public void put(int key, int value) {
        if (cap <= 0) return;
        // Update key to val map
        if (vals.containsKey(key)) {
            vals.put(key, value);
            get(key);
            return;
        }
        // If is full remove the min
        // remove from lists and vals map
        if (vals.size() >= cap) {
            int evit = lists.get(min).iterator().next();
            lists.get(min).remove(evit);
            vals.remove(evit);
        }
        // Not in cache
        vals.put(key, value);
        counts.put(key, 1);
        min = 1;
        lists.get(1).add(key);
    }
}

