public class Solution {
    public int getMaxRepetitions(String s1, int n1, String s2, int n2) {
        // check if [s1. ] obtains s2
        if (!ableToObtain(s1, s2))
            return 0;
        int cnt = 0, k = -1;
        String s = s1;
        // record 'remain string'
        StringBuilder remainBuilder;
        // record all the 'remain string'
        List<String> stringList = new ArrayList<>();
        // record matching count from start to the current remain string
        List<Integer> countList = new ArrayList<>();
        stringList.add(""); // record empty string
        countList.add(0);
        for (int i = 0; i <= n1; i++) {
            remainBuilder = new StringBuilder();
            // get the next remain string, returns the count of matching
            cnt += getRemain(s, s2, remainBuilder);
            String remain = remainBuilder.toString();
            // if there is a loop, break
            if ((k = stringList.indexOf(remain)) != -1)
                break;
            stringList.add(remain); // record the remain string into arraylist
            countList.add(cnt);
            s = remain + s1; // append s1 to make a new string
        }
        // here, k is the beginning of the loop
        if (k == -1)
            return cnt / n2; // if there is no loop
        // get matching count in the loop, and loop length
        int countOfLoop = cnt - countList.get(k);
        int loopLength = stringList.size() - k;
        cnt = countList.get(k) + countOfLoop * ((n1 - k) / loopLength);
        n1 = (n1 - k) % loopLength;
        cnt += countList.get(k + n1) - countList.get(k);
        return cnt / n2;
    }

    private boolean ableToObtain(String s1, String s2) {
        boolean[] cnt = new boolean[26];
        for (char c : s1.toCharArray())
            cnt[c - 'a'] = true;
        for (char c : s2.toCharArray()) {
            if (!cnt[c - 'a'])
                return false;
        }
        return true;
    }

    private int getRemain(String s1, String s2, StringBuilder remain) {
        int cnt = 0, lastMatch = -1, p2 = 0;
        for (int p1 = 0; p1 < s1.length(); p1++) {
            if (s1.charAt(p1) != s2.charAt(p2)) continue;
            if (++p2 == s2.length()) {
                p2 = 0;
                cnt++;
                lastMatch = p1;
            }
        }
        remain.append(s1.substring(lastMatch + 1));
        return cnt;
    }
}

public class Solution {
    public int findSubstringInWraproundString(String p) {
        // count[i] is the maximum unique substring end with ith letter.
        // 0 - 'a', 1 - 'b', ..., 25 - 'z'.
        int[] count = new int[26];

        // store longest contiguous substring ends at current position.
        int maxLengthCur = 0;

        for (int i = 0; i < p.length(); i++) {
            if (i > 0 && (p.charAt(i) - p.charAt(i - 1) == 1 || (p.charAt(i - 1) - p.charAt(i) == 25))) {
                maxLengthCur++;
            }
            else {
                maxLengthCur = 1;
            }

            int index = p.charAt(i) - 'a';
            count[index] = Math.max(count[index], maxLengthCur);
        }

        // Sum to get result
        int sum = 0;
        for (int i = 0; i < 26; i++) {
            sum += count[i];
        }
        return sum;
    }
}

public class Solution {

    class TrieNode {
        TrieNode[] sons;
        boolean isEnd;
        public TrieNode() {
            sons = new TrieNode[26];
        }
    }

    public List<String> findAllConcatenatedWordsInADict(String[] words) {
        List<String> res = new ArrayList<String>();
        if (words == null || words.length == 0) {
            return res;
        }
        TrieNode root = new TrieNode();
        for (String word : words) { // construct Trie tree
            if (word.length() == 0) {
                continue;
            }
            addWord(word, root);
        }
        for (String word : words) { // test word is a concatenated word or not
            if (word.length() == 0) {
                continue;
            }
            if (testWord(word.toCharArray(), 0, root, 0)) {
                res.add(word);
            }
        }
        return res;
    }

    // count means how many words during the search path
    private boolean testWord(char[] chars, int index, TrieNode root, int count) {
        TrieNode cur = root;
        int n = chars.length;
        for (int i = index; i < n; i++) {
            if (cur.sons[chars[i] - 'a'] == null) {
                return false;
            }
            if (cur.sons[chars[i] - 'a'].isEnd) {
                if (i == n - 1) { // no next word, so test count to get result.
                    return count >= 1;
                }
                if (testWord(chars, i + 1, root, count + 1)) {
                    return true;
                }
            }
            cur = cur.sons[chars[i] - 'a'];
        }
        return false;
    }

    private void addWord(String str, TrieNode root) {
        char[] chars = str.toCharArray();
        TrieNode cur = root;
        for (char c : chars) {
            if (cur.sons[c - 'a'] == null) {
                cur.sons[c - 'a'] = new TrieNode();
            }
            cur = cur.sons[c - 'a'];
        }
        cur.isEnd = true;
    }
}

public class Solution {
    PriorityQueue<Integer> minHeap = new PriorityQueue<Integer>();
    PriorityQueue<Integer> maxHeap = new PriorityQueue<Integer>(
            new Comparator<Integer>() {
                public int compare(Integer i1, Integer i2) {
                    return i2.compareTo(i1);
                }
            }
    );

    public double[] medianSlidingWindow(int[] nums, int k) {
        int n = nums.length - k + 1;
        if (n <= 0) return new double[0];
        double[] result = new double[n];

        for (int i = 0; i <= nums.length; i++) {
            if (i >= k) {
                result[i - k] = getMedian();
                remove(nums[i - k]);
            }
            if (i < nums.length) {
                add(nums[i]);
            }
        }
        return result;
    }

    private void add(int num) {
        if (num < getMedian()) {
            maxHeap.add(num);
        } else {
            minHeap.add(num);
        }
        if (maxHeap.size() > minHeap.size()) {
            minHeap.add(maxHeap.poll());
        }
        if (minHeap.size() - maxHeap.size() > 1) {
            maxHeap.add(minHeap.poll());
        }
    }

    private void remove(int num) {
        if (num < getMedian()) {
            maxHeap.remove(num);
        } else {
            minHeap.remove(num);
        }
        if (maxHeap.size() > minHeap.size()) {
            minHeap.add(maxHeap.poll());
        }
        if (minHeap.size() - maxHeap.size() > 1) {
            maxHeap.add(minHeap.poll());
        }
    }

    private double getMedian() {
        if (maxHeap.isEmpty() && minHeap.isEmpty()) return 0;

        if (maxHeap.size() == minHeap.size())
            return ((double) maxHeap.peek() + (double) minHeap.peek()) / 2.0;
        return (double) minHeap.peek();
    }
}

public class Solution {
    static final int M = 1000000007;
    public int checkRecord(int n) {
        // ending with P or L, no A
        long[] PorL = new long[n + 1];
        // ending with P, no A
        long[] P = new long[n + 1];
        PorL[0] = P[0] = 1; PorL[1] = 2; P[1] = 1;

        for (int i = 2; i <= n; i++) {
            P[i] = PorL[i - 1];
            PorL[i] = (P[i] + P[i - 1] + P[i - 2]) % M;
        }

        long res = PorL[n];
        // inserting A into (n-1)-length strings
        for (int i = 0; i < n; i++) {
    	    long s = (PorL[i] * PorL[n - i - 1]) % M;
            res = (res + s) % M;
        }
        return (int) res;
    }
}

public class FileSystem {
    class Dir {
        HashMap<String, Dir> dirs = new HashMap<>();
        HashMap<String, String> files = new HashMap<>();
    }

    Dir root;

    public FileSystem() {
        root = new Dir();
    }

    // Time complexity is O(m + n + klog(k))
    public List<String> ls(String path) {
        Dir t = root;
        List<String> files = new ArrayList<>();
        if (!path.equals("/")) {
            String[] d = path.split("/");
            for (int i = 1; i < d.length - 1; i++) {
                t = t.dirs.get(d[i]);
            }
            if (t.files.containsKey(d[d.length - 1])) {
                files.add(d[d.length - 1]);
                return files;
            } else {
                t = t.dirs.get(d[d.length - 1]);
            }
        }
        files.addAll(new ArrayList<>(t.dirs.keySet()));
        files.addAll(new ArrayList<>(t.files.keySet()));
        Collections.sort(files);
        return files;
    }

    // Time complexity is O(m + n)
    public void mkdir(String path) {
        Dir t = root;
        String[] d = path.split("/");
        for (int i = 1; i < d.length; i++) {
            if (!t.dirs.containsKey(d[i]))
                t.dirs.put(d[i], new Dir());
            t = t.dirs.get(d[i]);
        }
    }

    // Time complexity is O(m + n)
    public void addContentToFile(String filePath, String content) {
        Dir t = root;
        String[] d = filePath.split("/");
        for (int i = 1; i < d.length - 1; i++) {
            t = t.dirs.get(d[i]);
        }
        t.files.put(d[d.length - 1], t.files.getOrDefault(d[d.length - 1], "") + content);
    }

    // Time complexity is O(m + n)
    public String readContentFromFile(String filePath) {
        Dir t = root;
        String[] d = filePath.split("/");
        for (int i = 1; i < d.length - 1; i++) {
            t = t.dirs.get(d[i]);
        }
        return t.files.get(d[d.length - 1]);
    }
}

