public class Solution {
    /***
     * for each element in input
     *      put count and char into map
     * for each element in input
     *      if count == 1
     *          add to res
     *
     * @param input
     * @return
     */
    public String remove(String input) {
        Map<Character, Integer> charToCount = new HashMap<>();
        StringBuilder res = new StringBuilder();

        for(int i = 0; i < input.length(); i++){
            char c = input.charAt(i);
            if(charToCount.containsKey(c)){
                charToCount.put(c, charToCount.get(c) + 1);
            } else {
                charToCount.put(c, 1);
            }
        }

        for(int i = 0; i < input.length(); i++){
            char c = input.charAt(i);
            if(charToCount.get(c) == 1)
                res.append(c);
        }
        return res.toString();
    }
}

package facebook.phone.AprilJune.NearestKth;

import java.util.Random;

public class Solution {

    public Point findNearestKthPoint(Point[] points, int k){
        if(k > points.length || k == 0){
            throw new IllegalArgumentException("K's value is invaild.\n");
        }
        return quickSelect(points, 0, points.length - 1, k - 1);
    }

    private Point quickSelect(Point[] points, int left, int right, int k) {
        Random rand = new Random();
        int pivotIndex = 0;
        if(right != 0){
            pivotIndex = rand.nextInt(right) % (right -left + 1) + left;
        }
        pivotIndex = partition(pivotIndex, points, left, right);
        if (pivotIndex == k) {
            return points[k];
        } else if (pivotIndex > k) {
            return quickSelect(points, left, pivotIndex - 1, k);
        } else {
            return quickSelect(points, pivotIndex + 1, right, k);
        }
    }

    private int partition(int pivotIndex, Point[] points, int left, int right) {
        int i = left;
        int j = right;
        Point temp = points[pivotIndex];
        int tempDistance = distance(temp);
        swap(points, left, pivotIndex);
        while (i < j) {
            while (j > i && distance(points[j]) >= tempDistance) {
                j--;
            }
            points[i] = points[j];
            while (j > i && distance(points[i]) <= tempDistance) {
                i++;
            }
            points[j] = points[i];
        }
        points[i] = temp;
        return i;
    }

    private void swap(Point[] points, int left, int pivotIndex) {
        Point temp = points[left];
        points[left] = points[pivotIndex];
        points[pivotIndex] = temp;
    }

    private int distance(Point point) {
        return point.x * point.x + point.y * point.y;
    }

}

public class Solution {

    /**
     * Represents Node of the graph and contains the nodes that are attached to it. We may
     * also have ids but emitted as array index is being used as node id.
     */
    private static class Node<T> {
        private final List<Node<T>> childNodes = new ArrayList<>();

        private void addChildNode(Node<T> node) {
            this.childNodes.add(node);
        }
    }

    /**
     * Returns an array of integer containing below information about tree represented by input node:
     * 	- No of vertices
     * 	- Edges removed to decompose tree into trees of even vertices
     */
    private static int[] decomposeTree(Node<Integer> node) {

        int count = 0, edgesToRemove = 0;
        for(Node<Integer> childNode : node.childNodes) {
            // For each child node
            // calls current method recursively
            final int[] tmpMetadata = decomposeTree(childNode);
            // Accumulates edgesToRemove in a local variable
            edgesToRemove += tmpMetadata[1];
            // Checks if returned count is even - if yes, increments edgesToRemove by 1
            // otherwise adds returned count to this method's count
            if(tmpMetadata[0] % 2 == 0) {
                edgesToRemove++;
            } else {
                count += tmpMetadata[0];
            }
        }
        // Increments count since we are done with current node processing
        count++;
        //returns count for input node along with edges that can be removed to decompose the tree
        return new int[]{count, edgesToRemove};
    }

    public static void main(String[] args) {
        final Scanner in = new Scanner(System.in);
        final int N = in.nextInt();
        final int M = in.nextInt();

        // Keeps all the nodes of tree in an array
        final Node<Integer>[] treeNodes = (Node<Integer>[]) new Node[N];
        for(int i = 0; i < M; i++) {
            // Gets first node of vertex
            final int node1 = in.nextInt() - 1;
            // Gets second node of vertex
            final int node2 = in.nextInt() - 1;

            // If nodes exist in array, reuses those else creates a new node and adds back to array
            treeNodes[node1] = (treeNodes[node1] == null ? new Node<Integer>() : treeNodes[node1]);
            treeNodes[node2] = (treeNodes[node2] == null ? new Node<Integer>() : treeNodes[node2]);

            //In order to create edge, we need to link nodes. Since this is a tree, it will be a directed edge and
            // for consistency, we will add bigger node to smaller nodes
            if(node1 < node2) {
                treeNodes[node1].addChildNode(treeNodes[node2]);
            } else {
                treeNodes[node2].addChildNode(treeNodes[node1]);
            }
        }

        // Since first element of array represents root of tree, we pass it to recursive method decompose tree
        final int[] metadata = decomposeTree(treeNodes[0]);

        // Print no of edges to be removed returned by above method invocation
        System.out.println(metadata[1]);

        in.close();
    }
}

public class Solution {
    /**
     * Solution 1 use dp table to track max
     * Get result in second scan
     */
    public static List<Character> mostContinuous(String s) {
        List<Character> list = new ArrayList<>();
        if (s == null || s.trim().isEmpty())
            return list;

        s = s.trim().replaceAll(" ", "");
        int n = s.length();
        int[] dp = new int[n];
        dp[0] = 1;
        int max = 1;
        for (int i = 1; i < s.length(); i++) {
            dp[i] = s.charAt(i) == s.charAt(i - 1) ? dp[i - 1] + 1 : 1;
            max = Math.max(max, dp[i]);
        }

        for (int i = 0; i < dp.length; i++) {
            if (dp[i] == max) {
                list.add(s.charAt(i));
            }
        }
        return list;
    }

    /***
     * Solution2:  Keep track of max, one sacn
     * @param str
     */
    public void continuousChar(String str) {
        str = str.trim();
        if (str == null || str.length() == 0)
            return;

        char[] arr = str.toCharArray();
        int max = 1;
        int count = 1;
        char prev = arr[0];
        ArrayList<Character> result = new ArrayList<>();

        for (int i = 1; i < arr.length; i++) {
            if (arr[i] == ' ')
                continue;
            if (arr[i] == prev) {
                count++;
            } else {
                if (count > max) {
                    result.clear();
                    result.add(prev);
                    max = count;
                } else if (count == max) {
                    result.add(prev);
                }
                prev = arr[i];
                count = 1;
            }
        }

        System.out.println(Arrays.toString(result.toArray()));
    }
}

public class Solution {

    public Map<Integer, Integer> getCount(int[] arr) {
        Map<Integer, Integer> res = new HashMap<>();
        if (arr == null || arr.length == 0) {
            return res;
        }

        int start = 0;
        while (start < arr.length) {
            int end = findLast(start, arr);
            res.put(arr[start], end - start + 1);
            start = end + 1;
        }
        return res;
    }

    private int findLast(int start, int[] arr) {
        int c = arr[start];
        int diff = 1;
        while (start + diff < arr.length && arr[start + diff] == c) {
            diff <<= 1;
        }
        if (start + diff >= arr.length) {
            return arr.length - 1;
        }

        // Binary Search for end
        int end = start + diff;
        while (start + 1 < end) {
            int mid = (end - start) / 2 + start;
            if (arr[mid] == c) {
                start = mid;
            } else {
                end = mid;
            }
        }
        return arr[end] == c ? end : start;
    }

    public static void main(String[] args) {
        int[] arr = { 1, 1, 1, 1, 2, 2, 2, 2, 2, 2, 2, 3, 3, 3, 3, 3, 3, 3, 3, 3, 3 };
        Practice d = new Practice();
        Map<Integer, Integer> res = d.getCount(arr);
        for (Entry<Integer, Integer> entry : res.entrySet()) {
            System.out.println(entry.getKey() + ": " + entry.getValue());
        }
    }
}

public class Solution {
    public String replaceWithSpace(char[] originStr) {
        int readPosition = 0;
        int writePosition = 0;

        while(readPosition < originStr.length){
            // If find %20
            if(originStr[readPosition] == '%' &&
                    readPosition + 2 < originStr.length &&
                    originStr[readPosition + 1] == '2' &&
                    originStr[readPosition + 2] == '0'){
                readPosition += 3; // Update read position
                // Don't write multiple spaces
                if(writePosition - 1 >= 0 && originStr[writePosition - 1] != ' ')
                    originStr[writePosition++] = ' ';
            } else {
                originStr[writePosition++] = originStr[readPosition++];
            }
        }

        return String.valueOf(originStr).substring(0, writePosition);
    }
}

public class Solution {
    //Recursive way
    // 'Time complexity: O(n),
    // Space complexity: O(h), height of the tree'
    public TreeNode findLCARucrsive(TreeNode root) {
        Pair result = findLCA(root);
        return result.node;
    }

    private Pair findLCA(TreeNode root) {
        if (root == null) {
            return new Pair(null, 0);
        }
        int depth = 0;
        Pair left = findLCA(root.left);
        Pair right = findLCA(root.right);
        depth = Math.max(left.depth, right.depth) + 1;
        if (left.depth == right.depth) {
            return new Pair(root, depth);
        } else if (left.depth > right.depth) {
            return new Pair(left.node, depth);
        } else {
            return new Pair(right.node, depth);
        }
    }

    class Pair {
        public TreeNode node;
        public int depth;

        public Pair(TreeNode node, int depth) {
            this.node = node;
            this.depth = depth;
        }
    }

    // Iterative Solution
    public TreeNode findLCAIterative(TreeNode root) {
        if (root == null) {
            return null;
        }
        HashMap<TreeNode, TreeNode> childToParent = new HashMap<>();
        Queue<TreeNode> level = new LinkedList<>();
        TreeNode left = null;
        TreeNode right = null;
        level.offer(root);
        while (!level.isEmpty()) {
            int size = level.size();
            for (int i = 0; i < size; i++) {
                TreeNode node = level.poll();
                if (i == 0) {
                    left = node;
                }
                if (i == size - 1) {
                    right = node;
                }
                if (node.left != null) {
                    level.offer(node.left);
                    childToParent.put(node.left, node);
                }
                if (node.right != null) {
                    level.offer(node.right);
                    childToParent.put(node.right, node);
                }
            }
        }
        while (left != right) {
            left = childToParent.get(left);
            right = childToParent.get(right);
        }
        return left;
    }
}

public class MultipleChildrenSolution {
    public TreeNodeMulti findLca(TreeNodeMulti root) {
        return helper(root).node;
    }

    private Result helper(TreeNodeMulti root) {
        if (root == null) {
            return new Result(null, 0);
        }
        int depth = 0;
        List<Result> next = new ArrayList<>();
        for (TreeNodeMulti child : root.children) {
            next.add(helper(child));
        }
        Result deepest = new Result(null, 0);
        Result deepest2 = new Result(null, 0);
        for (Result result : next) {
            if (result.depth >= deepest.depth) {
                deepest2.node = deepest.node;
                deepest2.depth = deepest.depth;
                deepest.node = result.node;
                deepest.depth = result.depth;
            }
        }
        depth = 1 + deepest.depth;
        if (deepest.depth == deepest2.depth) {
            return new Result(root, depth);
        }
        return new Result(deepest.node, depth);
    }

    class Result {
        TreeNodeMulti node;
        int depth;

        public Result(TreeNodeMulti node, int depth) {
            this.node = node;
            this.depth = depth;
        }
    }

    class TreeNodeMulti {
        public int val;
        public List<TreeNodeMulti> children;

        public TreeNodeMulti(int val) {
            this.val = val;
            this.children = new ArrayList<>();
        }
    }
}

public class Solution {
    /***
     * 1. Use first two elements to judge is up or down
     * 2. if it is up:
     * 		Assume A[x] is peak, we have
     * 		A[x] - A[i] = x - i and A[x] - A[j] = j - x
     * 		so x = (A[j] - A[i] + i + j)/2
     * 2. if it is down
     * 		Assume A[x] is vally, we have
     * 		A[x] - A[i] = i - x and A[x] - A[j] = x - j
     * 		x = (A[i] - A[j] + i + j)/2
     */
    public int findPeak(int[] A) {
        boolean isUp = (A[1] > A[0]) ? true : false;
        if (isUp) {
            return (A[A.length - 1] - A[0] + A.length - 1) / 2;
        } else {
            return (A[0] - A[A.length - 1] + A.length - 1) / 2;
        }
    }
}

public class Solution {
    public int rowWithMax1s(int[][] mat){
        // Initialize first row as row with max 1s
        int max_row_index = 0;

        // The function first() returns index of first 1 in row 0.
        // Use this index to initialize the index of leftmost 1 seen so far
        int j = 0;
        while(j < mat[0].length && mat[0][j] == 0){
            j++;
        }

        j = j == mat[0].length ? j - 1 : j;
        for (int i = 1; i < mat.length; i++){
            // Move left until a 0 is found
            while (j >= 0 && mat[i][j] == 1){
                j--;  // Update the index of leftmost 1 seen so far
                max_row_index = i;  // Update max_row_index
            }
        }
        return max_row_index;
    }
}

public class Solution {
    public int findMaxGuests(int[] arrivals, int[] exits) {
        // Sort arrival and exit arrays
        Arrays.sort(arrivals);
        Arrays.sort(exits);

        // guests_in indicates number of guests at a time
        int guests_in = 1, max_guests = 1, time = arrivals[0];
        int i = 1, j = 0, n = arrivals.length;

        // Similar to merge in merge sort to process
        // all events in sorted order
        while (i < n && j < n) {
            // If next event in sorted order is arrival,
            // increment count of guests
            if (arrivals[i] <= exits[j]) {
                guests_in++;
                // Update max_guests if needed
                if (guests_in > max_guests) {
                    max_guests = guests_in;
                    time = arrivals[i];
                }
                i++;  //increment index of arrival array
            } else {// If event is exit, decrement count
                // of guests.
                guests_in--;
                j++;
            }
        }
        return max_guests;
    }
}

public class Solution {
    // Method converts a tree to a circular
    // Link List and then returns the head of the Link List
    public TreeNode bTreeToCList(TreeNode root) {
        if (root == null)
            return null;

        // Recursively convert left and right subtrees
        TreeNode left = bTreeToCList(root.left);
        TreeNode right = bTreeToCList(root.right);

        // Make a circular linked list of single node (or root).
        // To do so, make the right and left pointers of this node point to itself
        root.left = root.right = root;

        // Step 1 Concatenate the left list with the list with current node
        // Step 2 Concatenate the returned list with the right List
        return concatenate(concatenate(left, root), right);
    }

    // concatenate both the lists and returns the head of the List
    private TreeNode concatenate(TreeNode leftList, TreeNode rightList) {
        // If either of the list is empty, then return the other list
        if (leftList == null) return rightList;
        if (rightList == null) return leftList;

        // Store the last Node of left List
        TreeNode leftLast = leftList.left;
        // Store the last Node of right List
        TreeNode rightLast = rightList.left;

        // Connect the last node of Left List
        // with the first Node of the right List
        leftLast.right = rightList;
        rightList.left = leftLast;

        // left of first node refers to the last node in the list
        leftList.left = rightLast;
        // Right of last node refers to the first node of the List
        rightLast.right = leftList;

        // Return the Head of the List
        return leftList;
    }
}

class Solution {
    // Returns largest subarray with equal number of 0s and 1s
    int maxLen(int arr[], int n) {
        // Creates an empty hashMap hM
        Map<Integer, Integer> hM = new HashMap<>();

        int sum = 0;     // Initialize sum of elements
        int max_len = 0; // Initialize result
        int ending_index = -1;
        int start_index = 0;

        for (int i = 0; i < n; i++) {
            arr[i] = (arr[i] == 0) ? -1 : 1;
        }

        // Traverse through the given array
        for (int i = 0; i < n; i++) {
            // Add current element to sum
            sum += arr[i];

            // To handle sum=0 at last index
            if (sum == 0) {
                max_len = i + 1;
                ending_index = i;
            }

            // If this sum is seen before, then update max_len
            // if required
            if (hM.containsKey(sum)) {
                if (max_len < i - hM.get(sum + n)) {
                    max_len = i - hM.get(sum + n);
                    ending_index = i;
                }
            } else // Else put this sum in hash table
                hM.put(sum + n, i);
        }

        for (int i = 0; i < n; i++) {
            arr[i] = (arr[i] == -1) ? 0 : 1;
        }
        int end = ending_index - max_len + 1;

        return max_len;
    }
}

