public class Solution {
    public int knightPath(Point2 start, Point2 end, Set<Point2> blocks) {
        int[][] nextStep = { { 1, -2 }, { 1, 2 }, { -1, 2 }, { 1, -2 },
                { 2, -1 }, { 2, 1 }, { -2, -1 }, { -2, 1 } };
        Queue<Point2> explore = new LinkedList<>();
        Set<Point2> visited = new HashSet<>();
        int step = 0;
        explore.offer(start);
        visited.add(start);
        while (!explore.isEmpty()) {
            step++;
            int size = explore.size();
            for (int i = 0; i < size; i++) {
                Point2 point = explore.poll();
                for (int k = 0; k < 8; k++) {
                    int x = nextStep[k][0];
                    int y = nextStep[k][1];
                    int nextX = point.x + x;
                    int nextY = point.y + y;
                    Point2 next = new Point2(nextX, nextY);
                    boolean canReach1 = true;
                    boolean canReach2 = true;
                    if (Math.abs(y) > Math.abs(x)) {
                        int tempY = y;
                        while (Math.abs(tempY) > 0) {
                            Point2 check = new Point2(point.x, point.y + tempY);
                            Point2 check2 = new Point2(nextX, point.y + tempY - 1);
                            canReach1 = blocks.contains(check) ? false : canReach1;
                            canReach2 = blocks.contains(check2) ? false : canReach2;
                            tempY = tempY > 0 ? tempY - 1 : tempY + 1;
                        }
                    } else {
                        int tempX = x;
                        while (Math.abs(tempX) > 0) {
                            Point2 check = new Point2(point.x + tempX, point.y);
                            Point2 check2 = new Point2(point.x + tempX - 1, nextY);
                            canReach1 = blocks.contains(check) ? false : canReach1;
                            canReach2 = blocks.contains(check2) ? false : canReach2;
                            tempX = tempX > 0 ? tempX - 1 : tempX + 1;
                        }
                    }
                    if ((!canReach1 && !canReach2) || blocks.contains(next)) {
                        continue;
                    }
                    if (next.equals(end)) {
                        return step;
                    }
                    if (!visited.contains(next)) {
                        explore.add(next);
                        visited.add(next);
                    }
                }
            }
        }
        return -1;
    }
}

public class Solution {
    // Output the string
    String getStr(String s, int k) {
        Map<Character, Integer> map = new HashMap<>();
        StringBuilder sb = new StringBuilder();
        for (char c : s.toCharArray()) {
            int idx = map.getOrDefault(c, -k);
            while (sb.length() - k < idx) {
                sb.append("_");
            }
            sb.append(c);
            map.put(c, sb.length());
        }
        return sb.toString();
    }

    // Output the total time spent
    public static int missionOrder(int[] mission, int N) {
        if (mission.length == 0) {
            return 0;
        }
        Map<Integer, Integer> map = new HashMap<>();
        int time = 0;
        for (int i = 0; i < mission.length; i++) {
            time++;
            if (!map.containsKey(mission[i])) {
                map.put(mission[i], time);
            } else {
                if (time - map.get(mission[i]) > N) {
                    map.put(mission[i], time);
                } else {
                    time = N + map.get(mission[i]) + 1;
                    map.put(mission[i], time);
                }
            }
        }
        return time;
    }
}

// Returns index of x if it is present in arr[l..r], else
// return -1
class BinarySearch {
    // Recursive Solution
    int binarySearch(int arr[], int l, int r, int x) {
        // We reach here when element is not present in array
        if(r < l) return -1;

        int mid = l + (r - l) / 2;
        // If the element is present at the middle itself
        if (arr[mid] == x)
            return mid;
        // If element is smaller than mid, then it can only
        // be present in left subarray
        if (arr[mid] > x)
            return binarySearch(arr, l, mid - 1, x);
        // Else the element can only be present in right
        // subarray
        return binarySearch(arr, mid + 1, r, x);
    }

    // Iterative Solution
    int binarySearch(int arr[], int x) {
        int l = 0, r = arr.length - 1;
        while (l <= r) {
            int m = l + (r - l) / 2;
            // Check if x is present at mid
            if (arr[m] == x)
                return m;
            // If x greater, ignore left half
            if (arr[m] < x)
                l = m + 1;
                // If x is smaller, ignore right half
            else
                r = m - 1;
        }
        // if we reach here, then element was not present
        return -1;
    }

}

public class QuickSelect {
    // Recursive Solution
    // Time complexity = O(n)
    // Discard half each time: n+(n/2)+(n/4)..1 = n + (n-1) = O(2n-1) = O(n),
    // because n/2+n/4+n/8+..1=n-1.
    public int findKthLargest(int[] nums, int k) {
        if (nums == null || nums.length == 0)
            return Integer.MAX_VALUE;
        return findKthLargest(nums, 0, nums.length - 1, nums.length - k);
    }

    // quick select: kth smallest
    private int findKthLargest(int[] nums, int start, int end, int k) {
        if (start > end)
            return Integer.MAX_VALUE;

        int pivot = nums[end];// Take A[end] as the pivot,
        int left = start;
        for (int i = start; i < end; i++) {
            if (nums[i] <= pivot) // Put numbers < pivot to pivot's left
                swap(nums, left++, i);
        }
        swap(nums, left, end);// Finally, swap A[end] with A[left]

        if (left == k)// Found kth smallest number
            return nums[left];
        else if (left < k)// Check right part
            return findKthLargest(nums, left + 1, end, k);
        else // Check left part
            return findKthLargest(nums, start, left - 1, k);
    }

    private void swap(int[] A, int i, int j) {
        int tmp = A[i];
        A[i] = A[j];
        A[j] = tmp;
    }

    // Iterative
    public int findKthLargest(int[] nums, int k) {
        shuffle(nums);
        int start = 0, end = nums.length - 1, index = nums.length - k;
        while (start < end) {
            int pivot = partion(nums, start, end);
            if (pivot < index)
                start = pivot + 1;
            else if (pivot > index)
                end = pivot - 1;
            else
                return nums[pivot];
        }
        return nums[start];
    }
    // Shuffle array to O(N) guaranteed running time + O(1) space
    private void shuffle(int a[]) {
        final Random random = new Random();
        for(int ind = 1; ind < a.length; ind++) {
            final int r = random.nextInt(ind + 1);
            swap(a, ind, r);
        }
    }
    // Partition function
    private int partion(int[] nums, int start, int end) {
        int pivot = start, temp;
        while (start <= end) {
            while (start <= end && nums[start] <= nums[pivot]) start++;
            while (start <= end && nums[end] > nums[pivot]) end--;
            if (start > end) break;
            swap(nums, start, end);
        }
        swap(nums, end, pivot);
        return end;
    }
    // excahnge function
    private void swap(int[] a, int i, int j) {
        final int tmp = a[i];
        a[i] = a[j];
        a[j] = tmp;
    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode(int x) { val = x; }
 * }
 */
public class Solution {
    // Iterative Solution
    public ListNode reverse(ListNode begin, ListNode end) {
        ListNode curr = begin.next;
        while (curr.next != end) {
            ListNode temp = begin.next;
            begin.next = curr.next;
            curr.next = curr.next.next;
            begin.next.next = temp;
        }
        return curr;
    }

    // Recursive Solution
    public ListNode reverseList(ListNode head) {
        if (head == null || head.next == null)
            return head;
        ListNode p = reverseList(head.next);
        head.next.next = head;
        head.next = null;
        return p;
    }
}

public class Solution {
    public int strStr(String haystack, String needle) {
        if (needle == null || haystack == null || needle.length() > haystack.length())
            return -1;
        if (needle.length() == 0){
            return 0;
        }

        int[] overlay = getOverlay(needle);
        int i = 0;
        while (i <= haystack.length() - needle.length()){
            int j = 0;
            while(j < needle.length()){
                if (haystack.charAt(i+j) != needle.charAt(j)){
                    i = j == 0 ? i+1 : i+j-overlay[j-1];
                    break;
                }
                j++;
            }
            if(j == needle.length()){
                return i;
            }
        }
        return -1;
    }

    // Generate the prefix function for pattern itself
    private int[] getOverlay(String pattern){
        int[] res = new int[pattern.length()];
        res[0] = 0;
        for(int i = 1; i < pattern.length(); i++){
            int index = res[i-1];
            while (index > 0 && pattern.charAt(index) != pattern.charAt(i))
                index = res[index-1];
            res[i] = (pattern.charAt(index) == pattern.charAt(i)) ? index + 1 : 0;
        }
        return res;
    }
}

public class Solution {
    /**
     * Time Complexity: C(n,1) + C(n,2) + ..C(n, n) = O(2^n)
     * where n is the size of candidates,
     * and k is the max repeated times for each candidates.
     * Space complexity: O(m) where m is the size of array for the solution
     *
     * @param candidates
     * @param target
     * @return
     */

    public List<List<Integer>> combinationSum2(int[] candidates, int target) {
        Arrays.sort(candidates);
        List<List<Integer>> re = new ArrayList<List<Integer>>();
        getList(candidates, target, new ArrayList<Integer>(), re, 0);
        return re;
    }

    private void getList(int[] candidates, int target, List<Integer> li, List<List<Integer>> re, int start) {
        if (target <= 0) {
            if (target == 0) {
                List<Integer> templi = new ArrayList<Integer>(li);
                re.add(templi);
            }
            return;
        }

        for (int i = start; i < candidates.length; i++) {
            if(i > start && candidates[i] == candidates[i-1]){
                continue; // skip duplicates
            }
            li.add(candidates[i]);
            getList(candidates, target - candidates[i], li, re, i+1);
            li.remove(li.size() - 1);
        }
    }
}

public class Solution {
    public String minWindow(String s, String t) {
        String result = "";
        if (s.length() == 0 || t.length() == 0) {
            return result;
        }

        int[] map = new int[256];
        int[] window = new int[256];
        for (int i = 0; i < t.length(); i++) {
            map[t.charAt(i)]++;
        }

        int minLength = Integer.MAX_VALUE;
        int letterCounter = 0;
        for (int slow = 0, fast = 0; fast < s.length(); fast++) {
            if (map[s.charAt(fast)] > 0) {
                window[s.charAt(fast)]++;
                if (window[s.charAt(fast)] <= map[s.charAt(fast)]) {
                    letterCounter++;
                }
            }

            if (letterCounter >= t.length()) {
                while (map[s.charAt(slow)] == 0 || window[s.charAt(slow)] > map[s.charAt(slow)]) {
                    window[s.charAt(slow)]--;
                    slow++;
                }
                if (fast - slow + 1 < minLength) {
                    minLength = fast - slow + 1;
                    result = s.substring(slow, slow + minLength);
                }
            }
        }
        return result;
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    // Recursive Solution
    public List<Integer> inorderTraversal(TreeNode root) {
        List<Integer> re = new ArrayList<>();
        if(root == null) return re;
        re.addAll(inorderTraversal(root.left));
        re.add(root.val);
        re.addAll(inorderTraversal(root.right));
        return re;
    }

    // Iterative Solution
    public List<Integer> inorderTraversal(TreeNode root) {
        List<Integer> re = new ArrayList<>();
        Stack<TreeNode> stack = new Stack<>();
        if (root == null) return re;
        while (!stack.empty() || root != null) {
            if (root != null) {
                stack.push(root);
                root = root.left;
            } else {
                root = stack.pop();
                re.add(root.val);
                root = root.right;
            }
        }
        return re;
    }

    // Morris Traversal method
    public List<Integer> inorderTraversal(TreeNode root) {
        List<Integer> res = new ArrayList<Integer>();
        TreeNode temp = null;
        while(root!=null){
            if(root.left!=null){
                // connect threading for root
                temp = root.left;
                while(temp.right!=null && temp.right != root)
                    temp = temp.right;
                // the threading already exists
                if(temp.right!=null){
                    temp.right = null;
                    res.add(root.val);
                    root = root.right;
                }else{
                    // construct the threading
                    temp.right = root;
                    root = root.left;
                }
            }else{
                res.add(root.val);
                root = root.right;
            }
        }
        return res;
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 * int val;
 * TreeNode left;
 * TreeNode right;
 * TreeNode(int x) { val = x; }
 * }
 */
public class Solution

        // Recursive Solution
        public List<Integer> preorderTraversal(TreeNode root) {
            List<Integer> re = new ArrayList<Integer>();
            if (root == null) return re;
            re.add(root.val);
            re.addAll(preorderTraversal(root.left));
            re.addAll(preorderTraversal(root.right));
            return re;
        }

        // Iterative Solution
        public List<Integer> preorderTraversal(TreeNode root) {
            List<Integer> res = new ArrayList<>();
            if (root == null) return res;
            Stack<TreeNode> stack = new Stack<>();
            stack.push(root);
            while (!stack.isEmpty()) {
                root = stack.pop();
                res.add(root.val);
                if (root.right != null)
                    stack.push(root.right);
                if (root.left != null)
                    stack.push(root.left);
            }
            return res;
        }

        // Morris Travasal
        public List<Integer> preorderTraversal(TreeNode root) {
            List<Integer> res = new ArrayList<Integer>();
            TreeNode cur = root, prev;
            while (cur != null) {
                if (cur.left == null) {
                    res.add(cur.val);
                    cur = cur.right;
                } else {
                    prev = cur.left;
                    while (prev.right != null && prev.right != cur)
                        prev = prev.right;

                    if (prev.right == null) {
                        // the only difference with inorder-traversal
                        res.add(cur.val);
                        prev.right = cur;
                        cur = cur.left;
                    } else {
                        prev.right = null;
                        cur = cur.right;
                    }
                }
            }
            return res;
        }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
public class Solution {
    // Recursive Solution
    public ArrayList<Integer> postorderTraversal(TreeNode root) {
        List<Integer> re = new ArrayList<Integer>();
        if (root == null) return re;
        re.addAll(postorderTraversal(root.left));
        re.addAll(postorderTraversal(root.right));
        re.add(root.val);
        return re;
    }

    // Iterative Solution
    public List<Integer> postorderTraversal(TreeNode root) {
        List<Integer> res = new ArrayList<>();
        if (root == null) return res;
        Stack<TreeNode> parentStack = new Stack<>();
        TreeNode lastnodevisited = root, peekNode;
        while (!parentStack.isEmpty() || root != null) {
            if (root!= null) {
                parentStack.push(root);
                root = root.left;
            } else {
                peekNode = parentStack.peek();
                // If right child exists AND traversing node from left child, move right
                if (peekNode.right != null && lastnodevisited != peekNode.right) {
                    root = peekNode.right;
                } else {
                    parentStack.pop();
                    res.add(peekNode.val);
                    lastnodevisited = peekNode;
                }
            }
        }
        return res;
    }
}


