public class Point2 {
    public int x;
    public int y;
    public Point2(int x, int y) {
        this.x = x;
        this.y = y;
    }
    @Override
    public int hashCode() {
        return Arrays.hashCode(new int[]{x, y});
    }
    @Override
    public boolean equals(Object other) {
        if (other == null) {
            return false;
        }
        if (other == this) {
            return true;
        }
        if (!(other instanceof Point2)) {
            return false;
        }
        Point2 point = (Point2) other;
        return point.x == x && point.y == y;
    }
}

class Solution {
    private final static int[] X = { 0, 0, 1, -1 };
    private final static int[] Y = { 1, -1, 0, 0 };

    public int minStep(Point2 start, Point2 end, Set<Point2> blocks) {
        Set<Point2> visited = new HashSet<>();
        Queue<Point2> explore = new LinkedList<>();
        int step = 0;
        explore.offer(start);
        visited.add(start);
        while (!explore.isEmpty()) {
            step++;
            int size = explore.size();
            for (int i = 0; i < size; i++) {
                Point2 point = explore.poll();
                for (int k = 0; k < 4; k++) {
                    int nextX = point.x + X[k];
                    int nextY = point.y + Y[k];
                    Point2 next = new Point2(nextX, nextY);
                    if (next.equals(end)) {
                        return step;
                    }
                    if (!visited.contains(next) && !blocks.contains(next)) {
                        explore.offer(next);
                        visited.contains(next);
                    }
                }
            }
        }
        return -1;
    }

    public List<Point2> minPath(Point2 start, Point2 end, Set<Point2> blocks) {
        HashMap<Point2, Point2> visitedToLast = new HashMap<>();
        Queue<Point2> explore = new LinkedList<>();
        List<Point2> path = new ArrayList<>();
        boolean isFound = false;
        explore.offer(start);
        visitedToLast.put(start, null);
        while (!explore.isEmpty() && !isFound) {
            Point2 point = explore.poll();
            for (int k = 0; k < 4; k++) {
                int nextX = point.x + X[k];
                int nextY = point.y + Y[k];
                Point2 next = new Point2(nextX, nextY);
                if (!visitedToLast.containsKey(next) && !blocks.contains(next)) {
                    visitedToLast.put(next, point);
                }
                if (next.equals(end)) {
                    isFound = true;
                    // visitedToLast.put(end, next);
                }
            }
        }
        Point2 mover = end;
        while (mover != null) {
            path.add(0, mover);
            mover = visitedToLast.get(mover);
        }
        return path;
    }
}

public static class Node {
    private final T data;
    private final Set<Node> adjacent = new HashSet<Node>();

    public Set<Node> getAdjacent() {
        return adjacent;
    }

    public Node(T data) {
        this.data = data;
    }

    public T getData() {
        return data;
    }

    // returns if the node was added, false if already there
    public boolean addAdjacent(Node node) {
        return adjacent.add(node);
    }

    // returns true if any were added
    public boolean addAdjacents(Set<Node> nodes) {
        return adjacent.addAll(nodes);
    }
}

class Solution {
    public static boolean pathExistsBidirectional(Node a, Node b) {
        // BFS on both nodes at the same time
        Queue<Node> queueA = new Queue<Node>();
        Queue<Node> queueB = new Queue<Node>();
        Set<Node> visitedA = new HashSet<Node>();
        Set<Node> visitedB = new HashSet<Node>();

        visitedA.add(a);
        visitedB.add(b);
        queueA.add(a);
        queueB.add(b);

        while (!queueA.isEmpty() && !queueB.isEmpty()) {
            if (pathExistsBidirectionalHelper(queueA, visitedA, visitedB)) {
                return true;
            }
            if (pathExistsBidirectionalHelper(queueB, visitedB, visitedA)) {
                return true;
            }
        }

        return false;
    }

    private static boolean pathExistsBidirectionalHelper(Queue<Node> queue, Set<Node> visitedFromThisSide, Set<Node> visitedFromThatSide) {
        if (!queue.isEmpty()) {
            Node next = queue.remove();
            for (Node adjacent : next.getAdjacent()) {
                if (visitedFromThatSide.contains(adjacent)) {
                    return true;
                } else if (visitedFromThisSide.add(adjacent)) {
                    queue.add(adjacent);
                }
            }
        }
        return false;
    }
}
